INSERT INTO `#__volunteers_reports` (`id`, `department`, `team`, `title`, `alias`, `description`, `notes`, `state`, `ordering`, `version`, `created_by`, `created`, `modified_by`, `modified`, `checked_out`, `checked_out_time`) VALUES
	(5, 0, 2, 'Joomla! Volunteer Portal launched, join now!', 'joomla-volunteer-portal-launched-join-now', 'On Sunday November 9th the Volunteer Portal was launched during the Joomla World Conference. This first version of the portal will provide an overview of all the working groups within the Joomla projects and their team members. We invite all Joomlers to sign up so we can make team member overviews complete. And donâ€™t forget to share reports about what your team is working on regularly!', NULL, 1, 0, 0, 430, '2014-11-09 23:15:04', 0, '0000-00-00 00:00:00', 0, '0000-00-00 00:00:00'),
	(6, 0, 20, 'Meeting notes, Nov. 2014', 'meeting-notes-nov-2014', 'Full meeting notes can be found here:\r\n\r\nhttps://docs.google.com/document/d/1BFK52vfDhNgo0VVDGIXEjTvehlhKXfYOgP8V6mLVGoA/edit?usp=sharing', NULL, 1, 0, 0, 509, '2014-11-11 22:02:17', 0, '0000-00-00 00:00:00', 0, '0000-00-00 00:00:00'),
	(7, 0, 34, '8 nov 2014 report', '8-nov-2014-report', '* Several members of the System testing working group gather during Joomla World Conference for a Joomla In Action session: Kshitij and Valerie started setting up their machines for continue testing the com_localise extension: https://github.com/joomla-projects/com_localise#system-tests. A work that was started by Puneet and Javier.\r\n', NULL, 1, 0, 0, 527, '2014-11-12 10:56:40', 0, '0000-00-00 00:00:00', 0, '0000-00-00 00:00:00'),
	(8, 0, 7, 'Marketing Reports', 'marketing-reports', 'Our Marketing reports can be found here:\r\nhttp://joom.la/MarketingMailingList', NULL, 1, 0, 0, 522, '2014-11-12 16:00:26', 0, '0000-00-00 00:00:00', 0, '0000-00-00 00:00:00'),
	(9, 0, 7, 'Friday November 22nd 2013', 'friday-november-22nd-2013', '<p>Marketing Team 2013 Planning Meeting Agenda &nbsp;</p>\r\n<p>Friday November 22nd 2013. Time TBA</p>\r\n<p>Please see here for the meeting time in your timezone</p>\r\n<p>Moderator:&nbsp;</p>\r\n<p>Attendees:</p>\r\n<p>Members of the marketing chatâ€¦ Add your name if you plan to attend or remove it if you are not going to be able to make it</p>\r\n<p>Chris Nielsen [CN]</p>\r\n<p>Ruth Cheesley [RC]</p>\r\n<p>Duke Speer [DS]</p>\r\n<p>Paul Orwig [PO]</p>\r\n<p>Jess Dunbar [JD]</p>\r\n<p>Beat [BB]</p>\r\n<p>Mark Bender [MB]</p>\r\n<p>Agenda</p>\r\n<p>(Please feel free to add any additional items below before meeting )</p>\r\n<p>3.2 Launch campaign unfinished work add list &nbsp;of todos</p>\r\n<p>Overall Focus of the Group - Mission Statement (5 minutes)</p>\r\n<p>https://docs.google.com/a/anything-digital.com/document/d/1gV2RzTI3pEdUJhfTMjt9bCD8Dpeu9C_Wb_2p92bVb4w/edit</p>\r\n<p>Timeline of activities - what are the next milestone dates we should be concerned with? (5 minutes)</p>\r\n<p>Minutes</p>\r\n<p>Tasks outstanding from 3.2 launch</p>\r\n<p>New blog to be written to credit those involved with marketing 3.2. &nbsp;Sander following up with Hung for the landing page. &nbsp;[JD]</p>\r\n<p>Marketing materials to be translated - RC to put in touch with Marijke to push this out to translation teams [RC]</p>\r\n<p>Aims and mission of the group</p>\r\n<p>Form an official working group of 10-12 people which feeds into leadership (representatives from each LT on the group) that draws upon an expanded Marketing Advisory Group for resources. &nbsp;</p>\r\n<p>See document for further info, aim/mission:</p>\r\n<p>The purpose of the marketing working group is to raise awareness of the Joomla CMS and Framework within the Joomla community and around the world.</p>\r\n<p>Produce a call for members on community.joomla.org [RC &amp; JD] &nbsp;</p>\r\n<p>First two parts of this document are OK - clarify the rest of the document in a subsequent hangout.</p>\r\n<p>Upcoming dates</p>\r\n<p>Framework launch on 1st December - all to support Parth Lawate in marketing but need blog posts, marketing collateral.</p>\r\n<p>26th November - Targeted release date for 2.5.17 and 3.2.1 - need positive communication about issues which have been resolved - JD to liaise with developers, DS to assist</p>\r\n<p>Action Items</p>\r\n<p>Write new blog post outlining the people involved with marketing for 3.2 release [JD]</p>\r\n<p>Get in touch with translation teams to ensure that marketing materials are available in as many languages as possible [RC]</p>\r\n<p>Write a call for nominations/suggestions for the Marketing Working Group on community.joomla.org [RC]</p>\r\n<p>Support Parth Lawate in marketing for the forthcoming release of the Framework [ALL]</p>\r\n<p>Prepare marketing materials for the next minor release (scheduled for 26th November at present) [JD]</p>\r\n<p>Next Meeting Agenda Items (Scheduled for Nov. 29th)</p>\r\n<p>Action items, goals and initiatives - ( 20 minutes )</p>\r\n<p>Style Guide Updated - Include Badges</p>\r\n<p>add list &nbsp;of todos</p>\r\n<p>Who will lead this?</p>\r\n<p>Media kit for releases</p>\r\n<p>add list &nbsp;of todos</p>\r\n<p>Who will lead this?</p>\r\n<p>Social Strategy and associated content&nbsp;</p>\r\n<p>add list &nbsp;of todos</p>\r\n<p>Who will lead this?</p>\r\n<p>Video for 3.5 release &nbsp;- Storyboard and marketing highlights</p>\r\n<p>add list &nbsp;of todos</p>\r\n<p>Who will lead this?</p>\r\n<p>Getting deemed as an official working group sanctioned by the leadership team</p>\r\n<p>add list &nbsp;of todos</p>\r\n<p>Who will lead this?</p>\r\n<p>Roles and Responsibilities - Humans leading specific initiatives that this team will take on (10 minutes)</p>\r\n<p>Define our talent pool - Identify whom brings what skills or org credentials to the table</p>\r\n<p>Have a web space to give credit to the volunteers - (until Volunteer portal is live)</p>\r\n<p>Marketing RFP- Impact and impact of timeline (10 Minutes)&nbsp;</p>\r\n<p>https://docs.google.com/a/community.joomla.org/document/d/1AJZ7_ahSy_bzeP_isDrM_rX-NogcxsDgYC1UVPo93V0/edit?usp=sharing [RFP link]</p>', NULL, 1, 0, 0, 522, '2014-11-13 20:31:33', 430, '2014-11-16 20:45:51', 0, '0000-00-00 00:00:00'),
	(10, 0, 13, '13th November Meeting Minutes', '13th-november-meeting-minutes', '<p>In attendance: Ruth Cheesley [RC], Jennifer Gress [JG], Alejandro Pascoli [AP]<br />Apologies: Liam Mandville [LM]</p>\r\n<p>Meeting opened at 1800, topics to discuss include setting goals and objectives for the coming year, round up of Joomla World Conference (JWC) session, and presentation of proposal to support Spanish speaking community by AP.</p>\r\n<p>JG and RC reported on session at the Joomla World Conference which was extremely helpful and provided some great ideas for engaging with Joomla User Group Organisers. We were also able to provide advice on registering a JUG, explored the documentation portal, and encouraged several user groups to get started during the event. JG left as she was unwell. RC continued meeting and explored the goals which were proposed by emails. These were formulated into a set of clearly defined goals for 2015 which include AP\'s proposal.</p>\r\n<ul>\r\n<li>To increase the number of Joomla User Groups in non-native English speaking countries by 25%</li>\r\n<li>To improve workflows for the processes involved with registering and amending Joomla User Group registrations</li>\r\n<li>Update Terms of Service, Frequently Asked Questions and create guidelines for JUG operation</li>\r\n<li>Translate all JUG documentation into at least two languages</li>\r\n<li>To develop a proactive outreach campaign to keep in touch with Joomla User Groups and showcase what is happening around the world in our community</li>\r\n<li>Develop a â€˜JUG Buddiesâ€™ system to pair up new JUG organisers with another experienced organiser</li>\r\n<li>To develop a welcome package for new Joomla User Groups including physical items and clear documentation about running a JUG</li>\r\n<li>To implement the ability to register Translation Teams and Regional/National organisations who support Joomla User Groups within their region/country within the Joomla User Group directory</li>\r\n</ul>\r\n<p>AP presented his proposal to support Spanish speaking JUG formation by supporting a member of the JUG team to attend five events in the Spanish community in 2015 - this will be included in the proposed goals and budget request for 2015.</p>\r\n<p>JG suggested before leaving that the JUG team review team members and ensure that mailing lists and chat rooms are updated to reflect current team members - also an update was requested from LM regarding new team members with applications pending. LM to ensure any team members leaving are thanked and given appreciation for their work. LM to update at his convenience.</p>\r\n<p>RC will incorporate goals into CLT goals for 2015 and will assist the team in preparing a budget request for items which require funding.</p>\r\n<p>Meeting concluded at 1845.</p>', NULL, 1, 0, 0, 439, '2014-11-13 22:00:00', 430, '2014-11-16 20:35:53', 0, '0000-00-00 00:00:00'),
	(11, 0, 8, 'Current Objectives', 'current-objectives', '<p>Our group met in Cancun Mexico to discuss our objectives and work out some new plans for the coming six months. Currently we are:</p>\r\n<ol>\r\n<li>Translating the JRD into Spanish, Portuguese and French.</li>\r\n<li>Sunsetting the old Joomla! 1.5 resources directory.</li>\r\n<li>Reviewing over 200 applications for approval.</li>\r\n<li>Redesigning the layout based on user experience feedback.</li>\r\n<li>Reviewing the language on the website to make it simpler and more digestible.</li>\r\n</ol>', NULL, 0, 0, 0, 574, '2014-11-17 00:26:29', 0, '0000-00-00 00:00:00', 0, '0000-00-00 00:00:00'),
	(12, 0, 8, 'Current Objectives', 'current-objectives-1', '<p>Our group met in Cancun Mexico to discuss our objectives and work out some new plans for the coming six months. Currently we are:</p>\r\n<ol>\r\n<li>Translating the JRD into Spanish, Portuguese and French.</li>\r\n<li>Sunsetting the old Joomla! 1.5 resources directory.</li>\r\n<li>Reviewing over 200 applications for approval.</li>\r\n<li>Redesigning the layout based on user experience feedback.</li>\r\n<li>Reviewing the language on the website to make it simpler and more digestible.</li>\r\n</ol>', NULL, 1, 0, 0, 574, '2014-11-17 00:27:04', 0, '0000-00-00 00:00:00', 0, '0000-00-00 00:00:00'),
	(13, 0, 7, '2014-11-20 Marketing Team Planning Meeting Agenda - November 20, 2014', '2014-11-20-marketing-team-planning-meeting-agenda-', '<p><a href="https://docs.google.com/document/d/1ae5l-9fRxTzGk4zFJmD7OlnD72nfk_TWVAQUwngwhPs/edit?usp=sharing">November 20th 2014 meeting</a></p>', NULL, 1, 0, 0, 522, '2014-11-20 16:38:16', 0, '0000-00-00 00:00:00', 0, '0000-00-00 00:00:00'),
	(14, 0, 28, 'November 2014 Doc WG Report', 'november-2014-doc-wg-report', '<p>For the total overview and complete report for JDOCs, please see our "Community well" page. The&nbsp;<a href="http://docs.joomla.org/JDOC:Community_well" target="_blank">JDOCs Community well page</a> will contain more details, the future and current goings on for the Documentation Working Group. Plus a chance to give your feedback!</p>\r\n<p>Summary of past month:</p>\r\n<ul>\r\n<li>Documentation has made some great improvements for Joomla migration and updating documentation.</li>\r\n<li>Translations, many thanks to MATsxm for taking on the translation administration of JDOCs.</li>\r\n<li>A few weeks ago, JDOCs cleaned out some unused user accounts, the vast majority were spam user accounts.</li>\r\n<li>Our JDOC Contributor Policy has been updated to be more inline with that of the Joomla Forum.</li>\r\n<li>733 social shares of documentation pages occurred over the past month.</li>\r\n<li>Last but not least, community reporting! If you haven\'t guessed it yet, you\'re reading it right now.</li>\r\n</ul>', NULL, 1, 0, 0, 511, '2014-11-23 20:07:57', 0, '0000-00-00 00:00:00', 0, '0000-00-00 00:00:00'),
	(15, 41, 0, 'Production Leadership team is looking for nominees', 'production-leadership-team-is-looking-for-nominees', '<p>The PLT agrees on looking for new nominees. Please see http://developer.joomla.org/news/598-call-for-production-leadership-team-nominations.html</p>', NULL, 1, 0, 0, 527, '2014-11-24 11:53:50', 0, '0000-00-00 00:00:00', 0, '0000-00-00 00:00:00'),
	(16, 0, 34, '26th November Meeting', '26th-november-meeting', '<p>3rd-Meeting for System Test Working Group,</p>\r\n<p>Meeting was attended by:&nbsp;</p>\r\n<p>Puneet Kala, Javier Gomez, Milton Bryant, Kshitij Sharma, David Jardin, Viktor Vogel, Valerie Isaksen.</p>\r\n<p>Topics Covered in the Meeting:</p>\r\n<ol>\r\n<li>Javier and Kshitij talked about the presentation for System Tests at JWC-14, feedbacks and questions they were asked were discussed, Kshtij is going to add his presentation for JWC in Documentation before next meeting.&nbsp;</li>\r\n<li>Puneet and Javier talked about the progress on CodeCeption tests on com_localise, what all have been done so far, what is the plan for the future.</li>\r\n<li>Viktor and David talked about the plan to make use of Servers provided by 1&amp;1 for making use with System Tests, David is going to prepare a documented plan within next few days on How we are going to make use of the servers.</li>\r\n<li>System tests that were done as part of GSOC-14, plan was prepared to tests them against the latest 3.4 beta and fix issues if any, Javier, Puneet and Kshitij are going to work on this and prepare a Pull request to the main repo by first week of December.</li>\r\n<li>Puneet talked about the latest framework that he has been working on, mustached, <a href="http://mustached.org,">http://mustached.org,</a>&nbsp;Javier suggested few more changes that is needed to make it work for testing Joomla! extensions with CodeCeption.</li>\r\n<li>Javier asked all the group members to signup on the Volunteers portal.</li>\r\n<li>Milton is going to help us in arranging a Management tool for the working group.</li>\r\n<li>Valerie is going to help in Documenting&nbsp;<a href="http://docs.joomla.org/Testing_Joomla_Extensions_with_Codeception">http://docs.joomla.org/Testing_Joomla_Extensions_with_Codeception</a>&nbsp;along with Javier Gomez</li>\r\n</ol>\r\n<p>Next meeting is planned to happen on 10th December 2014, timings can be found here</p>\r\n<p><a href="http://www.timeanddate.com/worldclock/fixedtime.html?msg=4th+System+testing+working+group+meeting&amp;iso=20141210T16&amp;p1=31&amp;ah=1">http://www.timeanddate.com/worldclock/fixedtime.html?msg=4th+System+testing+working+group+meeting&amp;iso=20141210T16&amp;p1=31&amp;ah=1</a></p>\r\n<p>Complete notes for the meeting can be found here:</p>\r\n<p><a href="https://docs.google.com/document/d/1tFznRlaYSlGeGOuMe1x-G-Lv1BcWmrhb2qoudJZMrfo/edit">https://docs.google.com/document/d/1tFznRlaYSlGeGOuMe1x-G-Lv1BcWmrhb2qoudJZMrfo/edit#</a></p>\r\n<p>&nbsp;</p>', NULL, 1, 0, 0, 525, '2014-11-26 17:25:54', 0, '0000-00-00 00:00:00', 0, '0000-00-00 00:00:00'),
	(17, 0, 13, 'JUG 271114 Meeting Notes', 'jug-271114-meeting-notes', '<p>Liam Mandville<br />Alejandro Pascoli<br />Duke Speer<br />Helvecio da Silva<br />Son Koral<br /><br />Outstanding tickets discussed with no major concerns. <br />Concerns over the downtime for the directory and ticket system were discussed with the server change hopefully sorting access issues.<br /><br />Proposal for refresher training and videos to assist members discussed. <br /><br />Meeting finished early due to American holday commitments. </p>', NULL, 1, 0, 0, 646, '2014-11-30 14:11:34', 0, '0000-00-00 00:00:00', 0, '0000-00-00 00:00:00'),
	(18, 0, 50, 'December Update', 'december-update', '<p>Going forward, it is the intent of the CMS Maintenance Team to provide a monthly report on the status of the Joomla! CMS, maintenance issues, and user activity. &nbsp;Suggestions for reporting metrics are welcome for consideration and can be submitted to the Group Lead.</p>\r\n<h3>Open Reports</h3>\r\n<ul>\r\n<li>Issues: 100</li>\r\n<li>Pull Requests: 413</li>\r\n</ul>\r\n<p>Total Open Reports: <strong>513</strong></p>\r\n<h3>User Activity</h3>\r\n<ul>\r\n<li>Active Users in November:&nbsp;<strong>195</strong></li>\r\n<li>Active Users in October:&nbsp;<strong>312</strong></li>\r\n</ul>\r\n<h3>Items Opened/Closed</h3>\r\n<ul>\r\n<li>Total Items Opened in November:&nbsp;<strong>304</strong></li>\r\n<li>Total Items Closed in November:&nbsp;<strong>260</strong></li>\r\n</ul>', NULL, 1, 0, 0, 447, '2014-12-02 14:20:50', 0, '0000-00-00 00:00:00', 0, '0000-00-00 00:00:00'),
	(19, 0, 49, 'Detalles de la publicaciÃ³n Diciembre 2014', 'detalles-de-la-publicacion-diciembre-2014', '<p>Durante el mes de Noviembre en el marco de la Joomla! World Conference, tuvimos la oportunidad de tener nuestro encuentro como equipo de trabajo de la Revista de la Coomunidad Joomla!â„¢. Dentro de esa reuniÃ³n acordamos algunos puntos a seguir y algunas directrices, las puedes revisar en <a href="http://magazine.joomla.org/es/ediciones-anteriores/item/2432-reporte-del-equipo-durante-la-joomla-world-conference">este artÃ­culo</a>. Algunos de los primeros resultados de ese trabajo ya han visto sus frutos, es asÃ­ como a partir de la ediciÃ³n de <a href="http://magazine.joomla.org/es/ediciones-anteriores/diciembre-2014">Diciembre 2014</a> contamos con nuevos autores y traductores, logrando hacer una entrega de 14 artÃ­culos con 11 colaboradores. Del mismo modo se incorporÃ³ una nueva colaboradora, Andrea Kekan, quiÃ©n comenzarÃ¡ a contribuir con las imÃ¡genes o grÃ¡fica para los artÃ­culos de la Revista, de esa forma no solo el contenido de los artÃ­culos serÃ¡ original, sino que tambiÃ©n la grÃ¡fica que los acompaÃ±a.</p>\r\n<p>Recuerda que si tienes alguna duda, consulta, sugerencia o deseas comenzar a colaborar, puedes escribirme a <a href="mailto:guillermo.bravo@community.joomla.org">guillermo.bravo@community.joomla.org</a>&nbsp;y con gusto yo mismo o algÃºn miembro del equipo de trabajo te responderÃ¡.</p>', NULL, 1, 0, 0, 569, '2014-12-02 21:13:32', 0, '0000-00-00 00:00:00', 0, '0000-00-00 00:00:00'),
	(20, 0, 8, '12/3/2014 Meeting', '1232014-meeting', '<p>Today eight of us met to discuss a diverse and detailed agenda.</p>\r\n<p>We started with reviewing the approvals progress. The JRD has been extremely successful since its launch with approximately 350 applications. Our team is working as quickly as possible to approve applicants from oldest to newest and/or contact applicants who need to provide more information. The approval process usually takes 15 minutes per applicant and we have 98 outstanding to review.</p>\r\n<p>Deb has built a plugin with her team at Polished Geek to expediate the process by generating a ticket automatically when a new applicant submits their application. This will likely save about five minutes per approval. This should be live within the week.</p>\r\n<p>On other news, Ryan will be preparing a newsletter to communicate with everyone in the community our progress, success and provide an update.</p>\r\n<p>Dawn and Soren will be working with the JED to see if there are ways that we can unify our design approach for the two directories so there is a visual consistancy on the Joomla! platform.</p>\r\n<p>Ron is working closely with a few different translations teams to get the JRD into Spanish, Portugeuse, and French.</p>\r\n<p>Brian has updated us on the state of approvals and will be hosting an approval process working group next Wednesday, December 10th. Please signup if you\'d like to volunteer by clicking the "Get Involved" tab.&nbsp;</p>', NULL, 1, 0, 0, 574, '2014-12-03 17:35:35', 0, '0000-00-00 00:00:00', 0, '0000-00-00 00:00:00'),
	(21, 0, 52, 'Structural Team Meeting Minutes - December 3', 'structural-team-meeting-minutes-december-3', '<p dir="ltr"><strong>Present:</strong> Sarah Watz, Marijke Stuivenberg, Sander Potjer, Peter Bui, Alice Grevet</p>\r\n<p>PLT departures - itâ€™s term renewal time. The new people joining need to understand the structure discussion - they can be brought up to speed in a meeting once they join the team.</p>\r\n<p>We need fresh eyes to read our material to see if it is clear to those not involved in the process. To clarify the change needed, and how and why it can be implemented. Review the proposal we have based on feedback. Need to align the feedback with the proposal to see where it all fits. With all the comments right now itâ€™s not so clear. Comments in blog posts too. Publish a document with links to all of the conversations happening. List the things we heard the most - for example â€œwhat is the why behind this structure change?â€ and â€œThe work for a dept coordinator will be too much for one personâ€ - collect these frequent questions/comments and list them/present them.</p>\r\n<p>OSM within leadership or outside of it - explain the why. Explain why we say no to certain initiatives - the thought process behind it.</p>\r\n<p>All 3 proposals presented in Cancun agree that we need a change.</p>\r\n<p>How to present it? Slides with document for more details? Or storytelling? What do people need to see? They need to see how our structure works now and how it will work in the future. The 3 teams do not work in an identical fashion. Explain it to the marketing team. If they can understand it then it can be communicated to the community.</p>\r\n<p>Marijke will share process for OSM, Peter has started a document on CLT.</p>\r\n<p>Alice will start a document to list all the info from blogs that have been written on this topic. Team members will add to it. Be able to discuss it in our next meeting next week. Share a doc with the group so people can add to it. Many questions may have already been answered in other discussions/presentations - put them in this document. Add proposals - big things we might want to change to the document for discussion on the 9th. What might we want to change? Address question of non-English speaking communities feeling excluded from the proposal.</p>\r\n<p>By December 21st we need to have a text to go to the translators.</p>\r\n<p>Peter Bui shared his blog post draft about the current status of the process for the team to review and add input.</p>\r\n<p><br /><br /><br /><br /></p>', NULL, 1, 0, 0, 677, '2014-12-03 22:13:53', 0, '0000-00-00 00:00:00', 0, '0000-00-00 00:00:00'),
	(22, 0, 7, 'Marketing Team Meeting  Thursday  December 4, 2014', 'marketing-team-meeting-thursday-december-4-2014', '<p>You can find the marketing team meeting notes for&nbsp;December 4, 2014 in the link below</p>\r\n<p>&nbsp;</p>\r\n<p><a href="https://groups.google.com/d/msg/joomla-marketing-working-group-public-list/shbHgoguNbc/4iYTBxIUBbwJ">https://groups.google.com/d/msg/joomla-marketing-working-group-public-list/shbHgoguNbc/4iYTBxIUBbwJ</a></p>', NULL, 1, 0, 0, 522, '2014-12-04 19:29:10', 0, '0000-00-00 00:00:00', 0, '0000-00-00 00:00:00'),
	(23, 0, 38, 'Update Working Group report - December 7th, 2014', 'update-working-group-report-december-7th-2014', '<p>The team started in <strong>August of 2014</strong>, and falls under the <strong>Production Leadership Team</strong> (PLT).</p>\r\n<h3>Overview</h3>\r\n<p>Available at: <a href="https://docs.joomla.org/Update_Working_Group">https://docs.joomla.org/Update_Working_Group</a>&nbsp;<a href="https://docs.joomla.org/Update_Working_Group"></a></p>\r\n<h3>Team communication and meetings</h3>\r\n<p>The Update Working Group (UWG) has an open chatroom, #update-working-group on joomla.slack.com, and weâ€™ve had three scheduled meetings in our chatroom:&nbsp;</p>\r\n<ul>\r\n<li>6 Sept 2014</li>\r\n<li>27 Sept 2014</li>\r\n<li>18 Oct 2014</li>\r\n</ul>\r\n<p>Thereâ€™s also been ongoing discussion in the chatroom on various topic related to our groupâ€™s goals. We focussed on those items that could be done quickly with what we have to work with now, with the main focus being on Documentation. Goal being to have specific things done before 2.5 EOL or sooner.</p>\r\n<p>We are working together with a group from the <strong>Marketing team</strong> for the extra visuals and marketing posts.</p>\r\n<ul>\r\n<li><a href="/working-groups/marketing#reports">Marketing Team reports</a><a href="/working-groups/marketing#reports"></a></li>\r\n</ul>\r\n<h3>What weâ€™ve done</h3>\r\n<ul>\r\n<li><strong>Raise awareness of the fact that the Joomla! 2.5 series reaches End of Life (EOL) by the end of December 2014, and encourage people to upgrade to Joomla 3.x through:</strong>\r\n<ul>\r\n<li>Update to 2.5 that will show a notice, and info in Extensions listing\r\n<ul>\r\n<li><a href="https://github.com/joomla/joomla-cms/pull/5063">[2.5] End of Support Notification Plugin #5063</a></li>\r\n<li><a href="https://github.com/joomla/joomla-cms/pull/5015">Compatibility information for extensions in Joomla 2.5 #5015</a></li>\r\n</ul>\r\n</li>\r\n<li>Article in December Joomla! Community Magazine: <a href="http://magazine.joomla.org/issues/issue-dec-2014/item/2398-why-migrate-and-then-how">Why Migrate? And then how?</a></li>\r\n<li>Post to <a href="https://groups.google.com/d/msg/joomla-dev-cms/f7Waw1KlFdE/A8q3F7hCjggJ">Joomla! CMS Development list</a> and to the Developer site to get Extension Developers involved: <a href="http://developer.joomla.org/news/599-joomla-extension-developers-what-resources-do-you-need-for-the-final-release-of-2-5.html">Joomla Extension Developers, what resources do you need for the final release of 2.5</a></li>\r\n</ul>\r\n</li>\r\n<li><strong>Provide resources to help with the upgrade</strong>\r\n<ul>\r\n<li>Re-organize and update help pages on wiki that relates to updates, upgrades, and migrations.</li>\r\n<li>New&nbsp;content on the wiki:\r\n<ul>\r\n<li><a href="http://docs.joomla.org/Why_Migrate">http://docs.joomla.org/Why_Migrate</a>&nbsp;</li>\r\n<li><a href="http://docs.joomla.org/Migration_Step_by_Step_Self_Assessment">http://docs.joomla.org/Migration_Step_by_Step_Self_Assessment</a>&nbsp;</li>\r\n<li><a href="http://docs.joomla.org/Planning_for_Migration">http://docs.joomla.org/Planning_for_Migration</a>&nbsp;</li>\r\n<li><a href="http://docs.joomla.org/Planning_for_Mini-Migration_-_Joomla_2.5_to_3.x">http://docs.joomla.org/Planning_for_Mini-Migration_-_Joomla_2.5_to_3.x</a>&nbsp;</li>\r\n<li><a href="http://docs.joomla.org/Planning_Migration_-_Joomla_1.5_to_3">http://docs.joomla.org/Planning_Migration_-_Joomla_1.5_to_3</a>&nbsp;</li>\r\n<li><a href="http://docs.joomla.org/Template_Considerations_During_Migration">http://docs.joomla.org/Template_Considerations_During_Migration</a>&nbsp;</li>\r\n<li><a href="http://docs.joomla.org/Migrating_a_Template_from_Joomla_1.5_to_3.x">http://docs.joomla.org/Migrating_a_Template_from_Joomla_1.5_to_3.x</a>&nbsp;</li>\r\n<li><a href="http://docs.joomla.org/Joomla_2.5_to_3.x_Step_by_Step_Migration">http://docs.joomla.org/Joomla_2.5_to_3.x_Step_by_Step_Migration</a>&nbsp;</li>\r\n<li><a href="http://docs.joomla.org/Joomla_2.5_to_3.x_Common_Migration_Errors">http://docs.joomla.org/Joomla_2.5_to_3.x_Common_Migration_Errors</a>&nbsp;</li>\r\n<li><a href="http://docs.joomla.org/Joomla_1.5_to_3.x_Step_by_Step_Migra">http://docs.joomla.org/Joomla_1.5_to_3.x_Step_by_Step_Migra</a>&nbsp;</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n</li>\r\n<li><strong>Translations</strong>\r\n<ul>\r\n<li>Translations of documentation is currently underway.&nbsp;</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<h3>Doing now</h3>\r\n<ul>\r\n<li>Announcements on Joomla.org</li>\r\n<li>Reaching out to JUGs and JDays</li>\r\n<li>Video guide (in production)</li>\r\n<li>Further Marketing/Informational materials</li>\r\n<li>Further updates to wiki pages&nbsp;</li>\r\n<li>Update Download page information on joomla.org</li>\r\n<li>Special JCM edition in Jan/Feb <em>(under consideration)</em></li>\r\n<li>Translating the Update help guides into more languages</li>\r\n</ul>\r\n<h3>Plan to do</h3>\r\n<ul>\r\n<li>Enhance Update/Upgrade portal on JDOCs&nbsp;</li>\r\n<li>Update information on joomla.org web sites.</li>\r\n<li>Joomla 1.0, 1.5, 1.6, 1.7 migration guides.</li>\r\n<li>Migration XML Standards <em>(under consideration)</em></li>\r\n</ul>', NULL, 1, 0, 0, 498, '2014-12-07 00:00:00', 498, '2014-12-07 19:31:01', 0, '0000-00-00 00:00:00'),
	(24, 0, 34, '10th December Meeting', '10th-december-meeting', '<p>4th Meeting for System Test working Group,</p>\r\n<p>Meeting was attended by:</p>\r\n<p>Puneet Kala, Javier Gomez, Milton Bryant, Valerie Isaksen, Robert Deutz, Tanaporn Pantuprecharat</p>\r\n<p>Topics Covered in the Meeting:</p>\r\n<ol>\r\n<li>Puneet Kala informed about the status of System Tests, and updates on the GSOC Project work, Puneet will work with Kshitij to fix all the tests by end of December, 2014.</li>\r\n<li>Robert Attended first meeting for System testing group and he explained about the setup and plan to make use of Servers, Robert will work along with David Jardin and have the Docker instance and Jenkins server setup by January, 2015.</li>\r\n<li>Puneet Kala and Javier Gomez are going to plan for building tests for&nbsp;<a href="https://github.com/joomla-extensions/weblinks">https://github.com/joomla-extensions/weblinks</a>&nbsp;in January using&nbsp;<a href="http://mustached.org/">http://mustached.org/</a></li>\r\n<li>Once the server setup is done, Puneet and Javier will work on finding ways to make use of Sauce Labs/Browser Stack to make test execute on different browsers, php versions and make them run in Parallel.&nbsp;</li>\r\n<li>Tanaporn will send a text report about her PHD research Topic.</li>\r\n<li>Valerie and Tanaporn are going to start working on documenting:&nbsp;<a href="https://docs.joomla.org/Testing_Joomla_Extensions_with_Codeception">https://docs.joomla.org/Testing_Joomla_Extensions_with_Codeception</a></li>\r\n<li>Milton will help in Setting up System testing Project on Jira.</li>\r\n</ol>\r\n<p>Next meeting is planned to happen on 7th January, 2015.</p>\r\n<p>Complete Notes for todays meeting can be found here:</p>\r\n<p><a href="https://docs.google.com/document/d/1_GJqcOYBuBUNHUnDfIYXNBegfAm2rq0NBBWkpd44_0U/edit">https://docs.google.com/document/d/1_GJqcOYBuBUNHUnDfIYXNBegfAm2rq0NBBWkpd44_0U/edit</a></p>', NULL, 1, 0, 0, 525, '2014-12-10 00:00:00', 525, '2014-12-10 17:26:41', 0, '0000-00-00 00:00:00'),
	(25, 0, 8, 'JRD Membership Approvals Working Group', 'jrd-membership-approvals-working-group', '<p>Thank you to the JRD team members that continue to assist in working through the huge surge of applicants for the new Joomla Resource Directory.&nbsp; As of today we have nearly 80% (78.93% for the persnickety among us) that are either fully approved and listed or are in the approval process.</p>\r\n<p>That does still leave quite a few organizations that have submitted listings that have yet to hear back from us.&nbsp; We are quickly working through all the submissions in the order they were recieved.&nbsp; Some have been waiting quite awhile.&nbsp; We are working hard to get to your listings ASAP.</p>\r\n<p>Thank you all for your patience during this exciting post re-launch period.</p>', NULL, 1, 0, 0, 592, '2014-12-10 00:00:00', 592, '2014-12-10 19:14:51', 0, '0000-00-00 00:00:00'),
	(26, 0, 52, 'Structural Team Meeting Minutes - Dec. 10', 'structural-team-meeting-minutes-december-10', '<p>Present: Peter Bui, Ronni Christiansen, Alice Grevet, Sander Potjer, Marijke Stuivenberg, Sarah Watz</p>\r\n<p>The team reviewed the feedback list which was started a week ago, to see what needs emphasizing, clarification or change, and also to determine if there are any additions to be made. The list covers the major points raised in comments and blogs, etc. Time was spent comparing the team\'s proposal with the proposals presented at JWC.</p>\r\n<p>Specifically discussed were: the role of an omsbudsman, internationalization, the definition of Department Coordinators, the formation and removal of teams &amp; subteams, the choice of what to call the different roles and levels in a new structure, and the role/placement of the Board of Directors.</p>\r\n<p>Two action items for the coming week include:</p>\r\n<ul>\r\n<li>continue adding to the feedback list to add more detail where needed for sharing with the marketing team<br /><br /></li>\r\n<li>start a new document that will condense the three documents previously shared with the working groups, incorporating the changes from the feedback list. The purpose is to produce a clear, concise document to be sent to translation teams for public release.</li>\r\n</ul>\r\n<p>In addition to the document to be prepared for the translation teams, a very detailed, longer document will be prepared to provide the proposal with a strong foundation.</p>', NULL, 1, 0, 0, 677, '2014-12-11 00:00:00', 677, '2014-12-11 20:54:08', 0, '0000-00-00 00:00:00'),
	(27, 42, 0, 'Summary OSM Meeting December 11, 2014', 'summary-osm-meeting-december-11-2014', '<p dir="ltr">In todays Board of Directors meeting we discussed these items:</p>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr">Approval of the Meeting Minutes of October 23, 2014<br />The minutes are posted at <a href="http://opensourcematters.org/about/organization/minutes.html">http://opensourcematters.org/about/organization/minutes.html</a></p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr">Team Reports</p>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr">Demo Site:</p>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr">Earnings since launch</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr">Free Hosting Demo at joomla.com</p>\r\n</li>\r\n</ul>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr">Events Team</p>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr">Transition to CLT</p>\r\n</li>\r\n</ul>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr">Capital Team</p>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr">Joomla Shop</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr">Survey large sponsors and partners</p>\r\n</li>\r\n</ul>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr">OSM website</p>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr">New site is launched</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr">Todo list</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr">New ideas</p>\r\n</li>\r\n</ul>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr">GSoC</p>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr">Working on budget</p>\r\n</li>\r\n</ul>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr">Certification</p>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr">Pilot at JWC</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr">The certification platform</p>\r\n</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr">Unfinished Business</p>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr">Contract RFP Marketing</p>\r\n</li>\r\n</ul>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr">New Business</p>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr">Return on investment funding Leadership Summit at JWC 2014</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr">Managing OSM email addresses</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr">JandBeyond Sponsorship</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr">Global Sponsor Programme</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr">Complaint JED banning</p>\r\n</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<p>&nbsp;</p>\r\n<p dir="ltr">The dates for the next meetings are:</p>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr">18 December, 2014 - Budget Meeting</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr">8 January, 2015 - Monthly Board Meeting</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr">12 February 2015 - Monthly Board Meeting</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr">12 March 2015 - Monthly Board Meeting</p>\r\n</li>\r\n</ul>\r\n<p><br />Note: More detailed Meeting Minutes of this Board Meeting will be subject of the next monthly board meeting to be approved and published at the OSM website shortly after approval.</p>', NULL, 1, 0, 0, 490, '2014-12-13 23:51:33', 0, '0000-00-00 00:00:00', 0, '0000-00-00 00:00:00'),
	(28, 0, 13, 'Meeting notes 18 December- JUG tea', '18th-december', '<p>Meeting notes 18 December- JUG team<br /><br /><br />Outstanding tickets were dealt with and awaiting JUG registratants responses<br /><br />System downtime - Appears to have been resolved with server move.<br /><br />Proposed JUG team Meeting at JAB 2015 discussed<br /><br /><br />Son, Alice and Jenn did (refresher) training.<br /><br />Umbrella Groups futher discussions<br /><br />FAQ translations being looked at and updated. <br /><br />Next JUG Team meeting is 8 January 2015 18:00 UTC.<br /><br /><br /><br /><br /></p>', NULL, 1, 0, 0, 646, '2014-12-18 00:00:00', 646, '2014-12-18 19:36:27', 0, '0000-00-00 00:00:00'),
	(29, 0, 52, 'Structural Team Meeting Minutes - Dec. 17', 'structural-team-meeting-minutes-dec-17', '<p class="p1">Present: Peter Bui, Ronni Christiansen, Alice Grevet, Sander Potjer, Marijke Stuivenberg, Sarah Watz</p>\r\n<p class="p1">The team worked on a new document that combines the proposal, its structure and process with the working group feedback. The different levels of the proposed structure are being clarified, and internationalization has been included. The role of an ombudsman and advisory board continues to be defined, to be included as a separate but essential resource for the new structure.</p>\r\n<p class="p1">Other issues being addressed include the distribution of responsibiities and workload, terminology, and the role/placement of the Board of Directors. Detail is being added to explain why change is needed, and the thought process behind the different aspects of the proposal.</p>\r\n<p class="p1">With the holidays coming up a little more time is needed revising the proposal before sharing it with the Marketing Team for clarity of the message, and then the translators. The timeframe now is to hand the document to the Marketing Team by December 25, and to the translators by January 7, maintaining the <a href="http://community.joomla.org/blogs/leadership/1833-update-on-the-proposed-structure-changes.html" target="_blank">current target date</a> of January 12 for publication to the community in multi-languages for the next phase of feedback:</p>\r\n<ul>\r\n<li>\r\n<p>January 12: Publish the revised Joomla structure proposal in multiple languages</p>\r\n</li>\r\n<li>\r\n<p>January 12 - February 8: Public discussion and feedback from the community as a whole.</p>\r\n</li>\r\n<li>\r\n<p>February 9 - February 22: Leadership team members discuss feedback from the community, and if necessary make additional revisions to proposal.</p>\r\n</li>\r\n<li>\r\n<p>February 23 - March 1:Leadership team members vote on the proposed Joomla structure.</p>\r\n</li>\r\n<li>\r\n<p>March 2: Publish a blog with the results of vote and the next steps.</p>\r\n</li>\r\n</ul>', NULL, 1, 0, 0, 677, '2014-12-20 00:00:00', 677, '2014-12-21 08:43:30', 0, '0000-00-00 00:00:00'),
	(30, 41, 0, 'Meeting Document December 10th & 11th, 2014', 'standing-meeting-document-december-10th-a-11th-201', '<p id="docs-internal-guid-9c778095-6e42-494c-d04e-1b583356cb97" style="line-height: 1.15; margin-top: 0pt; margin-bottom: 0pt;" dir="ltr"><span style="font-size: 15px; font-family: Arial; color: #000000; background-color: transparent; font-weight: normal; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline;">Present December 10th, 2014: Tom, Javier, George, Roland, Chris</span></p>\r\n<p style="line-height: 1.15; margin-top: 0pt; margin-bottom: 0pt;" dir="ltr"><span style="font-size: 15px; font-family: Arial; color: #000000; background-color: transparent; font-weight: normal; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline;">Present December 11th, 2014: Tom, George, Roland, Chris, Thomas</span></p>\r\n<p style="line-height: 1.15; margin-top: 0pt; margin-bottom: 0pt;" dir="ltr">&nbsp;</p>\r\n<p style="line-height: 1.15; margin-top: 0pt; margin-bottom: 0pt;" dir="ltr"><span style="font-size: 15px; font-family: Arial; color: #000000; background-color: transparent; font-weight: normal; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline;">The meeting started by welcoming the two new members, George and Roland, to the PLT team.</span></p>\r\n<p style="line-height: 1.15; margin-top: 0pt; margin-bottom: 0pt;" dir="ltr">&nbsp;</p>\r\n<p style="line-height: 1.15; margin-top: 0pt; margin-bottom: 0pt;" dir="ltr"><span style="font-size: 15px; font-family: Arial; color: #000000; background-color: transparent; font-weight: normal; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline;">First the team discussed the voting on new members and it was decided that the voting closed on December 14</span><span style="font-size: 9px; font-family: Arial; color: #000000; background-color: transparent; font-weight: normal; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: super;">th</span><span style="font-size: 15px; font-family: Arial; color: #000000; background-color: transparent; font-weight: normal; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline;">, 2014. A new round of voting could be held in 2015 if needed.</span></p>\r\n<p style="line-height: 1.15; margin-top: 0pt; margin-bottom: 0pt;" dir="ltr">&nbsp;</p>\r\n<p style="line-height: 1.15; margin-top: 0pt; margin-bottom: 0pt;" dir="ltr"><span style="font-size: 15px; font-family: Arial; color: #000000; background-color: transparent; font-weight: normal; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline;">To prevent a large group of PLT members to step down when their terms end in the same month the team has gone over possible options on how to spread out members across the year. No decision has been reached yet.</span></p>\r\n<p style="line-height: 1.15; margin-top: 0pt; margin-bottom: 0pt;" dir="ltr">&nbsp;</p>\r\n<p style="line-height: 1.15; margin-top: 0pt; margin-bottom: 0pt;" dir="ltr"><span style="font-size: 15px; font-family: Arial; color: #000000; background-color: transparent; font-weight: normal; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline;">With the old PLT members gone and the new members in it was time to redistribute the responsibilities. The team decided to postpone this until the PLT is complete again.</span></p>\r\n<p style="line-height: 1.15; margin-top: 0pt; margin-bottom: 0pt;" dir="ltr">&nbsp;</p>\r\n<p style="line-height: 1.15; margin-top: 0pt; margin-bottom: 0pt;" dir="ltr"><span style="font-size: 15px; font-family: Arial; color: #000000; background-color: transparent; font-weight: normal; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline;">Next topic is the structure review on formalizing the CMS Release Team. A proposal has been put forward by Michael Babker which is well received by the PLT. Chris Davenport will start a thread to discuss the CMS Release Team idea so we can finalise it.</span></p>\r\n<p style="line-height: 1.15; margin-top: 0pt; margin-bottom: 0pt;" dir="ltr">&nbsp;</p>\r\n<p style="line-height: 1.15; margin-top: 0pt; margin-bottom: 0pt;" dir="ltr"><span style="font-size: 15px; font-family: Arial; color: #000000; background-color: transparent; font-weight: normal; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline;">The budget for 2015 will be reviewed when the new members are on board. Chris Davenport will be the liaison for the budget.</span></p>\r\n<p style="line-height: 1.15; margin-top: 0pt; margin-bottom: 0pt;" dir="ltr">&nbsp;</p>\r\n<p style="line-height: 1.15; margin-top: 0pt; margin-bottom: 0pt;" dir="ltr"><span style="font-size: 15px; font-family: Arial; color: #000000; background-color: transparent; font-weight: normal; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline;">Joomla 3.4 is waiting for the new JED to go live so that com_weblinks can be published. This is a requirement for the 3.4 beta to go out so this can be tested. A final release of 3.4 is not expected this year.</span></p>\r\n<p style="line-height: 1.15; margin-top: 0pt; margin-bottom: 0pt;" dir="ltr">&nbsp;</p>\r\n<p style="line-height: 1.15; margin-top: 0pt; margin-bottom: 0pt;" dir="ltr"><span style="font-size: 15px; font-family: Arial; color: #000000; background-color: transparent; font-weight: normal; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline;">Roberto Segura will be the release manager for Joomla 3.5.</span></p>\r\n<p style="line-height: 1.15; margin-top: 0pt; margin-bottom: 0pt;" dir="ltr">&nbsp;</p>\r\n<p style="line-height: 1.15; margin-top: 0pt; margin-bottom: 0pt;" dir="ltr"><span style="font-size: 15px; font-family: Arial; color: #000000; background-color: transparent; font-weight: normal; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline;">The status of the framework is an action point for George Wilson to discuss with Michael Babker to see what they like to see happen with the framework. After George has reported back we can move forward with a plan.</span></p>\r\n<p style="line-height: 1.15; margin-top: 0pt; margin-bottom: 0pt;" dir="ltr">&nbsp;</p>\r\n<p style="line-height: 1.15; margin-top: 0pt; margin-bottom: 0pt;" dir="ltr"><span style="font-size: 15px; font-family: Arial; color: #000000; background-color: transparent; font-weight: normal; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline;">A document with the high level goals of 2015 is under review by the PLT. This is to stick to our mission "To provide a flexible platform for digital publishing and collaboration".<br /></span></p>\r\n<p style="line-height: 1.15; margin-top: 0pt; margin-bottom: 0pt;" dir="ltr">&nbsp;</p>\r\n<p style="line-height: 1.15; margin-top: 0pt; margin-bottom: 0pt;" dir="ltr"><span style="font-size: 15px; font-family: Arial; color: #000000; background-color: transparent; font-weight: normal; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline;">The minutes of the PLT meeting will now be published on the Volunteer portal in the reports section. Roland Dalmulder will write up the minutes of the December meeting for the Volunteer portal.</span></p>\r\n<p style="line-height: 1.15; margin-top: 0pt; margin-bottom: 0pt;" dir="ltr">&nbsp;</p>\r\n<p style="line-height: 1.15; margin-top: 0pt; margin-bottom: 0pt;" dir="ltr"><span style="font-size: 15px; font-family: Arial; color: #000000; background-color: transparent; font-weight: normal; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline;">There are just too many Google docs flying around the place Roland Dalmulder stated and asked if we donâ€™t have a CMS that can help. The team agreed that we have too many Google documents and as an interim solution we will have 1 Google Document that holds the links to all other Google Documents.</span></p>\r\n<p style="line-height: 1.15; margin-top: 0pt; margin-bottom: 0pt;" dir="ltr">&nbsp;</p>\r\n<p><span style="font-size: 15px; font-family: Arial; color: #000000; background-color: transparent; font-weight: normal; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline;">Now that the final release of 2.5 is out the door, it was requested if we could close all 2.5 issues on the tracker. The closing of open 2.5 issues will be done after December 31</span><span style="font-size: 9px; font-family: Arial; color: #000000; background-color: transparent; font-weight: normal; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: super;">st</span><span style="font-size: 15px; font-family: Arial; color: #000000; background-color: transparent; font-weight: normal; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline;">, 2014 after the 2.5 release has reached the end of support.</span></p>\r\n<p><span style="font-size: 15px; font-family: Arial; color: #000000; background-color: transparent; font-weight: normal; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline;">In regards to extending the End Of Support for 2.5, the PLT has decided there will not be any extended period of support for 2.5.</span></p>', NULL, 1, 0, 0, 491, '2014-12-21 00:00:00', 491, '2014-12-21 19:16:15', 0, '0000-00-00 00:00:00'),
	(31, 42, 0, 'Summary OSM Meeting 18 December, 2014', 'summary-osm-meeting-18-december-2014', '<p>The main topic of this meeting was to discuss the Budget for 2015.</p>\r\n<p>The Treasurer started the Budget Process end of October, working with liaisons from PLT and CLT.<br />Goals and Objectives for each goal have been set by each Leadership Team.<br />For most of the line items an owner is assigned and an annual dollar amount.</p>\r\n<p>For oversight, the Treasurer prepared three documents for the board containing the â€œReal Budgetâ€, the budget worksheet and a quick summary.</p>\r\n<p dir="ltr">The Treasurer is happy with the progress made but unfortunately, it is not ready to vote on in todays meeting.<br />Some line items for requested funds need more clarification and some require just one line item owner.<br />The quick summary is already showing that we are over budgeting, while the goal is a revenue neutral budget for 2015.</p>\r\n<p>To move forward the Treasurer will contact the liaisons of the Leadership Teams for the budget and set up a meeting to identify areas to trim down.</p>\r\n<p>The discussion about the Budget 2015 will continue on the mailing list, after that it will be voted on.<br />The goal is to approve the Budget before end of December, 2014.</p>\r\n<p>The dates for the next meetings are:</p>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr">8 January, 2015 - Monthly Board Meeting</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr">12 February 2015 - Monthly Board Meeting</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr">12 March 2015 - Monthly Board Meeting</p>\r\n</li>\r\n</ul>\r\n<p>Note: More detailed Meeting Minutes of this Board Meeting will be subject of the next monthly board meeting to be approved and published at the OSM website shortly after approval.</p>', NULL, 1, 0, 0, 490, '2014-12-22 17:22:32', 0, '0000-00-00 00:00:00', 0, '0000-00-00 00:00:00'),
	(32, 0, 7, 'Meeting Thursday  December 11, 2014', 'meeting-thursday-december-11-2014', '<p>Our reports are on google groups, you may visit here&nbsp;<a href="https://groups.google.com/forum/#!topic/joomla-marketing-working-group-public-list/2eQkDOUr-lA">https://groups.google.com/forum/#!topic/joomla-marketing-working-group-public-list/2eQkDOUr-lA</a></p>', NULL, 1, 0, 0, 522, '2014-12-23 23:24:55', 0, '0000-00-00 00:00:00', 0, '0000-00-00 00:00:00'),
	(33, 0, 7, 'Marketing Team Meeting  Thursday  December 18, 2014.', 'marketing-team-meeting-thursday-december-18-2014', '<p><span style="margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; font-size: 24px; font-family: \'Source Sans Pro\'; color: #6d9eeb; white-space: pre-wrap; background-color: transparent;">Marketing Team Meeting notes for</span></p>\r\n<p><span style="color: #434343; font-family: \'Source Sans Pro\'; font-size: 15px; white-space: pre-wrap; line-height: 1.15; background-color: transparent;">Thursday &nbsp;December 18, 2014. </span><a href="https://groups.google.com/forum/#!topic/joomla-marketing-working-group-public-list/Ra5GXqAe6Pw">https://groups.google.com/forum/#!topic/joomla-marketing-working-group-public-list/Ra5GXqAe6Pw</a></p>', NULL, 1, 0, 0, 522, '2014-12-23 23:26:25', 0, '0000-00-00 00:00:00', 0, '0000-00-00 00:00:00'),
	(34, 0, 50, 'January Update', 'january-update', '<h3 class="p1">Open Reports</h3>\r\n<ul>\r\n<li class="p1">Issues: 122</li>\r\n<li class="p1">Pull Requests: 364</li>\r\n</ul>\r\n<p class="p3">Total Open Reports on January 1, 2015: <strong>486</strong></p>\r\n<p class="p3">Total Open Reports on December 2, 2014:&nbsp;<strong>513</strong></p>\r\n<p class="p3">Net Change: <strong>-27</strong></p>\r\n<h3 class="p1">User Activity</h3>\r\n<ul class="ul1">\r\n<li class="li2">Active Users in December:&nbsp;<strong>162</strong></li>\r\n<li class="li2">Active Users in November:&nbsp;<strong>195</strong></li>\r\n</ul>\r\n<p class="p2">Net Change: <strong>-33</strong></p>\r\n<h3 class="p1">Items Opened/Closed</h3>\r\n<ul class="ul1">\r\n<li class="li2">Total Items Opened in December:&nbsp;<strong>313</strong></li>\r\n<li class="li2">Total Items Closed in December:&nbsp;<strong>332</strong></li>\r\n</ul>', NULL, 1, 0, 0, 447, '2015-01-01 00:00:00', 447, '2015-01-01 16:41:15', 0, '0000-00-00 00:00:00'),
	(35, 0, 49, 'Detalles de la publicaciÃ³n Enero 2015', 'detalles-de-la-publicacion-enero-2015', '<p style="font-size: 12.8000001907349px;">Durante el mes de Diciembre, a pesar de ser un mes histÃ³ricamente lento para la Magazine, debido a las vacaciones por fiestas de fin de aÃ±o, el equipo de colaboradores de la Revista de la Comunidad Joomla! trabajÃ³ arduamente por tener una ediciÃ³n que mantuviera la calidad de los artÃ­culos que hemos venido mostrando durante el Ãºltimo tiempo. Es asÃ­ que se publicÃ³ la ediciÃ³n de Enero 2015 con 9 artÃ­culos, poniendo Ã©nfasis en desarrolladores, diseÃ±adores y administradores. AsÃ­ mismo se incluyÃ³ un resumen de lo que fue el aÃ±o de Joomla! 2014 y algunas notas respecto a las novedades que veremos durante el 2015. Colaboraron con la ediciÃ³n Enero de 2015 Alejandro Pascoli, Leo Soto, Cesar Vargas-Machuca, Viviana Menzel, Andrea Kekan, Angel GarcÃ­a, Carlos Olivares, JuanKa DÃ­az y Guillermo Bravo.</p>\r\n<p style="font-size: 12.8000001907349px;">Recuerda que si tienes alguna duda, consulta, sugerencia o deseas comenzar a colaborar, puedes escribirme a&nbsp;<a href="mailto:guillermo.bravo@community.joomla.org">guillermo.bravo@community.joomla.org</a>&nbsp;y con gusto yo mismo o algÃºn miembro del equipo de trabajo te responderÃ¡.</p>', NULL, 1, 0, 0, 569, '2015-01-02 00:00:00', 569, '2015-01-02 15:42:44', 0, '0000-00-00 00:00:00'),
	(36, 41, 0, 'PLT adds new members', 'plt-adds-new-members', '<p>The PLT has added 3 new members: Robert Deutz, Viktor Vogel and Jessica Dunbar.</p>\r\n<p>All the details can be found at&nbsp;http://community.joomla.org/blogs/leadership/1841-new-additions-to-the-plt.html</p>', NULL, 1, 0, 0, 527, '2015-01-02 15:49:59', 0, '0000-00-00 00:00:00', 0, '0000-00-00 00:00:00'),
	(37, 0, 34, '07-January, 2015 Report', '07-january-2015-report', '<p>5th Meeting for System Test working Group,</p>\r\n<p>Meeting was attended by:</p>\r\n<p>Puneet Kala, Javier Gomez, Robert Deutz, Tanaporn Pantuprecharat, Kshitij Sharma, Viktor Vogel, David Jardin</p>\r\n<p>Topics Covered in the Meeting:</p>\r\n<ol>\r\n<li>Kshitij Sharma, and Puneet Kala informed about the status of System Tests, and updates on the GSOC Project work. Puneet and Javier will review the work done by Kshitij and prepare a PR to the main Repo, if everything is working as expected.</li>\r\n<li>David Jardin and Robert presented the progress that they have made running system tests on Docker Containers, All the progress that has been made is present here&nbsp;<a href="https://github.com/joomla-projects/joomla-systemtest-env">https://github.com/joomla-projects/joomla-systemtest-env</a>&nbsp;, Currently the Tests are running on local, by next meeting David and Robert will integrate them to Jenkins.</li>\r\n<li>Javier and Puneet are going to work on writing tests for weblinks in Codeception.&nbsp;</li>\r\n<li>Valerie, Kshitij and Tanaporn are going to work together on documenting:&nbsp;<a href="https://docs.joomla.org/Testing_Joomla_Extensions_with_Codeception">https://docs.joomla.org/Testing_Joomla_Extensions_with_Codeception</a><a href="https://docs.joomla.org/Testing_Joomla_Extensions_with_Codeception"></a></li>\r\n<li>Kshitij presented his new project that he has developed, a Java based GUI for running system tests, Code for the project can be found here:&nbsp;<a href="https://github.com/kshitijSharma2014/Joomla-Test-java-GUI">https://github.com/kshitijSharma2014/Joomla-Test-java-GUI</a>&nbsp;</li>\r\n<li>Javier and Milton will work together on integrating JIRA on slack chat group for system testing.</li>\r\n</ol>\r\n<p>Next meeting is planned to happen on 21st January, 2015 at</p>\r\n<p><a href="http://www.timeanddate.com/worldclock/fixedtime.html?msg=Joomla+System+Testing+Group+Meeting&amp;iso=20150121T15&amp;p1=%3A&amp;ah=1">http://www.timeanddate.com/worldclock/fixedtime.html?msg=Joomla+System+Testing+Group+Meeting&amp;iso=20150121T15&amp;p1=%3A&amp;ah=1</a>&nbsp;</p>\r\n<p>Complete Notes for todays meeting can be found here:</p>\r\n<p><a href="https://docs.google.com/document/d/1_GJqcOYBuBUNHUnDfIYXNBegfAm2rq0NBBWkpd44_0U/edit">https://docs.google.com/document/d/1XLvJiYfzyeoMvSXhcbWHjVoMaoJE95cZleYrMNwTWC4/edit</a></p>', NULL, 1, 0, 0, 525, '2015-01-07 00:00:00', 525, '2015-01-07 16:50:26', 0, '0000-00-00 00:00:00'),
	(38, 0, 53, 'Week 1 en-GB User Interface Text Working Group', 'week-1-en-gb-user-interface-text-working-group', '<p>The PLT approved the <a href="http://developer.joomla.org/news/600-announcement-en-gb-user-interface-text-working-group.html">proposal</a> to create the group.</p>\r\n<p>A <a href="https://github.com/joomla/user-interface-text">style guide</a> has been started and reviewed by the groups.</p>\r\n<p>A review of the strings has started and the first <a href="http://issues.joomla.org/tracker/joomla-cms/5615">PR</a> has been submitted.</p>\r\n<p>A list of <a href="https://github.com/joomla/user-interface-text/issues/24">issues</a> to be addressed after the release of 3.4 has been started.</p>\r\n<p>A <a href="https://github.com/joomla/user-interface-text/issues/30">first draft</a> of a proposal to manage the aproval workflow when language strings are changed or commited has been created.</p>\r\n<p>Discussions regarding the <a href="https://github.com/joomla/user-interface-text/issues/29">format</a> of the final style guide have started.</p>', NULL, 1, 0, 0, 448, '2015-01-07 22:30:06', 0, '0000-00-00 00:00:00', 0, '0000-00-00 00:00:00'),
	(39, 42, 0, 'Summary OSM Meeting 8 January, 2015', 'summary-osm-meeting-8-january-2015', '<p style="line-height: 1.15; margin-top: 0pt; margin-bottom: 0pt;" dir="ltr">&nbsp;</p>\r\n<p dir="ltr">In todays Board of Directors meeting we discussed these items:</p>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr">Approval of the Meeting Minutes of December 11 and 18, 2014 will take place on the email list.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr">Team Reports</p>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr">Demo Site:</p>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr">Free Hosting Demo at joomla.com<br />The launch needed to be delayed since a solution for supporting the free demo hosting users need to be developed.</p>\r\n</li>\r\n</ul>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr">Events Team</p>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr">The team will meet next week and discuss leadership of the team</p>\r\n</li>\r\n</ul>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr">GSoC</p>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr">Preparations for assembling a team for 2015 are made</p>\r\n</li>\r\n</ul>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr">Trademark</p>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr">A meeting is planned next week. The new website needs priority.</p>\r\n</li>\r\n</ul>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr">Certification</p>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr">The team decided not to use the certification platform that was used as a pilot at JWC. They will prepare a requirements document for the platform.</p>\r\n</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr">Unfinished Business</p>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr">Contract RFP Marketing is signed by Sensis</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr">Global Sponsor Programme<br />Joe Sonne, Tessa Mero and Ronni K. Gothard Christiansen will be working towards a detailed proposal where the board can vote on.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr">Budget<br />A meeting with representatives of OSM, PLT and CLT and the financial team took place. Where needed clarification was requested. In order to accomplish a revenue neutral budget all teams are asked to look into where we can cut down and prioritizing is requested.</p>\r\n</li>\r\n</ul>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr">New Business</p>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr">Request claim for local domain registration<br />The Board approved a reimbursement of $125,- for local domain registration to Jacques Rentzke. It involves a one time reimbursement and it does not change the decision made in the past to not register sunrise domains.</p>\r\n</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<p dir="ltr">The dates for the next meetings are:</p>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr">12 February 2015 - Monthly Board Meeting</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr">12 March 2015 - Monthly Board Meeting</p>\r\n</li>\r\n</ul>\r\n<p>Note: More detailed Meeting Minutes of this Board Meeting will be subject of the next monthly board meeting to be approved and published at the OSM website shortly after approval.</p>', NULL, 1, 0, 0, 490, '2015-01-11 23:24:06', 0, '0000-00-00 00:00:00', 0, '0000-00-00 00:00:00'),
	(40, 0, 8, 'Update on our progress and upcoming features.', 'update-on-our-progress-and-upcoming-features', '<p>This week we met in two working groups to tackle the last of the remaining approvals. At this point, we are planning to have ALL of the applications reviewed and responded to within two weeks, or by January 28th. Please make sure your listing is complete to ensure fast approval.</p>\r\n<p>We will be sending out a newsletter in a week to update all current and former JRD applicants of our status on approvals so the community stays in the loop.</p>\r\n<p>We agreed to sunset the archive of the old JRD which is on Joomla 1.5 on April 1st, 2015. If you have not applied to the JRD and are on the old Resources Directory, now is the time to prevent losing any SEO.</p>\r\n<p>A couple of members of our group will convene this week to discuss the implementation of a disaster recovery plan in the case the the site is comprimised in the future. These notes will be catalogued in the Teams JRD Google Docs.&nbsp;</p>\r\n<p>Translations of the JRD into Portugeuse are moving forward and should be completed by the first quarter of 2015, hopefully before Joomla Day Brazil. While the listings must be in English, the pages and other permanent content will be in Portugeuse. This is the first of many languages to come and is based on volunteer status. Right now we have volunteers for French and Spanish too which will come later. We discussed the possibility of having the listings be in other languages, but we will need to revisit this in April.</p>\r\n<p>This week we will review the status of the badges for the JRD and provide an ETA on that piece.</p>', NULL, 1, 0, 0, 574, '2015-01-14 17:16:12', 0, '0000-00-00 00:00:00', 0, '0000-00-00 00:00:00'),
	(41, 0, 5, 'January 2015 Events Team Meeting Report', 'january-2015-events-team-meeting-report', '<p>&nbsp;</p>\r\n<p><span style="font-size: 15px; font-family: Arial; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Attendees: </span><span style="font-size: 15px; font-family: Arial; color: #000000; background-color: transparent; font-weight: normal; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Mike Carson, Radek Suski, Sigrid Suski, Wim Marnes, Dianne Henning, Ryan Bernstein, Saurabh Shah</span><strong style="font-weight: normal;"></strong></p>\r\n<p style="line-height: 1.15; margin-top: 0pt; margin-bottom: 0pt;" dir="ltr"><span style="font-size: 15px; font-family: Arial; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Not in attendance: </span><span style="font-size: 15px; font-family: Arial; color: #000000; background-color: transparent; font-weight: normal; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">All members were in attendance</span></p>\r\n<p style="line-height: 1.15; margin-top: 0pt; margin-bottom: 0pt;" dir="ltr"><span style="font-size: 15px; font-family: Arial; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Date/Time: </span><span style="font-size: 15px; font-family: Arial; color: #000000; background-color: transparent; font-weight: normal; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Jan 14, 2015 @ 11:00 EST</span></p>\r\n<p><strong style="font-weight: n