	(1310, 0, 150, 'Educational Outreach Working Group - Agenda - August 21, 2020', 'eowg-agenda-8-21-2020', '<h1 dir="ltr" style="line-height: 1.38; margin-top: 20pt; margin-bottom: 6pt;"><span style="font-size: 20pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Joomla Educational Outreach Working Group</span></h1>\r\n<h2 dir="ltr" style="line-height: 1.38; margin-top: 18pt; margin-bottom: 6pt;"><span style="font-size: 16pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Agenda - August 21, 2020</span></h2>\r\n<p><span id="docs-internal-guid-ae7c130a-7fff-6cbe-6fba-5f97cbb7591d"></span></p>\r\n<ul style="margin-top: 0; margin-bottom: 0;">\r\n<li dir="ltr" style="list-style-type: disc; font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre;">\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: #ffff00; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Develop Working Group Mission Statement</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre;">\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: #ffff00; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Develop Working Group Goals based on Mission Statement</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre;">\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">JCM Article Status - Laura May 2020 Class (submitted)</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre;">\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">JCM Article Status - Holger August 2020 (submitted)</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre;">\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">JCM Article Status - AK&nbsp;</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre;">\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Area on google docs to upload documents / resources</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre;">\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Alert other ‘joomla members’ that this area exists?</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre;">\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Information Architecture and Content Strategy for teachjoomla.org</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre;">\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">New Business</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre;">\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Review topics for next meeting</span></p>\r\n</li>\r\n</ul>', NULL, 0, 996, 2, 1715, '2020-08-21 15:46:59', 1715, '2020-08-21 18:03:53', 0, '0000-00-00 00:00:00'),
	(1311, 0, 10, 'Social Media Team - Dancing as fast as we can. ', 'smt-dancing-as-fast-as-we-can', '<p><img src="images/reports/SMT-Dancing.jpg" alt="SMT Dancing As Fast As We Can" /></p>\r\n<p>It\'s been busy times in the SMT.&nbsp;</p>\r\n<p>In June Davide officially stepped away from the team. We wish him all the very best in his new role as Team Leader for Joomla! Volunteer Engagement Team. He is also on several other teams. Thank you for all your help. It is a pleasure to work with you.&nbsp;</p>\r\n<p>In July Ed Hathaway resigned abruptly leaving Joomla totally. Ed ran our Joomlers account but could be counted on to step up and take care of any other posts that needed to be done. I\'ve been filling in Joomlers since but having someone focused on it would be best. He is missed.</p>\r\n<p>Aside from all our usual recent posts and since our last report we supported the J&amp;Beyond event social promos, PBF, J Birthday and the semi-anual Thank You campaign to acknowledge the support of our partners and sponsors. This year we also included our Extension Partners. New graphics were made for all. Our budget was also submitted during this time.&nbsp;</p>\r\n<p>Beyond SMT our team members are also involved in other Joomla activities beyond their own personal lives. All team members either run their own businesses or work full time.</p>\r\n<p>My #1 - Assistant Team Lead Hans manages the daily posts to Facebook, Twitter, and LinkedIn and is also involved in Community Magazine Team, Marketing Local Community Liaison, Certification Exam Translations Team, and Community Translation Team.&nbsp;&nbsp;</p>\r\n<p>Sandra who is noted as a member is also our ex-Marketing DC and has that wealth of knowledge for our team. She manages our Instagram account, does graphics, suggests post concepts and so much more. She is also involved still in other areas Community Magazine Team, Creative Director for Community Magazine Team, PBF Organization Working Group, Community Translation Team, Documentation Team, Marketing Team (advisor / knowledge sharing), and Privacy Compliance Team.&nbsp; &nbsp;</p>\r\n<p>Giovanni is also a member managing our Pinterest account but is also a translator on Documentation Translation Team and involved in his local JUG.&nbsp;</p>\r\n<p>Patrick Jackson has just this weekend joined the team after a shout out for help. He is also on other teams.&nbsp;&nbsp;</p>\r\n<p>I\'m team lead and work with Hans on the core accounts, sharing posts to various FB groups (that isn\'t automated in any way), filling in on the Joomlers Twitter account, various social administration tasks, graphics and write copy (we all write copy), manage special posts for events, thank you campaigns etc. I\'m also on the Marketing team (as are Hans and Sandra) to track what is coming to social and provide input. You will also find me in the FB Joomla No Spam group where I admin there and a few other groups.&nbsp; &nbsp;</p>\r\n<p>Meetings: We meet daily in our group and are in constant communication about what is going on and how best to get the message out. We also attend meeting in various teams we are involved with.&nbsp;</p>\r\n<p>Why did I break this all down. Surely we are not the only team with hard workers. Of course not! There are a huge number of hard workers giving crazy amounts of time to the project.&nbsp;</p>\r\n<p>Recently the Contact the SMT group had a discussion about a Twitter Dev account that was not being used and why was SMT not managing it. You will find in the history of SMT on the Jdocs wiki that even back in 2011, prior to any of my team becoming involved, it\'s existance was listed as "Official Accounts Run by Other Parts of the Joomla Project". That account has never been part of our task load. When Sandra was DC and also on way more teams, that account was part of the work for that other department. That was handed off when she left that department.&nbsp;</p>\r\n<p>Next time you are coming down heavy remember we are all "dancing as fast as we can".&nbsp;</p>', NULL, 1, 997, 2, 621, '2020-08-23 15:59:48', 621, '2020-08-23 16:11:27', 0, '0000-00-00 00:00:00'),
	(1312, 51, 0, 'Production Dept. Meeting MINUTES - August 11, 2020', 'production-dept-meeting-minutes-august-11-2020', '<h1 id="docs-internal-guid-9e0aa4a4-7fff-1ccb-e724-f78d0ab5b7f7" dir="ltr" style="line-height: 1.38; text-align: right; margin-top: 20pt; margin-bottom: 6pt;"><span style="font-size: 20pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Production Dept. Meeting MINUTES</span><span style="font-size: 20pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;"><br /></span><span style="font-size: 20pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">- August 11, 2020</span><span style="font-size: 20pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;"><br /></span><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">1900 CEST, UTC+2&nbsp; </span><a href="https://www.timeanddate.com/worldclock/fixedtime.html?iso=20200407T19&amp;p1=16&amp;ah=1" style="text-decoration: none;"><span style="font-size: 11pt; font-family: Arial; color: #1155cc; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: underline; -webkit-text-decoration-skip: none; text-decoration-skip-ink: none; vertical-align: baseline; white-space: pre-wrap;">Event time TZ</span></a></h1>\r\n<p>&nbsp;</p>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Participants:</span><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;"> Benjamin Trenkle (Team Leader), Carlos Cámara (Team Leader), David Jardin (Team Leader), Harald Leithner (Release Lead 3.9), Marco Dings (Department Coordinator), Llewellyn van der Merwe (Assistant Department Coordinator), Niels Braczek (Team Leader), Philip Walton (Team Leader), Tobias Zulauf (Release Lead 3.10)</span></p>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Absent with notice:</span><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;"> Hannes Papenberg (Team Leader)</span></p>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Absent without notice: </span><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">George Wilson (Release Lead 4.0), Ilagnayeru Manickam (Team Leader), Nicola Galgano (Team Leader), Mike Brandner (Team Leader)</span></p>\r\n<h2 dir="ltr">Agenda Items &amp; minutes</h2>\r\n<h3 dir="ltr"><a href="departments/production">Production Department </a>Coordinator (Marco Dings)</h3>\r\n<h4 dir="ltr">Talk Topics:</h4>\r\n<ol>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">The Cassiopeia working group started and it is quite active. Supporting indirectly as not to influence</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Internal tasks in Github pm https://github.com/joomla/production-pm</p>\r\n</li>\r\n</ol><hr />\r\n<h3 dir="ltr">Joomla! <a href="teams/joomla-enhancement-development-team">Enhancement Development Team</a> (Benjamin Trenkle)</h3>\r\n<h4 dir="ltr">Talk Topics:</h4>\r\n<ol>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">THM had a successful week, first PR are created, 3 weeks left for the project</p>\r\n</li>\r\n</ol>\r\n<h4 dir="ltr">Working groups</h4>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Frontend template is work in progress</p>\r\n</li>\r\n</ul>\r\n<hr />\r\n<h3 dir="ltr">Joomla <a href="teams/accessibility-team">Accessibility Team</a> (Carlos Cámara Mora)</h3>\r\n<h4 dir="ltr">Talk Topics:</h4>\r\n<ol>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Back to traditional written meetings</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">New article in next issue of the magazine</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Working with the new Cassiopeia WG to help with A11y in the template</p>\r\n</li>\r\n</ol>\r\n<p>&nbsp;</p>\r\n<hr />\r\n<h3 dir="ltr"><a href="teams/security-strike-team">Security Strike Team</a> (David Jardin)</h3>\r\n<h4 dir="ltr">Updates/Proposed Topics:</h4>\r\n<ol>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">First cross-CMS sprint for PHP-TUF client happened last week, results see https://github.com/php-tuf/</p>\r\n</li>\r\n</ol><hr />\r\n<h3 dir="ltr">Releaseleads (George Wilson, Tobias Zulauf Harald Leithner)</h3>\r\n<h4 dir="ltr">* Releaseleads 3.10 (Tobias Zulauf):</h4>\r\n<ol>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Nothing to report</p>\r\n</li>\r\n</ol>\r\n<h4 dir="ltr">* Releaseleads 3.9 (Harald Leithner):</h4>\r\n<ol>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Nothing to report</p>\r\n</li>\r\n</ol>\r\n<h3 dir="ltr"><a href="teams/software-architecture-strategy-working-group">Software Architecture &amp; Strategy</a> Team (Niels Braczek)</h3>\r\n<h4 dir="ltr">Talk Topics:</h4>\r\n<ol>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Joomla 3: Where do we draw the line for improvements? See the list and date field as examples. - Improvements should be made against 4.1. In general, those improvements can then be backported to 3.10.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Copyright PR: When a pull request has two testers (approvals), it must be merged. The Copyright PR has been ready for more than 3 weeks and must just be merged, since Tobias and Llewellyn already approved it.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">RfC-RfC: Expected a motion today…?!?</p>\r\n</li>\r\n</ol><hr />\r\n<h3 dir="ltr"><a href="teams/cms-release-team">CMS Release</a> Team (Philip Walton)</h3>\r\n<h4 dir="ltr">Updates/Proposed Topics:</h4>\r\n<ol>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">No release of RC since last meeting.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Testing of 3.10 alpha is under way and will be topic of JUGL meeting next Tuesday 18-8-2020</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation"><a href="https://joomlalondon.co.uk/attend-a-jugl-meeting/event/135-joomla-london-user-group-jugl">https://joomlalondon.co.uk/attend-a-jugl-meeting/event/135-joomla-london-user-group-jugl</a></p>\r\n</li>\r\n</ol><hr />\r\n<h3 dir="ltr">Other Business</h3>\r\n<h4 dir="ltr">Talk Topics:</h4>\r\n<ol>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Roadmap blogpost to be published</p>\r\n</li>\r\n</ol>', NULL, 1, 998, 1, 597, '2020-09-01 16:29:47', 0, '2020-09-01 16:31:07', 0, '0000-00-00 00:00:00'),
	(1313, 0, 15, 'Webmasters Team Report - August, 2020', 'webmasters-team-report-august-2020', '<p><strong>Information:</strong><br />The webmasters team mainly consists of leaders and liaisons from other teams, with the purpose to effectively work together on all aspects of our properties and infrastructure.<br />The team meets as needed, because managing the properties is a daily task and communication is done through Glip, Mails and ADoodle.<br /><br />Our reports will include general information, actions performed and motions taken over a period of time.<br />The reports will not be about just one meeting which is planned every month like most teams.<br /><br /></p>\r\n<h2><strong>General:</strong></h2>\r\n<ul>\r\n<li>Working with UptimeRobot who supply the services to Joomla for free, which will make it possible to remove the other services in use for which we are required to pay.<a href="https://extensions.joomla.org/vulnerable-extensions/about/"><br /></a></li>\r\n<li>Tobias Zulauf has been working on updating the special plugins we use and creating new ones; <br /><a href="https://github.com/joomla-projects/plg_content_joomlarrssb">https://github.com/joomla-projects/plg_content_joomlarrssb</a> / <a href="https://github.com/zero-24/plg_content_mydocslanguage">https://github.com/zero-24/plg_content_mydocslanguage</a> / <a href="https://github.com/zero-24/plg_system_force2fausergroup">https://github.com/zero-24/plg_system_force2fausergroup</a></li>\r\n</ul>\r\n<h2><strong>Improvements / tasks / actions:</strong></h2>\r\n<ul>\r\n<li>All our ConfigServer Security &amp; Firewall server settings are checked and updated where needed, to keep the platform secure and protect our users for unauthorized data access.<br /><br /></li>\r\n<li>IPv6 has been implemented on the servers and will be deployed slowly per website, to support more communications flows from different platforms.<br /><br /></li>\r\n<li>The Reverse DNS has been updated, to make external systems clear who we are.<br /><br /></li>\r\n<li>Access configuration for our Analytics account properties have been updated, to make sure data is only accessible on a need to know basis.</li>\r\n<li>The SSH access keys used by Operations are recycled within all accounts.</li>\r\n</ul>', NULL, 1, 999, 2, 2198, '2020-08-31 23:43:06', 2198, '2020-09-02 12:46:50', 0, '0000-00-00 00:00:00'),
	(1314, 0, 4, 'JCM Meeting Report - August 27th, 2020', 'jcm-meeting-report-august-27th-2020', '<p><em>The meeting started at 14:30 (CEST)</em></p>\r\n<p><strong>Participants:</strong> Anja de Crom, Patrick Jackson, Luca Marzo, Marc Dechevre, Wilco Alsemgeest, Chis Keen, Andrea Gentil</p>\r\n<p><strong>Apologies</strong>: Hans van der Meer, Sandra Decoux, Mark Bender, Ahmad Moussa</p>\r\n<h3>Discussion outline</h3>\r\n<ul>\r\n<li>August issue published on time: thanks to the whole team for the work done</li>\r\n<li>September issue planning started.</li>\r\n<li>Homepage layout: ready. Next step: Apply the same layout to the Issues page</li>\r\n<li>Promotion actions: as per Sandra recommendation, we started to create texts for SM and Patrick produced images for Instagram.</li>\r\n<li>Review of monthly stats from Analytics and AcyMailing. Luca and Andrea will set up a Data Studio for the team.</li>\r\n<li>Call for authors: set up a mailing list for the registered authors in JCM website to invite them to write again for the JCM.</li>\r\n<li>Pending: Onboarding process for authors: final review to implement it.</li>\r\n</ul>\r\n<p>&nbsp;</p>\r\n<p><em>The meeting ended at 15:30 (CEST)</em></p>', NULL, 1, 1000, 1, 1459, '2020-09-02 12:57:33', 0, '2020-09-02 13:02:33', 0, '0000-00-00 00:00:00'),
	(1315, 0, 57, 'Joomla Accessibility Team - August meeting minutes', 'joomla-accessibility-team-august-meeting-minutes', '<p><strong>Date:</strong> August 26</p>\r\n<p><span style="font-weight: bold;">Atendeess:</span> Carlos Cámara (Team Leader), Armen Mnatsian (Contributor), Carlos Cámara (Team Leader), Christiane Maier-Stadtherr (Contributor), Vito Disimino (Contributor), Hans Kuijpers (Member), Donato Matturro (Member)</p>\r\n<p><span style="font-weight: bold;">Apologies:</span> Davide Messia (Assistant Team Leader), Stefan Wajda (Member), Luca Marzo (OSM Secretary), Justyna Michallek (Member), Marco Dings (Department coordinator), Vito Disimino (Contributor)</p>\r\n<h2>Topics</h2>\r\n<h3>Team cleaning tasks</h3>\r\n<p>All members were reminded to renew their consent at the identity portal to be shown in the volunteers portal.</p>\r\n<p>We welcomed Hans Kuijpers as new team member after a couple of months of great contributions to the team</p>\r\n<h3>JAT magazine articles</h3>\r\n<p>Last month Donato and Vito wrote a great article about accessible resources for J3 and they are working on a new article about A11y for next magazine issue. Since the magazine reboot we have been publishing one article per month and we are committed to keep speaking out and loud about A11y.</p>\r\n<h3>A11y status of the new Media Manager</h3>\r\n<p>After a request by Benjamin Trenkle, we have checked the status of the new Media Manager. Most of the issues regarding A11y opened last year are closed but we need to do more tests to be sure the final result complies WCAG 2.1 AA.</p>\r\n<p>Armen offered to team up with Hans and try to raise some issues for the Media Manager.</p>\r\n<h3>Lack of testers</h3>\r\n<p>Currently we lack from testers and we are exploring how to get more high level a11y testers to help us.</p>\r\n<h3>Liaison for Cassiopeia unofficial working group</h3>\r\n<p>Christianne and Hans are involved in the new unofficial Cassiopeia working group. They will liaison with the A11y and help them regarding A11y.</p>\r\n<h3>A11y issues of Workflow component</h3>\r\n<p>Christianne mentioned there were some issues with the a11y of the new Workflow component but nobody worked on fixing them. We need to work on this and give it more visibility to the issue to get it fixed.</p>', NULL, 1, 1001, 1, 632, '2020-09-02 14:15:10', 0, '2020-09-02 14:18:13', 0, '0000-00-00 00:00:00');
INSERT INTO `#__volunteers_reports` (`id`, `department`, `team`, `title`, `alias`, `description`, `notes`, `state`, `ordering`, `version`, `created_by`, `created`, `modified_by`, `modified`, `checked_out`, `checked_out_time`) VALUES
	(1316, 0, 25, 'Joomla Bug Squad Team Meeting - September 04, 2020 ', 'joomla-bug-squad-team-meeting-september-04-2020', '<p id="docs-internal-guid-ab5774e2-7fff-a802-989e-5fe3f7dab9ad" dir="ltr" style="line-height: 1.9871999999999999; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: underline; -webkit-text-decoration-skip: none; text-decoration-skip-ink: none; vertical-align: baseline; white-space: pre-wrap;">Date:</span><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;"> September&nbsp; 04, 2020</span></p>\r\n<p dir="ltr" style="line-height: 1.9871999999999999; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: underline; -webkit-text-decoration-skip: none; text-decoration-skip-ink: none; vertical-align: baseline; white-space: pre-wrap;">Time:</span><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;"> 19:05 CEST</span></p>\r\n<p dir="ltr" style="line-height: 1.9871999999999999; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: underline; -webkit-text-decoration-skip: none; text-decoration-skip-ink: none; vertical-align: baseline; white-space: pre-wrap;">Total time of meeting:</span><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;"> 1+ hour</span></p>\r\n<p dir="ltr" style="line-height: 1.9871999999999999; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Attendees:</span></p>\r\n<p dir="ltr" style="line-height: 1.9871999999999999; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Sandra Decoux, Richard Fath, Nicola Galgano, Astrid Günther, Christiane Maier-Stadtherr, Luca Racchetti, SharkyKZ, Jacob Waisner</span></p>\r\n<p>&nbsp;</p>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Discussion outline</span><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">&nbsp;&nbsp;&nbsp; </span><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">&nbsp;&nbsp;&nbsp; </span><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">&nbsp;&nbsp;&nbsp; </span></p>\r\n<p>&nbsp;</p>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Current Status</span></p>\r\n<p>&nbsp;</p>\r\n<p dir="ltr" style="line-height: 1.38; text-align: justify; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">We have approximately 770 open issues, and the trend of open issues is still growing, we hope that after the summer and holidays we can reverse that trend.</span></p>\r\n<p>&nbsp;</p>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">New Team Member</span></p>\r\n<p>&nbsp;</p>\r\n<p dir="ltr" style="line-height: 1.38; text-align: justify; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">We welcomed </span><a href="joomlers/963-luca-racchetti" style="text-decoration: none;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Luca Racchetti</span></a><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;"> to the team.</span></p>\r\n<p><br /><br /></p>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Team Twitter account</span></p>\r\n<p>&nbsp;</p>\r\n<p dir="ltr" style="line-height: 1.38; text-align: justify; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">We have far too many inactive social media accounts in the project and few people to manage them, it has been decided to close the JBS Twitter account. When the JBS wants to communicate something, the Joomla Developer account can be used. Will make a tweet saying we\'re gonna close the account and invite people to follow the dev account where we will make our future communication.</span></p>\r\n<p>&nbsp;</p>\r\n<p dir="ltr" style="line-height: 1.38; text-align: justify; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">How to work efficiently</span></p>\r\n<p>&nbsp;</p>\r\n<p dir="ltr" style="line-height: 1.38; text-align: justify; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">In the hope to work with more efficiency, and to lower the frustration and the consequent demotivation that has been reported, and with in mind the few work power that we currently have, we will start an experiment. Every team member will propose 1 issue/pr, and all the team will focus to solve those with the highest priority, only when the proposed item will be totally consumed ie (merged /rejected/ closed), a new item can be added to the list, in addition, to play fair, we implement some kind of First In First Out mechanism, to be sure that all the proposed items will be attentioned. For the start we would like to list only not too many complex items, in this way we can better evaluate if this "modus operandi" can bring us results in a short time.</span></p>\r\n<p dir="ltr" style="line-height: 1.38; text-align: justify; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Every community member can propose an item, asking to a team member to sponsor their proposal.</span></p>\r\n<p>&nbsp;</p>\r\n<p dir="ltr" style="line-height: 1.656; text-align: justify; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Next Meeting</span></p>\r\n<p dir="ltr" style="line-height: 1.656; text-align: justify; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">The next team meeting will be: Fri, 18 September at 7:00 PM CEST</span></p>', NULL, 1, 1002, 1, 526, '2020-09-05 06:53:37', 0, '2020-09-05 06:55:38', 0, '0000-00-00 00:00:00'),
	(1317, 0, 44, 'JED Team Meeting - July 31st, 2020', 'jed-team-meeting-july-31st-2020', '<h1 dir="ltr" style="line-height: 1.68; background-color: #ffffff; margin-top: 24pt; margin-bottom: 0pt; padding: 0pt 0pt 6pt 0pt;"><span style="font-size: 23pt; font-family: \'Open Sans\',sans-serif; color: #333333; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">JED Team Meeting - July 31st, 2020</span></h1>\r\n<p dir="ltr" style="line-height: 1.38; background-color: #ffffff; margin-top: 0pt; margin-bottom: 17pt;"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">The Joomla! Extensions Directory (JED) team met on July 29th, 2020, at 15hs UTC, via Glip to discuss our progress of current tasks.</span></p>\r\n<ul style="margin-top: 0; margin-bottom: 0;">\r\n<li dir="ltr" style="list-style-type: disc; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 19pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; background-color: #ffffff; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">In attendance</span><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">: Anibal Sanchez, Carlos Cámara, Claire Mandville, Dénes Székely, Hugh Douglas-Smith, Luca Marzo, Mark Lee and Wilco Alsemgeest (Assistant to the Department Coordinator)</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 19pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; background-color: #ffffff; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Apologies</span><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">: Jaz Parkyn</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 19pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; background-color: #ffffff; margin-top: 0pt; margin-bottom: 12pt;" role="presentation"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Absent</span><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">: Daniel Dubois</span></p>\r\n</li>\r\n</ul>\r\n<h2 dir="ltr" style="line-height: 1.68; background-color: #ffffff; margin-top: 18pt; margin-bottom: 4pt;"><span style="font-size: 17pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Team Organization</span></h2>\r\n<ul style="margin-top: 0; margin-bottom: 0;">\r\n<li dir="ltr" style="list-style-type: disc; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 19pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; background-color: #ffffff; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">We are processing the queue of new extensions and pending tickets within the seven days processing window. With the addition of new members, we are improving the processing times.</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 19pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; background-color: #ffffff; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">In July, we are migrating our accounts to @community.joomla.org</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 19pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; background-color: #ffffff; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">The VEL merge of operations completed. We are polishing the final steps.</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 19pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; background-color: #ffffff; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">JEDChecker translation project is in the new Crowdin Enterprise: </span><a href="https://joomla.crowdin.com/jed-checker-extension" style="text-decoration: none;"><span style="font-size: 10.5pt; font-family: Arial; color: #1155cc; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: underline; -webkit-text-decoration-skip: none; text-decoration-skip-ink: none; vertical-align: baseline; white-space: pre-wrap;">https://joomla.crowdin.com/jed-checker-extension</span></a></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 19pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; background-color: #ffffff; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">JED Newsletter has been sent following the release of the new Joomla 4 Beta and Joomla 3.10 Alpha</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 19pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; background-color: #ffffff; margin-top: 0pt; margin-bottom: 12pt;" role="presentation"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">JED Ticketing System, we are migrating the canned responses to the Knowledgebase</span></p>\r\n</li>\r\n</ul>\r\n<h2 dir="ltr" style="line-height: 1.68; background-color: #ffffff; margin-top: 18pt; margin-bottom: 4pt;"><span style="font-size: 17pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Plans</span></h2>\r\n<ul style="margin-top: 0; margin-bottom: 0;">\r\n<li dir="ltr" style="list-style-type: disc; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 19pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; background-color: #ffffff; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">JED LinkChecker - Next Phases: </span><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">we are adding stricter validations to the link validations</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 19pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; background-color: #ffffff; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">JED Design Updates: </span><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">General review of the site design for mobile and desktop. Renewal of JED general visual technology and design. </span><a href="https://github.com/joomla/jed-issues/issues/139" style="text-decoration: none;"><span style="font-size: 10.5pt; font-family: Arial; color: #0088cc; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">#139</span></a></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 19pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; background-color: #ffffff; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">List Q1: Quality Requirements of Extensions </span><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">To improve the current extension quality requirements, we are defining a new set of minimum rules. The idea is to improve the minimum information submitted by the extension developers. </span><a href="https://github.com/joomla/jed-issues/issues/149" style="text-decoration: none;"><span style="font-size: 10.5pt; font-family: Arial; color: #0088cc; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">#149</span></a></p>\r\n<ul style="margin-top: 0; margin-bottom: 0;">\r\n<li dir="ltr" style="list-style-type: circle; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 38pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">At this point, these are the rules that we are currently proposing:</span></p>\r\n<ul style="margin-top: 0; margin-bottom: 0;">\r\n<li dir="ltr" style="list-style-type: square; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 57pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Listings in English&nbsp;</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: square; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 57pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Project or Download page at least in English</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: square; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 57pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Screenshots in English</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: square; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 57pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Provide English language files</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: square; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 57pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Links linking to the right page (E.g. the demo link must target a proper demo)</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: square; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 57pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Screenshots with logo and/or an accurate representation of the extension</span></p>\r\n</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 19pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; background-color: #ffffff; margin-top: 0pt; margin-bottom: 12pt;" role="presentation"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">JED Checker CI Online. </span><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">This is project scope: </span><a href="https://github.com/joomla/jed-issues/wiki/JED-Extensions-Checker-CI" style="text-decoration: none;"><span style="font-size: 10.5pt; font-family: Arial; color: #0088cc; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">https://github.com/joomla/jed-issues/wiki/JED-Extensions-Checker-CI</span></a><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">. At the FFTF, Andy Gaskell and Llewellyn van der Merwe joined the JED Dev Team to support the effort. </span><a href="https://github.com/joomla/jed-issues/issues/67" style="text-decoration: none;"><span style="font-size: 10.5pt; font-family: Arial; color: #0088cc; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">#67</span></a><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;"> and </span><a href="https://github.com/joomla/jed-issues/issues/133" style="text-decoration: none;"><span style="font-size: 10.5pt; font-family: Arial; color: #0088cc; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">#133</span></a></p>\r\n</li>\r\n</ul>\r\n<h2 dir="ltr" style="line-height: 1.68; background-color: #ffffff; margin-top: 18pt; margin-bottom: 4pt;"><span style="font-size: 17pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">JED 4 Project</span></h2>\r\n<ul style="margin-top: 0; margin-bottom: 0;">\r\n<li dir="ltr" style="list-style-type: disc; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 19pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; background-color: #ffffff; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">JED4 development has been continuing. To contribute to the project, </span><a href="https://github.com/joomla/com_jed4/" style="text-decoration: none;"><span style="font-size: 10.5pt; font-family: Arial; color: #0088cc; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">join the team!</span></a></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 19pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; background-color: #ffffff; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">The board approved the proposal to make the new JED 4 codebase and project information available publicly.</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 19pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; background-color: #ffffff; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">The Review Detective and Review Scores will be kept private within the JED team.</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 19pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; background-color: #ffffff; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">This is the </span><a href="https://github.com/joomla/jed-issues/wiki/JED-4-Specifications-Summary" style="text-decoration: none;"><span style="font-size: 10.5pt; font-family: Arial; color: #0088cc; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">JED 4 Project Scope Summary</span></a><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">.</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 19pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; background-color: #ffffff; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">The </span><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">JED 4 development repository</span><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;"> has been published: </span><a href="https://github.com/joomla/com_jed4/" style="text-decoration: none;"><span style="font-size: 10.5pt; font-family: Arial; color: #0088cc; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">https://github.com/joomla/com_jed4</span></a><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">. Please, check the list of issues and collaborate with the development.</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 19pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; background-color: #ffffff; margin-top: 0pt; margin-bottom: 12pt;" role="presentation"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">According to the approved </span><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Joomla Property Management Policy, </span><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">JED4 will also manage templates.&nbsp;</span></p>\r\n</li>\r\n</ul>\r\n<h2 dir="ltr" style="line-height: 1.68; background-color: #ffffff; margin-top: 18pt; margin-bottom: 4pt;"><span style="font-size: 17pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Proposed JED Guidelines for Joomla 4</span></h2>\r\n<ul style="margin-top: 0; margin-bottom: 0;">\r\n<li dir="ltr" style="list-style-type: disc; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 19pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; background-color: #ffffff; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><a href="https://docs.joomla.org/Potential_backward_compatibility_issues_in_Joomla_4" style="text-decoration: none;"><span style="font-size: 10.5pt; font-family: Arial; color: #0088cc; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Potential backward compatibility issues in Joomla 4</span></a></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 19pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; background-color: #ffffff; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><a href="https://github.com/joomla/jed-issues/wiki/Frequently-Answered-Questions-about-Joomla-4" style="text-decoration: none;"><span style="font-size: 10.5pt; font-family: Arial; color: #0088cc; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Frequently Answered Questions about Joomla 4</span></a></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 19pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; background-color: #ffffff; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><a href="https://github.com/joomla/jed-issues/issues/67" style="text-decoration: none;"><span style="font-size: 10.5pt; font-family: Arial; color: #0088cc; background-color: transparent; font-weight: 400; font-style: italic; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">New extensions guidelines for J4 and JED Checker CI for Joomla 4</span><span style="font-size: 10.5pt; font-family: Arial; color: #0088cc; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">. </span><span style="font-size: 10.5pt; font-family: Arial; color: #0088cc; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">#67</span></a></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 19pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; background-color: #ffffff; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Minimum technical requirements</span><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">: We have been discussing the steps to manage extensions for Joomla 3, and Joomla 4 after Joomla 4 is released, to keep compatibility with minimum technical requirements for Joomla 3 and encourage the usage of PHP latest stable version and security practices.</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 19pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; background-color: #ffffff; margin-top: 0pt; margin-bottom: 12pt;" role="presentation"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Override Everything</span><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">: All assets should go through HTML helper or web assets. In this way, the assets are overridable, and scripts can be put in the footer and still work. Restrict handwritten script tags in the output (This needs to go through addScriptDeclaration). Include assets ONLY in the layouts, not in the main logic.</span></p>\r\n</li>\r\n</ul>\r\n<h2 dir="ltr" style="line-height: 1.68; background-color: #ffffff; margin-top: 18pt; margin-bottom: 4pt;"><span style="font-size: 17pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Scoring Issues</span></h2>\r\n<ul style="margin-top: 0; margin-bottom: 0;">\r\n<li dir="ltr" style="list-style-type: disc; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 19pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; background-color: #ffffff; margin-top: 0pt; margin-bottom: 12pt;" role="presentation"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">An issue on the extensions scoring has been reported. We are looking for a developer to evaluate the current algorithm and fix the issue. For more information </span><a href="https://github.com/joomla/jed-issues/issues/84" style="text-decoration: none;"><span style="font-size: 10.5pt; font-family: Arial; color: #0088cc; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">#84</span></a></p>\r\n</li>\r\n</ul>\r\n<h2 dir="ltr" style="line-height: 1.68; background-color: #ffffff; margin-top: 18pt; margin-bottom: 0pt; padding: 0pt 0pt 4pt 0pt;"><span style="font-size: 17pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Spam Checks for Reviews</span></h2>\r\n<p dir="ltr" style="line-height: 1.38; background-color: #ffffff; margin-top: 0pt; margin-bottom: 0pt; padding: 0pt 0pt 17pt 0pt;"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">The current system to process Spam Review needs to be revamped. The current implementation has several shortcomings that must be addressed.</span></p>\r\n<p dir="ltr" style="line-height: 1.38; background-color: #ffffff; margin-top: 0pt; margin-bottom: 17pt;"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">In the short term, to clarify the validation output, we are improving the messages shown to the user. Spam Checkup can fail due to the following points:</span></p>\r\n<ul style="margin-top: 0; margin-bottom: 0;">\r\n<li dir="ltr" style="list-style-type: disc; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 19pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; background-color: #ffffff; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">You may be using an IP masking service</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 19pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; background-color: #ffffff; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">A VPN</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 19pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; background-color: #ffffff; margin-top: 0pt; margin-bottom: 12pt;" role="presentation"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">A server is actively running on your desktop IP</span></p>\r\n</li>\r\n</ul>\r\n<p dir="ltr" style="line-height: 1.38; background-color: #ffffff; margin-top: 0pt; margin-bottom: 0pt; padding: 0pt 0pt 17pt 0pt;"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">In the medium term, we are planning to redefine the system and implement a new checkup in the context of the JED4 project. We are updating the progress in the implementation here </span><a href="https://github.com/joomla/jed-issues/issues/85" style="text-decoration: none;"><span style="font-size: 10.5pt; font-family: Arial; color: #0088cc; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">#65</span></a><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">.</span></p>\r\n<p><span id="docs-internal-guid-8632718a-7fff-b101-4421-56bb1f392e72"></span></p>', NULL, 1, 1003, 1, 628, '2020-08-02 09:05:02', 0, '2020-09-05 09:05:57', 0, '0000-00-00 00:00:00'),
	(1318, 58, 0, 'OSM, Inc. - Full Board Meeting - August 27, 2020', 'osm-inc-full-board-meeting-august-27-2020', '<h2 id="docs-internal-guid-ecdb8d25-7fff-023b-8e4a-2fc4566bfab6" dir="ltr">Open Source Matters, Inc.<br />Full Board of Directors and Officers Meeting</h2>\r\n<p dir="ltr">Date: August 27, 2020<br />Time: 17:00 UTC<br />Total time of meeting: 1 hour 30 minutes</p>\r\n<h4 dir="ltr">Attendees:</h4>\r\n<p dir="ltr">Brian Mitchell (President), Elisa Foltyn (Vice President), Luca Marzo (Secretary), Hugh Douglas-Smith (Director), Djamel Kherbi (Director), Jaz Parkyn (Director), Radek Suski (Director), Eric Lamy (Director).</p>\r\n<h4 dir="ltr">Apologies:</h4>\r\n<p dir="ltr">Marco Dings (Director).</p>\r\n<h4 dir="ltr">Proxies:</h4>\r\n<p dir="ltr">Jaz Parkyn for Marco Dings.</p>\r\n<h4 dir="ltr">Invited guests:</h4>\r\n<p dir="ltr">Marianela Queme (Elected Director)</p>\r\n<h2 dir="ltr">Discussion outline</h2>\r\n<h3 dir="ltr">President</h3>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">No major updates.</p>\r\n</li>\r\n</ul>\r\n<h3 dir="ltr">Treasurer</h3>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Brian shared the bank balances as of yesterday.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">BuySellAds payment came to the PayPal account.</p>\r\n</li>\r\n</ul>\r\n<h3 dir="ltr">Production Department</h3>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation"><a href="https://www.joomla.org/announcements/release-news/5821-joomla-3-9-21.html">Joomla 3.9.21 released</a>.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Provisional roadmap published <a href="https://www.joomla.org/announcements/release-news/5819-the-day-after-joomla-4-0-planning-the-future.html">The Day After Joomla 4.0 - Planning the Future</a>.</p>\r\n</li>\r\n</ul>\r\n<h3 dir="ltr">Legal &amp; Finance Department</h3>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Hugh gained control of the account with BuySellAds and has cashed out $6500 which is being paid into our PayPal account, this leaves a balance of $35. We can now start to use this again and generate monthly revenue.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Hugh presented an analysis of the income generated by the current ads serving platform compared with the previous platform. Further analysis and decisions need to be made on that.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Hugh is working to resolve access issues with the Google Adsense account</p>\r\n</li>\r\n</ul>\r\n<h3 dir="ltr">Marketing &amp; Communications Department</h3>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Marianela is being onboarded as the newly elected Marketing &amp; Communications DC.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Landing page update. We have a volunteer who is currently working to make this happen. We need to discuss with Operations the management of this page.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">The new landing page will inherit the top menu and footer from the CDN and propose a totally different look and feel. The page will be published in the joomla.org/4 subfolder.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Marianela requested Brian to engage “officially” with 123RF, to request credits for videos.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Instagram. We are currently working on a giveaway for Instagram to glean more followers for brand awareness.&nbsp;</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">The Marketing Team is about to arrange a Team Lead election. People from the J4 Marketing subteam, might consider joining the main team.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Marianela hasn’t defined yet the Assistant to the DC.</p>\r\n</li>\r\n</ul>\r\n<h3 dir="ltr">Events Department</h3>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Brian Ronnow organized the 6th of August&nbsp; the first meeting with JUG organizers, the main purpose of the meeting is to share ideas and experiences among the JUG organizers. This will also benefit the new JUG organizers who are struggling to organize meetings.</p>\r\n</li>\r\n</ul>\r\n<h3 dir="ltr">Operations Department</h3>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">The Joomla Community Magazine is working continuously and doing a great job. The latest team meeting has been held today. The meeting report will be published soon on the Volunteers Portal.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">VEL Website is merged into the Extensions website. The latest JED meeting was on June 26th. The meeting report can be found at Volunteers Portal.&nbsp;</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">The (JED) Extensions GSuite is phased out, everybody is now using community.jomla.org addresses.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">&nbsp;JED4 development is ongoing, a two day sprint has been performed. Everyone is invited to help with its development.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">A survey has been created by the JTD team and shared with the help of the Social Media Team.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">The JVP team is continuously working on all reported issues.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">The webmaster team requested feedback from the Webmasters to standardize the Joomla Privacy and security settings.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">All cPanel passwords have been regenerated to make sure access information is kept secure in the vaults.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">The Board discussed the consent renewal of the Identity Portal.</p>\r\n</li>\r\n</ul>\r\n<h3 dir="ltr">Programs Department</h3>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Jaz has been working on a document to formally outline the responsibilities of Department Coordinators.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Jaz attended the last Educational Outreach WG meeting and helped them to put the next steps in place for defining their mission and goals.</p>\r\n</li>\r\n</ul>\r\n<h3 dir="ltr">Vice President</h3>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">In the German channel there was a discussion about “user driven decisions” . Elisa to explore the solutions to get user feedback.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Elisa had training with kids for 1 week before vacations, it went really well.&nbsp;</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Elisa suggested to consider the opportunity to prepare a “UX release”, focused on improving the User Experience and making everything easier.</p>\r\n</li>\r\n</ul>\r\n<h3 dir="ltr">Secretary</h3>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">[<strong>Elections</strong>] Ballots for Vice President and Events DC are running until August 30, 2020.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">[<strong>Elections</strong>] The Call for Manifestos for the Treasurer role has been extended until August 30, 2020. No manifestos have been received so far.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">[<strong>Elections</strong>] Marianela Quemé has been elected as Marketing &amp; Communications Department Coordinator. The handover period has started, so she will be invited to all the Board Meetings until the official commencement of her term as DC.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">[<strong>Elections</strong>] Jaz Parkyn has been re-elected as Programs Department Coordinator.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">[<strong>Shop RFP</strong>] One proposal has been received. The proposal is available for Board Review. According to the timeline, we should notify the vendor by September 05, 2020.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">[<strong>Conflict Handling RFP</strong>] One proposal has been received. The proposal is available for Board review. According to the timeline, we should notify the vendor by September 05, 2020.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">[<strong>Google Partnership</strong>] Missing some details about OSM bank account to complete the administrative part, after the contract signature.</p>\r\n</li>\r\n</ul>\r\n<h3 dir="ltr">Other business</h3>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Marco highlighted the need to use functional mailboxes for Department Coordinators.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Marco requested a clarification on the effects of the missing Privacy consent renewals on the Identity Portal for showing data on the Volunteers Portal. Luca clarified that the membership is not affected by the missing consent, as OSM has a legal basis to continue processing members data, as mandated by the Law of New York and the Bylaws.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">The Board discussed the status and the achievements of the agents appointed to find sponsors.</p>\r\n</li>\r\n</ul>\r\n<h3 dir="ltr">Motions taken during this meeting</h3>\r\n<p dir="ltr"><strong>#2020/115 - Extend by two weeks the length of submission period of the Shop RFP and Conflict Handling RFP.<br /></strong>Proposed by Luca Marzo, seconded by Radek Suski.<br /><em>The motion passed with unanimous consent.</em></p>\r\n<p dir="ltr"><strong>#2020/116 - Revoke the appointment of Carrie Megginson as an Agent of the Corporation according to the Corporate Policy regarding Sponsorship Commissions.<br /></strong>Proposed by Hugh Douglas-Smith, seconded by Luca Marzo.<br /><em>The motion passed with unanimous consent.</em></p>\r\n<p dir="ltr"><strong>#2020/117 - The Secretary proposes to adjourn the meeting.<br /></strong>Proposed by Luca Marzo, seconded by Hugh Douglas-Smith.<br /><em>The motion passed with unanimous consent.</em></p>\r\n<h3 dir="ltr">Motions taken offline before this meeting</h3>\r\n<p dir="ltr"><strong>#2020/114 - Approve and publish the Board Meeting Minutes.</strong><br />Proposed by Luca Marzo, seconded by Brian Mitchell.<br /><em>The motion passed with unanimous consent.</em></p>', NULL, 1, 1004, 1, 669, '2020-09-05 10:13:35', 0, '2020-09-05 10:16:31', 0, '0000-00-00 00:00:00'),
	(1319, 0, 44, 'JED Team Meeting - August 28th, 2020', 'jed-team-meeting-august-28th-2020', '<p dir="ltr" style="line-height: 1.38; background-color: #ffffff; margin-top: 0pt; margin-bottom: 17pt;"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">The Joomla! Extensions Directory (JED) team met on August 28th, 2020, at 15hs UTC, via Glip to discuss our progress of current tasks.</span></p>\r\n<ul style="margin-top: 0; margin-bottom: 0;">\r\n<li dir="ltr" style="list-style-type: disc; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 19pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; background-color: #ffffff; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">In attendance</span><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">: Anibal Sanchez, Carlos Cámara, Claire Mandville, Dénes Székely, Hugh Douglas-Smith, Jaz Parkyn, Luca Marzo, Mark Lee and Wilco Alsemgeest (Assistant to the Department Coordinator)</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 19pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; background-color: #ffffff; margin-top: 0pt; margin-bottom: 12pt;" role="presentation"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Absent</span><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">: Daniel Dubois and Dénes Székely</span></p>\r\n</li>\r\n</ul>\r\n<h2 dir="ltr" style="line-height: 1.68; background-color: #ffffff; margin-top: 18pt; margin-bottom: 4pt;"><span style="font-size: 17pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Team Organization</span></h2>\r\n<ul style="margin-top: 0; margin-bottom: 0;">\r\n<li dir="ltr" style="list-style-type: disc; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 19pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; background-color: #ffffff; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">We are processing the queue of new extensions and pending tickets within the seven days processing window. With the addition of new members, we are improving the processing times.</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 19pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; background-color: #ffffff; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">In July, we migrated our accounts to @community.joomla.org and reviewed the permissions of the team members on the Identity Portal.</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 19pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; background-color: #ffffff; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">The VEL merge of operations completed. We are polishing the final steps.</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 19pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; background-color: #ffffff; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">We are also reviewing the backup policy and the associated costs.</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 19pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; background-color: #ffffff; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">JEDChecker translation project is in the new Crowdin Enterprise: </span><a href="https://joomla.crowdin.com/jed-checker-extension" style="text-decoration: none;"><span style="font-size: 10.5pt; font-family: Arial; color: #1155cc; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: underline; -webkit-text-decoration-skip: none; text-decoration-skip-ink: none; vertical-align: baseline; white-space: pre-wrap;">https://joomla.crowdin.com/jed-checker-extension</span></a><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">. We have released </span><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">JEDChecker v2.3.0, </span><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">with support for 23 languages.&nbsp;</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 19pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; background-color: #ffffff; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">JED Ticketing System, we are migrating the canned responses to the Knowledgebase</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 19pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; background-color: #ffffff; margin-top: 0pt; margin-bottom: 12pt;" role="presentation"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Data Cleansing</span><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">, we are preparing a cleanup of old reviews and the anonymization of old accounts without activity.</span></p>\r\n</li>\r\n</ul>\r\n<h2 dir="ltr" style="line-height: 1.68; background-color: #ffffff; margin-top: 18pt; margin-bottom: 4pt;"><span style="font-size: 17pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Plans</span></h2>\r\n<ul style="margin-top: 0; margin-bottom: 0;">\r\n<li dir="ltr" style="list-style-type: disc; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 19pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; background-color: #ffffff; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">JED LinkChecker - Next Phases: </span><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">we are adding stricter validations to the link validations</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 19pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; background-color: #ffffff; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">JED Design Updates: </span><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">General review of the site design for mobile and desktop. Renewal of JED general visual technology and design. </span><a href="https://github.com/joomla/jed-issues/issues/139" style="text-decoration: none;"><span style="font-size: 10.5pt; font-family: Arial; color: #0088cc; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">#139</span></a></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 19pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; background-color: #ffffff; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">List Q1: Quality Requirements of Extensions </span><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">To improve the current extension quality requirements, we are defining a new set of minimum rules. The idea is to improve the minimum information submitted by the extension developers. </span><a href="https://github.com/joomla/jed-issues/issues/149" style="text-decoration: none;"><span style="font-size: 10.5pt; font-family: Arial; color: #0088cc; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">#149</span></a></p>\r\n<ul style="margin-top: 0; margin-bottom: 0;">\r\n<li dir="ltr" style="list-style-type: circle; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 38pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">At this point, these are the rules that we are currently proposing:</span></p>\r\n<ul style="margin-top: 0; margin-bottom: 0;">\r\n<li dir="ltr" style="list-style-type: square; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 57pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Listings in English&nbsp;</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: square; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 57pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Project or Download page at least in English</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: square; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 57pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Screenshots in English</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: square; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 57pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Provide English language files</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: square; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 57pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Links linking to the right page (E.g. the demo link must target a proper demo)</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: square; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 57pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Screenshots with logo and/or an accurate representation of the extension</span></p>\r\n</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 19pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; background-color: #ffffff; margin-top: 0pt; margin-bottom: 12pt;" role="presentation"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">JED Checker CI Online. </span><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">This is project scope: </span><a href="https://github.com/joomla/jed-issues/wiki/JED-Extensions-Checker-CI" style="text-decoration: none;"><span style="font-size: 10.5pt; font-family: Arial; color: #0088cc; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">https://github.com/joomla/jed-issues/wiki/JED-Extensions-Checker-CI</span></a><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">. At the FFTF, Andy Gaskell and Llewellyn van der Merwe joined the JED Dev Team to support the effort. </span><a href="https://github.com/joomla/jed-issues/issues/67" style="text-decoration: none;"><span style="font-size: 10.5pt; font-family: Arial; color: #0088cc; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">#67</span></a><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;"> and </span><a href="https://github.com/joomla/jed-issues/issues/133" style="text-decoration: none;"><span style="font-size: 10.5pt; font-family: Arial; color: #0088cc; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">#133</span></a></p>\r\n</li>\r\n</ul>\r\n<h2 dir="ltr" style="line-height: 1.68; background-color: #ffffff; margin-top: 18pt; margin-bottom: 4pt;"><span style="font-size: 17pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">JED 4 Project</span></h2>\r\n<ul style="margin-top: 0; margin-bottom: 0;">\r\n<li dir="ltr" style="list-style-type: disc; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 19pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; background-color: #ffffff; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">JED4 development has been continuing. To contribute to the project, </span><a href="https://github.com/joomla/com_jed4/" style="text-decoration: none;"><span style="font-size: 10.5pt; font-family: Arial; color: #0088cc; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">join the team!</span></a></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 19pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; background-color: #ffffff; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">The board approved the proposal to make the new JED 4 codebase and project information available publicly.</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 19pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; background-color: #ffffff; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">The Review Detective and Review Scores will be kept private within the JED team.</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 19pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; background-color: #ffffff; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">This is the </span><a href="https://github.com/joomla/jed-issues/wiki/JED-4-Specifications-Summary" style="text-decoration: none;"><span style="font-size: 10.5pt; font-family: Arial; color: #0088cc; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">JED 4 Project Scope Summary</span></a><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">.</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 19pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; background-color: #ffffff; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">The </span><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">JED 4 development repository</span><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;"> has been published: </span><a href="https://github.com/joomla/com_jed4/" style="text-decoration: none;"><span style="font-size: 10.5pt; font-family: Arial; color: #0088cc; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">https://github.com/joomla/com_jed4</span></a><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">. Please, check the list of issues and collaborate with the development.</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 19pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; background-color: #ffffff; margin-top: 0pt; margin-bottom: 12pt;" role="presentation"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">According to the approved </span><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Joomla Property Management Policy, </span><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">JED4 will also manage templates.&nbsp;</span></p>\r\n</li>\r\n</ul>\r\n<h2 dir="ltr" style="line-height: 1.68; background-color: #ffffff; margin-top: 18pt; margin-bottom: 4pt;"><span style="font-size: 17pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Proposed JED Guidelines for Joomla 4</span></h2>\r\n<ul style="margin-top: 0; margin-bottom: 0;">\r\n<li dir="ltr" style="list-style-type: disc; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 19pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; background-color: #ffffff; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><a href="https://docs.joomla.org/Potential_backward_compatibility_issues_in_Joomla_4" style="text-decoration: none;"><span style="font-size: 10.5pt; font-family: Arial; color: #0088cc; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Potential backward compatibility issues in Joomla 4</span></a></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 19pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; background-color: #ffffff; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><a href="https://github.com/joomla/jed-issues/wiki/Frequently-Answered-Questions-about-Joomla-4" style="text-decoration: none;"><span style="font-size: 10.5pt; font-family: Arial; color: #0088cc; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Frequently Answered Questions about Joomla 4</span></a></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 19pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; background-color: #ffffff; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><a href="https://github.com/joomla/jed-issues/issues/67" style="text-decoration: none;"><span style="font-size: 10.5pt; font-family: Arial; color: #0088cc; background-color: transparent; font-weight: 400; font-style: italic; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">New extensions guidelines for J4 and JED Checker CI for Joomla 4</span><span style="font-size: 10.5pt; font-family: Arial; color: #0088cc; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">. </span><span style="font-size: 10.5pt; font-family: Arial; color: #0088cc; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">#67</span></a></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 19pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; background-color: #ffffff; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Minimum technical requirements</span><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">: We have been discussing the steps to manage extensions for Joomla 3, and Joomla 4 after Joomla 4 is released, to keep compatibility with minimum technical requirements for Joomla 3 and encourage the usage of PHP latest stable version and security practices.</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 19pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; background-color: #ffffff; margin-top: 0pt; margin-bottom: 12pt;" role="presentation"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Override Everything</span><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">: All assets should go through HTML helper or web assets. In this way, the assets are overridable, and scripts can be put in the footer and still work. Restrict handwritten script tags in the output (This needs to go through addScriptDeclaration). Include assets ONLY in the layouts, not in the main logic.</span></p>\r\n</li>\r\n</ul>\r\n<h2 dir="ltr" style="line-height: 1.68; background-color: #ffffff; margin-top: 18pt; margin-bottom: 4pt;"><span style="font-size: 17pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Scoring Issues</span></h2>\r\n<ul style="margin-top: 0; margin-bottom: 0;">\r\n<li dir="ltr" style="list-style-type: disc; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 19pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; background-color: #ffffff; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">An issue on the extensions scoring has been reported. We are looking for a developer to evaluate the current algorithm and fix the issue. For more information </span><a href="https://github.com/joomla/jed-issues/issues/84" style="text-decoration: none;"><span style="font-size: 10.5pt; font-family: Arial; color: #0088cc; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">#84</span></a></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre; margin-left: 19pt;">\r\n<p dir="ltr" style="line-height: 1.8662739322533133; background-color: #ffffff; margin-top: 0pt; margin-bottom: 12pt;" role="presentation"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">We have fixed urgent issues caused by internal changes in the stats. </span><a href="https://github.com/joomla/jed-issues/issues/196" style="text-decoration: none;"><span style="font-size: 10.5pt; font-family: Arial; color: #1155cc; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: underline; -webkit-text-decoration-skip: none; text-decoration-skip-ink: none; vertical-align: baseline; white-space: pre-wrap;">#196</span></a><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">.</span></p>\r\n</li>\r\n</ul>\r\n<h2 dir="ltr" style="line-height: 1.68; background-color: #ffffff; margin-top: 18pt; margin-bottom: 0pt; padding: 0pt 0pt 4pt 0pt;"><span style="font-size: 17pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Spam Checks for Reviews</span></h2>\r\n<p dir="ltr" style="line-height: 1.38; background-color: #ffffff; margin-top: 0pt; margin-bottom: 0pt; padding: 0pt 0pt 17pt 0pt;"><span style="font-size: 10.5pt; font-family: Arial; color: #333333; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">The current system to process Spam Review needs to be revamped. The current implementation has several shortcomings that must be addressed.</span></p>\r\n<p><span id="docs-internal-guid-049da800-7fff-adcd-be7b-a912b65e5c91"><span style="font-size: 10.5pt; font-family: Arial; background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;">In the medium term, we are planning to redefine the system and implement a new checkup in the context of the JED4 project. We are updating the progress in the implementation here </span><a href="https://github.com/joomla/jed-issues/issues/85"><span style="font-size: 10.5pt; font-family: Arial; background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;">#65</span></a><span style="font-size: 10.5pt; font-family: Arial; background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;">.</span></span></p>', NULL, 1, 1005, 1, 628, '2020-09-07 10:38:28', 0, '2020-09-07 10:39:07', 0, '0000-00-00 00:00:00'),
	(1320, 0, 78, 'Certification Program: team meeting - August 2020', 'certification-program-team-meeting-august-2020', '<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>The meeting started September 2nd 2020 at 17:00 (UTC).</p>\r\n<h3 style="font-family: \'Helvetica Neue\', Helvetica, Arial, sans-serif; color: #333333;">Particpants:</h3>\r\n<p><em>Attendees:</em><span style="caret-color: auto;"></span><span style="caret-color: auto;">Andrea Gentil, Jeroen Moolenschot, Luca Marzo and Chris Keen.</span></p>\r\n<p><em>Guest:</em>&nbsp;Jaz Parkyn (Dept. Co-ordinator)<br /><br /><em>Absent:&nbsp;</em></p>\r\n<h2 style="font-family: \'Helvetica Neue\', Helvetica, Arial, sans-serif; color: #333333;">General points</h2>\r\n<p><strong>Team news:</strong><br />Wilco (Alsemgeest) has formally left the JCP team (although he remains in his Webmaster role): <br />We would like to really thank Wilco for all his input and appreciate all the work he has carried out during his time in the JCP. We wish him success with his work outside the JCP!</p>\r\n<p><span id="docs-internal-guid-db13cfb0-7fff-1e7f-2d9c-50751520fef3"><span style="font-size: 11pt; font-family: Roboto, sans-serif; color: #000000; background-color: transparent; font-weight: bold; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;">Exam sessions:<br /></span><span style="font-size: 11pt; font-family: Roboto, sans-serif; color: #000000; background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;">There are two exams confirmed in the upcoming period.</span></span></p>\r\n<ul>\r\n<li>September 11th 2020 -&nbsp; Zurich, Switzerland (number of candidates not yet known).</li>\r\n<li>October 16th 2020&nbsp; -&nbsp; Chicago, USA.<span style="caret-color: auto;">(number of candidates not yet known).</span><span style="caret-color: auto;"></span></li>\r\n</ul>\r\n<p>There is an also an unconfirmed exam scheduled to held held in Malaysia (details are not yet known)&nbsp;</p>\r\n<p><span id="docs-internal-guid-db13cfb0-7fff-1e7f-2d9c-50751520fef3"><span style="font-size: 11pt; font-family: Roboto, sans-serif; color: #000000; background-color: transparent; font-weight: bold; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;">Website and Exam platform<br /></span></span><span id="docs-internal-guid-fa6f5df6-7fff-6772-8e79-e390e9ee64f5"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;">We have identified a number of issues and need these to be fixed before we can migrate to the LMS Shika (from TechJoomla) our new exam platform. </span></span><span style="color: #000000; font-family: Arial; font-size: 14.6667px; white-space: pre-wrap; caret-color: auto;">Two of these have now been resolved, there remaining issue will be worked on with TechJoomla in the next month.</span><span style="color: #000000; font-family: Arial; font-size: 14.6667px; white-space: pre-wrap; caret-color: auto;"><br /><span id="docs-internal-guid-db13cfb0-7fff-1e7f-2d9c-50751520fef3" style="color: #333333; font-family: \'Helvetica Neue\', Helvetica, Arial, sans-serif; font-size: 14px; white-space: normal;"><span style="font-size: 11pt; font-family: Roboto, sans-serif; color: #000000; background-color: transparent; font-weight: bold; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;"></span></span></span></p>\r\n<p><span style="color: #000000; font-family: Arial; font-size: 14.6667px; white-space: pre-wrap; caret-color: auto;"><span id="docs-internal-guid-db13cfb0-7fff-1e7f-2d9c-50751520fef3" style="color: #333333; font-family: \'Helvetica Neue\', Helvetica, Arial, sans-serif; font-size: 14px; white-space: normal;"><span style="font-size: 11pt; font-family: Roboto, sans-serif; color: #000000; background-color: transparent; font-weight: bold; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;">J!4 exam(s)<br /></span></span><span id="docs-internal-guid-fa6f5df6-7fff-6772-8e79-e390e9ee64f5" style="color: #333333; font-family: \'Helvetica Neue\', Helvetica, Arial, sans-serif; font-size: 14px; white-space: normal;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;">We are now starting preparations for the J!4 exam(s). The J!4 Administrator Exam should be available approximately 6 months after the J!4 release date, this gives the JCP team enough time to complete the questions and answers based on the released version (including any initial bug fixes). This period will also give users sufficient time to use and learn J!4 and to prepare for the exam.<br /></span></span>We will be shortly looking for volunteers to help us (re)write the Administrator exams Q&amp;A\'s, if you\'re interested please contact us!</span></p>\r\n<p><strong>Next meeting:&nbsp;</strong>will be held on Septemebr 30th, 2020<strong>.</strong></p>', NULL, 1, 1006, 1, 1093, '2020-09-08 20:08:14', 0, '2020-09-08 20:24:35', 0, '0000-00-00 00:00:00'),
	(1321, 56, 0, 'Programs Department Meeting - 1st September 2020', 'programs-department-meeting-1st-september-2020', '<p id="docs-internal-guid-184bd1c7-7fff-fd44-d705-abbc6dce7c5a" dir="ltr">Participants: Jaz Parkyn, Chris Keen, Todd Woodward, Andrea Gentil, Davide Messia</p>\r\n<p dir="ltr">Apologies:&nbsp; None</p>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Dept&nbsp;</p>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Jaz reminded all team leaders to ensure that everyone has updated their consent on the Identity Portal.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Wilco provided an update from the webmasters team - all users with backend administrator access to the team’s websites will now need to be secure their logins via 2FA</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Jaz asked each of the team leaders to reach out to anyone within their teams whose activity levels have been low&nbsp;</p>\r\n</li>\r\n</ul>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">JCP&nbsp;</p>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Wilco has formally left the JCP team (although he remains in his Webmaster role): We would like to really thank Wilco for all his input and appreciate all the work he has carried out during his time in the JCP. We wish him success with his work outside the JCP!</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Confirmed Exams: September 11th 2020 -&nbsp; Zurich, Switzerland and October 16th 2020&nbsp; - Chicago, USA.&nbsp;</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Shika Exam platform status: The platform tests on 23 &amp; 24 August were aborted due to 2 showstoppers. The team will investigate further shortly.</p>\r\n</li>\r\n</ul>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">VET&nbsp;</p>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">With the big help of Chris Keen, VET have finished the first version of Vision and Onboarding documents, and have requested feedback from the rest of the team.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Laura and Benjamin are helping the JUG organiser group to create more engagement</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Davide asked about what is needed to send postcards out to volunteers. Jaz advised and will await further info from the team.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">VET has contacted Ruchika Nagarkar regarding her ‘Joomla Student Club’ document and has introduced her to the Education Outreach WG. Todd confirmed that she has been invited to the next meeting.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Laura and Chris are continuing their analysis of the help wanted page and are creating some wireframes to help better visualise possible changes.&nbsp;</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Davide is looking for someone to take over writing the Community Openings magazine article each month.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Hugh Douglas-Smith has joined the team as Legal &amp; Finance liaison</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Patrick Jackson has also joined the team</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Davide will ask the team if the meeting time should be changed to better suit the members</p>\r\n</li>\r\n</ul>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">JEO</p>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">A survey has been sent to members of the Working Group for input so that we can determine a group mission and goals. It will be compiled in time for the September meeting.</p>\r\n</li>\r\n</ul>\r\n</li>\r\n</ul>', NULL, 1, 1007, 1, 695, '2020-09-09 08:18:19', 0, '2020-09-09 08:18:58', 0, '0000-00-00 00:00:00'),
	(1322, 0, 160, 'JEDT - Cassiopeia Template 2020-08-11', 'jedt-cassiopeia-template-2020-07-28', '<h3>Attendees</h3>\r\n<ul>\r\n<li>Christiane Maier-Stadtherr</li>\r\n<li>Viviana‌ Menzel</li>\r\n<li>Helvecio‌ ‌da‌ ‌Silva‌</li>\r\n<li>Elisa‌ Foltyn</li>\r\n<li>Hans‌ Kuijpers</li>\r\n<li>Ciaran‌ Walsh</li>\r\n<li>Richard Fath</li>\r\n<li>Marco‌ ‌Dings‌</li>\r\n</ul>\r\n<h3>Agenda Items &amp; minutes</h3>\r\n<p><strong>Updates/Proposed Topics:</strong></p>\r\n<ul>\r\n<li>Deadline for Design: August 15th</li>\r\n<li>PR: September 10th</li>\r\n<li>Tasks that have been assigned</li>\r\n<li>Pending tasks</li>\r\n<li>Inform tasks that have been accomplished so far</li>\r\n</ul>\r\n<p><strong>PUBLISHED MINUTES</strong></p>\r\n<p>Tasks assigned: Tasks and asseignees are managed by members in the github project&nbsp;<br />Assigned: Design, Metismenu, sampleData, gitub management<br />Pending: GoogleFonts, Landscapes-Regions of the index.php, colour choser and others<br />Accomplished: Environmnt for development installed</p>\r\n<h3>DESIGN</h3>\r\n<p><strong>Updates/Proposed Topics:</strong></p>\r\n<ul>\r\n<li>Font combinations for the layout:&nbsp;\r\n<ul>\r\n<li>Josefin Sans Regular (general text)</li>\r\n<li>Josefin Sans Semibold (titles, subtitles and headings)</li>\r\n</ul>\r\n</li>\r\n<li>Added missing details to layout</li>\r\n<li>Comments made on mockup have been responded</li>\r\n</ul>\r\n<p><strong>PUBLISHED MINUTES</strong></p>\r\n<p>Colour contrast must be AA - Tools presented<br />No uppercase Menu - see PR from Richard</p>\r\n<h3>STRUCTURE/PERFORMANCE</h3>\r\n<p><strong>Updates/Proposed Topics:</strong></p>\r\n<ul>\r\n<li>MetisMenu Vanilla JS edition&nbsp;</li>\r\n<li>Use of Projects in GitHub</li>\r\n<li>Sample Data - CMS</li>\r\n</ul>\r\n<p><strong>PUBLISHED MINUTES</strong></p>\r\n<p>Work in Progress</p>\r\n<h3>TASKS ASSIGNED</h3>\r\n<p><strong>Updates/Proposed Topics:</strong></p>\r\n<ul>\r\n<li>Template Cassiopeia enhancement</li>\r\n<li>Sample Data</li>\r\n<li>Accessible Menu</li>\r\n<li>J4 Frontend Template Development</li>\r\n</ul>', NULL, 1, 1008, 2, 441, '2020-08-11 15:25:02', 441, '2020-09-13 15:33:48', 0, '0000-00-00 00:00:00'),
	(1323, 0, 160, 'JEDT - Cassiopeia Template 2020-08-25', 'jedt-cassiopeia-template-2020-08-25', '<h3 style="font-family: \'Open Sans\', sans-serif; color: #333333;">Attendees</h3>\r\n<ul>\r\n<li>Christiane Maier-Stadtherr</li>\r\n<li>Viviana‌ Menzel</li>\r\n<li>Helvecio‌ ‌da‌ ‌Silva‌</li>\r\n<li>Benjamin Trenkle</li>\r\n<li>Elisa‌ Foltyn</li>\r\n<li>Hans‌ Kuijpers</li>\r\n<li>Ciaran‌ Walsh</li>\r\n<li>Richard Fath</li>\r\n<li>Marco‌ ‌Dings‌</li>\r\n</ul>\r\n<h3 style="font-family: \'Open Sans\', sans-serif; color: #333333;">Agenda Items &amp; minutes</h3>\r\n<p style="overflow-wrap: break-word;"><strong>Updates/Proposed Topics:</strong></p>\r\n<ul>\r\n<li>Deadline for Design: August 15th</li>\r\n<li>PR: September 10th</li>\r\n<li>Tasks that have been assigned</li>\r\n<li>Pending tasks</li>\r\n<li>Inform tasks that have been accomplished so far</li>\r\n</ul>\r\n<h3>&nbsp;</h3>\r\n<h3>Christiane Maier-Stadtherr</h3>\r\n<ul>\r\n<li>Task(s) assigned: Watch issue tracker, Sample data, testing&nbsp;</li>\r\n<li>Pending tasks: Sample data - needs feedback and requirements&nbsp;</li>\r\n<li>ToDo: Will test again metis-menu</li>\r\n</ul>\r\n<h3>&nbsp;</h3>\r\n<h3>Viviana‌ Menzel</h3>\r\n<p><strong>Task(s) assigned:</strong></p>\r\n<ul>\r\n<li>Metismenu</li>\r\n<li>Frontend html structure changes</li>\r\n<li>Back-to-top link (directly in CMS)</li>\r\n</ul>\r\n<p><strong>Pending tasks:&nbsp;</strong></p>\r\n<ul>\r\n<li>metismenu needs more tests</li>\r\n<li>Banner module</li>\r\n<li>Work further on structure and css changes</li>\r\n</ul>\r\n<p>&nbsp;</p>\r\n<h3 style="font-family: \'Open Sans\', sans-serif; color: #333333;">Helvecio da Silva DESIGN</h3>\r\n<p style="overflow-wrap: break-word;"><strong>Updates/Proposed Topics:</strong></p>\r\n<ul>\r\n<li>HTML on Github in four versions (colors)</li>\r\n</ul>\r\n<h3 style="overflow-wrap: break-word;">Richard Fath</h3>\r\n<p style="overflow-wrap: break-word;"><b>Task(s) assigned:&nbsp;</b></p>\r\n<ul>\r\n<li style="overflow-wrap: break-word;">Creating GitHub branches for development of particular workpieces and for testing, building test packages</li>\r\n<li style="overflow-wrap: break-word;">Google Fonts https://github.com/joomla/cassiopeia/pull/18 changes and enhancements - RFC https://github.com/joomla/cassiopeia/issues/32 - Any opinions about more general naming, using subfolders for a better structure and about not dropping fronts from Web?</li>\r\n<li style="overflow-wrap: break-word;">Metismenu https://github.com/joomla/cassiopeia/pull/26 - I only manage that PR but not the task. Is it complete? Does it need more testing?</li>\r\n<li style="overflow-wrap: break-word;">Frontend Search Enhancement https://github.com/joomla/cassiopeia/pull/31 I only manage that PR for Hans. Status?</li>\r\n</ul>\r\n<p style="overflow-wrap: break-word;"><strong>Accomplishments: </strong></p>\r\n<ul>\r\n<li style="overflow-wrap: break-word;">GitHub branches and test packages done as needed, continuous ongoing work.</li>\r\n</ul>\r\n<p style="overflow-wrap: break-word;"><strong>Pending tasks: </strong></p>\r\n<ul>\r\n<li style="overflow-wrap: break-word;">Google Fonts changes and enhancements, continuous GitHub and Git tasks.</li>\r\n</ul>\r\n<h3><span id="docs-internal-guid-705366cf-7fff-1a1c-5954-b9657f84d08b"><span style="font-size: 12pt; font-family: Arial; color: #000000; background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;">MISCELLANEOUS</span></span></h3>\r\n<ul>\r\n<li><span style="font-family: \'Open Sans\', sans-serif;">VM: Project general status: 6 PRs</span></li>\r\n<li><span style="font-family: \'Open Sans\', sans-serif;">VM: Google Fonts and Metismenu ready</span></li>\r\n<li><span style="font-family: \'Open Sans\', sans-serif;">RF had a RFC&nbsp; issue that was solved (Google Fonts and GDPR)</span></li>\r\n<li><span style="font-family: \'Open Sans\', sans-serif;">HK posted a GitHub issue for HS to comment</span></li>\r\n<li><span style="font-family: \'Open Sans\', sans-serif;">RF suggested to make proposals over the next days</span></li>\r\n</ul>', NULL, 1, 1009, 1, 441, '2020-08-25 15:34:12', 0, '2020-09-13 15:43:26', 0, '0000-00-00 00:00:00'),
	(1324, 0, 160, 'JEDT - Cassiopeia Template 2020-09-01', 'jedt-cassiopeia-template-2020-09-01', '<h3 style="font-family: \'Open Sans\', sans-serif; color: #333333;">Attendees</h3>\r\n<ul>\r\n<li>Christiane Maier-Stadtherr</li>\r\n<li>Viviana‌ Menzel</li>\r\n<li>Helvecio‌ ‌da‌ ‌Silva‌</li>\r\n<li>Benjamin Trenkle</li>\r\n<li>Elisa‌ Foltyn</li>\r\n<li>Hans‌ Kuijpers</li>\r\n<li>Ciaran‌ Walsh</li>\r\n<li>Richard Fath</li>\r\n<li>Marco‌ ‌Dings‌</li>\r\n</ul>\r\n<h3>Agenda Items &amp; minutes:</h3>\r\n<ul>\r\n<li>Updates/Proposed Topics:</li>\r\n<li>Discuss basis font</li>\r\n<li>Status metis menu</li>\r\n<li>Status design</li>\r\n<li>Pending tasks</li>\r\n</ul>\r\n<h3>Christiane Maier-Stadtherr</h3>\r\n<p><strong>Task(s) assigned: </strong></p>\r\n<ul>\r\n<li>Watch issue tracker, Sample data, testing&nbsp;</li>\r\n</ul>\r\n<p><strong>Accomplishments:&nbsp;</strong></p>\r\n<ul>\r\n<li>Some changes on Sample Data, add newsflash module and dummy images</li>\r\n<li>Testing metismenu for accessibility</li>\r\n<li>Testing PR of members</li>\r\n</ul>\r\n<p><strong>Pending tasks:&nbsp;</strong></p>\r\n<ul>\r\n<li>Need the PR for horizontal boxes merged, otherwise the newsflash looks awkward #30527</li>\r\n</ul>\r\n<h3>Viviana‌ Menzel</h3>\r\n<p><strong>Task(s) assigned:</strong></p>\r\n<ul>\r\n<li>Metismenu</li>\r\n<li>Frontend html structure changes</li>\r\n<li>Back-to-top link (directly in CMS)</li>\r\n<li>Change scss variables to css variables</li>\r\n<li>Work on color switcher (presetted color palettes that can be chosen in the template)</li>\r\n</ul>\r\n<p><strong>Accomplishments:&nbsp;</strong></p>\r\n<ul>\r\n<li>Back-to-top is merged in core</li>\r\n</ul>\r\n<p><strong>Pending tasks:&nbsp;</strong></p>\r\n<ul>\r\n<li>Banner module</li>\r\n<li>Work further on structure and css changes</li>\r\n</ul>\r\n<h3>Helvecio da Silva</h3>\r\n<p><strong>Task(s) assigned:</strong></p>\r\n<p>Template design</p>\r\n<p><strong>Accomplishments:</strong></p>\r\n<p>Layout adjustments</p>\r\n<h3>Richard Fath</h3>\r\n<p><strong>Task(s) assigned:&nbsp;</strong></p>\r\n<ul>\r\n<li>Creating GitHub branches for development of particular workpieces and for testing, building test packages</li>\r\n<li>Google Fonts https://github.com/joomla/cassiopeia/pull/18 changes and enhancements - RFC https://github.com/joomla/cassiopeia/issues/32 - Any opinions about more general naming, using subfolders for a better structure and about not dropping fronts from Web?</li>\r\n<li>Metismenu https://github.com/joomla/cassiopeia/pull/26 - I only manage that PR but not the task. Is it complete? Does it need more testing?</li>\r\n<li>Frontend Search Enhancement https://github.com/joomla/cassiopeia/pull/31 I only manage that PR for Hans. Status?</li>\r\n</ul>\r\n<p><strong>Accomplishments:</strong></p>\r\n<ul>\r\n<li>GitHub branches and test packages done as needed, continuous ongoing work.</li>\r\n</ul>\r\n<p><strong>Pending tasks: </strong></p>\r\n<ul>\r\n<li>Google Fonts changes and enhancements, continuous GitHub and Git tasks.</li>\r\n</ul>\r\n<h3>MISCELLANEOUS</h3>\r\n<ul>\r\n<li>VM: Project general status: 6 PRs</li>\r\n<li>VM: Google Fonts and Metismenu ready</li>\r\n<li>RF had a RFC&nbsp; issue that was solved (Google Fonts and GDPR)</li>\r\n<li>HK posted a GitHub issue for HS to comment</li>\r\n<li>RF suggested to make proposals over the next days</li>\r\n</ul>', NULL, 1, 1010, 1, 441, '2020-09-01 15:43:40', 0, '2020-09-13 15:52:59', 0, '0000-00-00 00:00:00'),
	(1325, 0, 6, 'Forum Meeting 24/8/2020', 'forum-meeting-24-8-2020', '<p>Meeting Notes:</p>\r\n<ul>\r\n<li>Discussions about forum behavior of certain users</li>\r\n<li>Discussing locking/archiving Language forums with little or no activity</li>\r\n<li>Discussing Inactive Moderators</li>\r\n<li>Next Meeting: Monday 28th Sept 2020</li>\r\n</ul>', NULL, 1, 1011, 2, 503, '2020-08-24 17:30:21', 503, '2020-09-14 10:35:04', 0, '0000-00-00 00:00:00'),
	(1326, 0, 112, 'Volunteer Engagement Team: team Meeting - August 31st 2020', 'volunteer-engagement-team-team-meeting-august-31st', '<h1 id="docs-internal-guid-1107dcbf-7fff-b314-7381-030aea22a24c" dir="ltr" style="line-height: 1.38; margin-top: 20pt; margin-bottom: 6pt;"><span style="font-size: 20pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">VET Meeting Minutes</span></h1>\r\n<h2 dir="ltr" style="line-height: 1.38; margin-top: 18pt; margin-bottom: 6pt;"><span style="font-size: 16pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">August 31st 2020, 6pm UTC</span></h2>\r\n<p>&nbsp;</p>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Roboto,sans-serif; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Participants:</span><span style="font-size: 11pt; font-family: Roboto,sans-serif; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;"> Davide Messia, Benjamin Trenkle, David Aswani, Chris Keen, Marianela Queme, Daniel Dubois, Laura Gordon, Hugh Douglas-Smith</span></p>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Roboto,sans-serif; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Apologies:</span><span style="font-size: 11pt; font-family: Roboto,sans-serif; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;"> Luca Marzo, Niels Braczek</span></p>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Roboto,sans-serif; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Absent without notice:</span><span style="font-size: 11pt; font-family: Roboto,sans-serif; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">&nbsp; Rowan Hoskyns Abrahall, Vikas Pisal, Patience Nekesa, Marco Dings, Darek Śnieg, Justina Michallek, Brian Mitchell</span></p>\r\n<p>&nbsp;</p>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Roboto,sans-serif; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Welcome to our new liaison team members - Hugh.</span></p>\r\n<p>&nbsp;</p>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Roboto,sans-serif; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Davide started the meeting off by welcoming the new team member and shared the draft of the vision for the team created by Chris. The aim is to go for the three “R”: </span><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Recognition, Retention and Recruitment. The team was asked to read and comment on the draft to bring it to a final version.</span></p>\r\n<p>&nbsp;</p>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Roboto,sans-serif; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">An onboarding process, which is a living document and will be finalized asap needs feedback.</span></p>\r\n<p>&nbsp;</p>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Roboto,sans-serif; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Laura made an introduction of the Joomla! Student Club which is a concept from Ruchika. Going to support this concept Davide will try to bring the big player from GSoC, VET, Educational Outreach Working Group and JUG together to discuss with Ruchika.</span></p>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Roboto,sans-serif; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">This leads to a new approach by not sending new volunteers to different people in the Joomla! Universe but to contact the target person and let make them the contact (because it could be hard to contact if you’re new)</span></p>\r\n<p>&nbsp;</p>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Roboto,sans-serif; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Then Marianela took over talking about the need for new volunteers in the marketing team.</span></p>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Roboto,sans-serif; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Beside that the reconstruction of the “help wanted” page of the volunteers portal should be continued. Laura, Davide and Chris will come up with a first concept, which data are important and needed. The second step should then be the details page, like contact teams without login.</span></p>\r\n<p>&nbsp;</p>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Roboto,sans-serif; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">A second point from Marianela was the suggestion that we send out postcards to volunteers as “thank you”. She will come up with a suggestion how to do so.</span></p>\r\n<p>&nbsp;</p>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Roboto,sans-serif; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">A volunteer is needed to write the monthly Joomla! Magazine community opening article. Approx. 3-5h per month are needed. We search internally but if someone is interested, please contact </span><a href="teams/volunteer-engagement-team#contact" style="text-decoration: none;"><span style="font-size: 11pt; font-family: Roboto,sans-serif; color: #1155cc; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: underline; -webkit-text-decoration-skip: none; text-decoration-skip-ink: none; vertical-align: baseline; white-space: pre-wrap;">the VET.</span></a></p>\r\n<p>&nbsp;</p>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Roboto,sans-serif; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Patrick Jackson suggested a lot of great ideas which will be evaluated and we\'re planning to give feedback asap. So everyone is asked to read the document.</span></p>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Roboto,sans-serif; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">A big thank you to Laura for kickstarting the JUG project. An idea from Marianela was to feed the&nbsp; JUG organiser group with important topics they should bring into their JUG meeting (like new features like API). This can happen in the JUG organiser meeting.</span></p>\r\n<p>&nbsp;</p>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Roboto,sans-serif; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Members are asked to update their profile on the volunteer portal to get activated again.</span></p>\r\n<p>&nbsp;</p>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Roboto,sans-serif; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Probably a new time slot will be defined for the meetings to have more participants.</span></p>', NULL, 1, 1012, 1, 1293, '2020-09-14 13:15:27', 0, '2020-09-14 13:17:48', 0, '0000-00-00 00:00:00'),
	(1327, 0, 120, 'Privacy Compliance Team Meeting September 16, 2020', 'privacy-compliance-team-meeting-september-16-2020', '<p style="overflow-wrap: break-word;">This meeting of the Joomla! Privacy Compliance Team has been held on Glip on August 19th, 2020 at 16:30 CET.&nbsp;</p>\r\n<p style="overflow-wrap: break-word;"><strong>Participants&nbsp;</strong></p>\r\n<p style="overflow-wrap: break-word;">In attendance:&nbsp;</p>\r\n<ul>\r\n<li>Donata Kalnenaite&nbsp;</li>\r\n<li>Luca Marzo</li>\r\n<li>Sander Potjer</li>\r\n<li>Sandra Decoux&nbsp;</li>\r\n</ul>\r\n<p style="overflow-wrap: break-word;"><strong>Discussion Outline&nbsp;</strong></p>\r\n<ul>\r\n<li>The team discussed the fact that a team member needs to work with the&nbsp; Webmasters Team to help with the Cookie Control Implementation Task. Alkaios is currently busy so we are looking into making a call for volunteers that would have the skillset to help with this task.&nbsp;</li>\r\n<li>Sander Potjer is working on the task of updating the JVP for anonymizing profiles.&nbsp;</li>\r\n<li>Luca stated that he will be catching up on his tasks from our last meeting this weekend and that he will be adding a new team member to our team this week to help with the work that needs to be done as specified in our roadmap.&nbsp;</li>\r\n</ul>', NULL, 1, 1013, 1, 6279, '2020-09-16 15:07:25', 0, '2020-09-16 15:16:40', 0, '0000-00-00 00:00:00'),
	(1328, 0, 25, 'Joomla Bug Squad Team Meeting - September 18, 2020 ', 'joomla-bug-squad-team-meeting-september-18-2020', '<p id="docs-internal-guid-9fcd487e-7fff-b497-73c7-b13c52bed41d" dir="ltr" style="line-height: 1.9871999999999999; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: underline; -webkit-text-decoration-skip: none; text-decoration-skip-ink: none; vertical-align: baseline; white-space: pre-wrap;">Date:</span><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;"> September&nbsp; 18, 2020</span></p>\r\n<p dir="ltr" style="line-height: 1.9871999999999999; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: underline; -webkit-text-decoration-skip: none; text-decoration-skip-ink: none; vertical-align: baseline; white-space: pre-wrap;">Time:</span><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;"> 19:05 CEST</span></p>\r\n<p dir="ltr" style="line-height: 1.9871999999999999; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: underline; -webkit-text-decoration-skip: none; text-decoration-skip-ink: none; vertical-align: baseline; white-space: pre-wrap;">Total time of meeting:</span><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;"> 1 hour</span></p>\r\n<p dir="ltr" style="line-height: 1.9871999999999999; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Attendees:</span></p>\r\n<p dir="ltr" style="line-height: 1.9871999999999999; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Richard Fath, Nicola Galgano, Troy Hall, Christiane Maier-Stadtherr, Luca Racchetti, SharkyKZ, Jean-marie Simonet</span></p>\r\n<p>&nbsp;</p>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Discussion outline</span><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">&nbsp;&nbsp;&nbsp; </span><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">&nbsp;&nbsp;&nbsp; </span><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">&nbsp;&nbsp;&nbsp; </span></p>\r\n<p><br /><br /></p>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Current Status</span></p>\r\n<p>&nbsp;</p>\r\n<p dir="ltr" style="line-height: 1.38; text-align: justify; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">The number of open issues is stable from the last meeting, even if it is still huge, about (770). We have received a request to join the Team, and therefore given to the candidate the needed information on how to become a Team member.</span></p>\r\n<p>&nbsp;</p>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Rights and duties</span></p>\r\n<p>&nbsp;</p>\r\n<p dir="ltr" style="line-height: 1.38; text-align: justify; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Reminded the Team members to exercise their right / duty to vote for the election of Treasurer, also recalled to renew the consent for privacy, we still have 3 guests on the team members page.</span></p>\r\n<p><br /><br /></p>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Response time for pull requests marked as RTC</span></p>\r\n<p>&nbsp;</p>\r\n<p dir="ltr" style="line-height: 1.38; text-align: justify; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">We need faster answer to the pull requests that have been marked Ready To Commit, we have some dating back to april/may , we wish to lower the frustration and the consequent demotivation of the contributors, sometimes it is only needed a final decision, but it is counterproductive don\'t having a decision at all, this is an official&nbsp; request to the Maintainers Team and to the Release Leads.</span></p>\r\n<p>&nbsp;</p>\r\n<p dir="ltr" style="line-height: 1.656; text-align: justify; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Joomla 4 status</span></p>\r\n<p>&nbsp;</p>\r\n<p dir="ltr" style="line-height: 1.38; text-align: justify; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">We have had a discussion on what is the state of the art, shortly we will see some improvements from the Cassiopeia team, some concerns have been expressed about the new media manager.</span></p>\r\n<p>&nbsp;</p>\r\n<p dir="ltr" style="line-height: 1.656; text-align: justify; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Next Meeting</span></p>\r\n<p>&nbsp;</p>\r\n<p dir="ltr" style="line-height: 1.656; text-align: justify; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">The next team meeting will be: Fri, 2 October at 7:00 PM CEST</span></p>\r\n<p><br /><br /></p>', NULL, 1, 1014, 1, 526, '2020-09-19 07:57:54', 0, '2020-09-19 07:59:26', 0, '0000-00-00 00:00:00'),
	(1329, 0, 9, 'September 2020', 'september-2020', '<ul>\r\n<li>Stats August: 12 sites submitted -&gt; 3 approved + 3 rejected + 6 corrections requested</li>\r\n<li>Stats July: 28 sites submitted -&gt; 17 approved + 5 rejected + 6 corrections requested</li>\r\n<li>Stats June: 14 sites submitted -&gt; 7 approved + 6 rejected + 1 corrections requested</li>\r\n<li>Disk space issue: Clean up Akeeba backups (no more Sandra to manage this)</li>\r\n<li>Specific User requests processed</li>\r\n<li>Clean up user database by deleting accounts not connected since 2015, updates, cleaned Joomla ACL tree</li>\r\n<li>About the tool&nbsp;<a href="https://github.com/SIWECOS/Version-Scanner" target="_blank" rel="noopener">https://github.com/SIWECOS/Version-Scanner</a>&nbsp;which required Docker, we\'re working on the extraction of the app from the container.&nbsp;</li>\r\n<li>Working on implementation of the new rule to accept only httpS site</li>\r\n<li>Following New Cookie banner implementation (to respect GDPR) (started 1 year ago)</li>\r\n<li>Enable 2 factor authentification for registered users -&gt; planned</li>\r\n<li>Following SSO topic</li>\r\n</ul>', NULL, 1, 1015, 1, 657, '2020-09-21 16:43:17', 0, '2020-09-21 16:55:41', 0, '0000-00-00 00:00:00'),
	(1330, 0, 160, 'JEDT Cassiopeia Template - 2020-09-22', 'jedt-frontend-template-enhancement-2020-09-22', '<h2>Attendees:<strong><br /></strong></h2>\r\n<p>&nbsp;&nbsp;&nbsp; Christiane Maier-Stadtherr,&nbsp;&nbsp;&nbsp;&nbsp; Viviana‌ Menzel,&nbsp;&nbsp;&nbsp; Benjamin Trenkle,&nbsp;&nbsp;&nbsp; Hans‌ Kuijpers (part time),&nbsp;&nbsp;&nbsp; Richard Fath&nbsp;&nbsp;&nbsp; Llewellyn van der Merwe</p>\r\n<h2>Absent:</h2>\r\n<p>&nbsp;&nbsp;&nbsp; Elisa‌ Foltyn,&nbsp;&nbsp;&nbsp; Helvecio‌ ‌da‌ ‌Silva‌,&nbsp;&nbsp;&nbsp; Ciaran‌ Walsh,&nbsp;&nbsp;&nbsp; Marco‌ ‌Dings‌</p>\r\n<h2>Resume on recent work</h2>\r\n<h3>Decisions made:</h3>\r\n<p><strong>Template Design Decision:</strong> no new design, improvement of&nbsp; Cassiopeia<br /><strong>Architectural decision</strong>: Based on Bootstrap v4 in core layouts. No extra jQuery, no new dependencies. Make icons flexible<br /><strong>Local hosted fonts (GDPR)</strong> Roboto local font delivered by core / Roboto + Noto Sans from web / Fira Sans from web<br /><strong>Dropdown Menu:</strong> It was decided to use Metismenu JS, no jQuery,&nbsp; accessible, as override, 2 sublevels maximum.</p>\r\n<h3>Results:</h3>\r\n<ul>\r\n<li>Evaluated different Designs with different colour schemes</li>\r\n<li>Clean HTML and scss structure</li>\r\n<li>Alternative layout for mod_custom for header banner: background image + text overlay</li>\r\n<li>PRs in CMs for horizontal cards in newsflash , placement of the to-top-button</li>\r\n<li>Menu with submenus: metismenu as alternative layout</li>\r\n<li>Improved accessibility of the dropdown toggle</li>\r\n<li>Added the possibility of a link plus button in one row</li>\r\n<li>Local hosted fonts for GDPR with parameters in template styles and cassiopeia/variables font changes</li>\r\n<li>Multi-columns for blog layout was already made by Ciaran Walsh, Sample data show different use cases</li>\r\n<li>Selectable colour sets for the user&nbsp; -&gt; color switcher in template, more colours can be added</li>\r\n<li>Many&nbsp; accessibility issues have been fixed&nbsp; - mostly core layouts</li>\r\n<li>Complete rewrite of the output of search results. Looks more like Google search results now.</li>\r\n<li>Rebuild the search form</li>\r\n<li>Added number of results to the search results page</li>\r\n</ul>\r\n<p>&nbsp;</p>\r\n<h2>Next Goal:</h2>\r\n<p>A good PR, which can be merged into the next beta release (5) – hopefully October 30th,<br />This PR must resolve known open issues for Cassiopeia.<br /><br />This PR will contain all PRs which we have made and the whole has to be described carefully so enabling documentation and test.<br /><br />It cannot resolve all issues of the frontend in the layouts / cms, there are many issues in joomla-cms which will be solved by this PR. We see the danger that some work is made in parallel.<br /><br />We hope that PBF2020 will activate many testers who check all views and modules with different settings.</p>\r\n<h2>Some of the topics where we are working on</h2>\r\n<p><strong>Template Design:</strong></p>\r\n<p>As we don’t make big changes in cassiopeia, which small improvements can we add? We could add template overrides, just as demo what is possible with overrides.</p>\r\n<p>Small things would make a difference:</p>\r\n<ul>\r\n<li>Readmore Buttons</li>\r\n<li>Next-Previous arrows (svg ?)</li>\r\n<li>Submenus in metismenu</li>\r\n</ul>\r\n<p><strong>MetisMenu - documentation</strong></p>\r\n<p>MenuItems must have visible edit Buttons if the user has the right for that</p>\r\n<p><strong>Chromes</strong></p>\r\n<p>Improve names and layouts, cleaup, Tet RTL</p>\r\n<p><strong>Documentation</strong></p>\r\n<p><strong>Error Page and Offline Page</strong></p>\r\n<p><strong>Sample data</strong> - use of images to present cassiopeia in the frontend</p>', NULL, 1, 1016, 8, 2390, '2020-09-22 15:00:02', 2390, '2020-09-23 08:23:39', 0, '0000-00-00 00:00:00'),
	(1331, 54, 0, 'September 21, 2020 Events Department meeting report', 'september-21-2020-events-department-meeting-report', '<p id="docs-internal-guid-91d66b41-7fff-2991-f9d6-ca530941cbb6" dir="ltr">Participants:&nbsp; Djamel Kherbi, Carlos Camara Mora, David aswani&nbsp;</p>\r\n<p dir="ltr">Apologies: &nbsp; Brian Ronnow</p>\r\n<p dir="ltr">No Show : Ahmad Moussa</p>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;">Total time of the meeting :&nbsp; 45 minutes <span style="font-size: 11pt; font-family: Roboto,sans-serif; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;"><br /></span></p>\r\n<p>&nbsp;</p>\r\n<p id="docs-internal-guid-77111eb8-7fff-8415-779b-6f89946f0cd5" dir="ltr">The Events department held its monthly&nbsp; meeting where the following items were discussed.</p>\r\n<p dir="ltr">Meeting took place on Google meet.</p>\r\n<p id="docs-internal-guid-80ae71c3-7fff-f002-60e7-c08a336cd785" dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><strong>Events Department</strong> <span style="font-size: 11pt; font-family: Roboto,sans-serif; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;"><br /></span></p>\r\n<p id="docs-internal-guid-56360ec6-7fff-3375-8312-0dad2b8bb2b5" dir="ltr">Welcome to David Aswani the newly elected events DC, the handover with David is already in good progress, he was introduced to all the tasks belonging to the events dept, he also has access to the shared folders of Events, David will start his term on September 29.</p>\r\n<p id="docs-internal-guid-546a76c6-7fff-7cb4-98f9-df1c364b9b3c" dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><strong>JUG Team</strong> <span style="font-size: 11pt; font-family: Roboto,sans-serif; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;"><br /></span></p>\r\n<p id="docs-internal-guid-81b98845-7fff-b84a-317e-8dca22d6b5ba" dir="ltr">The redo of the jug directory is in good progress,&nbsp; a new layout has been implemented and most of the tasks have been achieved, I would like to say thank you to&nbsp; Jaz Parkyn, Andrea Gentil and Wilco Alsemgeest for their collaboration.</p>\r\n<p><strong>Joomla! Events Team</strong></p>\r\n<p>Any particular update from the Joomla! Events team, events submitted and opened tickets are handled in a timely manner.</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>', NULL, 1, 1017, 2, 468, '2020-09-23 12:47:03', 468, '2020-09-23 12:59:07', 0, '0000-00-00 00:00:00'),
	(1332, 55, 0, '[ September 21st, 2020 ] Operations Department Meeting Minutes', 'september-21st-2020-operations-department-meeting', '<p style="margin-bottom: 0px; text-align: center; font-stretch: normal; font-size: 22.4px; line-height: normal; font-family: \'Helvetica Neue\'; color: #1d1d1d;"><b>Operations Department Meeting September 21, 2020 - 18:00 - 18:30 UTC</b></p>\r\n<p style="margin-bottom: 0px; font-stretch: normal; line-height: normal; font-family: \'Helvetica Neue\'; color: #1d1d1d; min-height: 16px;">&nbsp;</p>\r\n<p style="margin-bottom: 0px; font-stretch: normal; line-height: normal; font-family: \'Helvetica Neue\'; color: #115db2;"><span style="color: #1d1d1d;"><b>Participants</b>: <a href="joomlers/1099-andrea-gentil"><span style="text-decoration: underline; color: #115db2;">Andrea Gentil</span></a>, <a href="joomlers/337-jaz-parkyn"><span style="text-decoration: underline; color: #115db2;">Jaz Parkyn</span></a>, <a href="joomlers/69-olaf-offick"><span style="text-decoration: underline; color: #115db2;">Olaf Offick</span></a>, <a href="joomlers/116-radek-suski"><span style="text-decoration: underline; color: #115db2;">Radek Suski</span></a>, <a href="joomlers/155-roland-dalmulder"><span style="text-decoration: underline; color: #115db2;">Roland Dalmulder</span></a>, <a href="joomlers/60-sander-potjer"><span style="text-decoration: underline; color: #115db2;">Sander Potjer</span></a></span></p>\r\n<p style="margin-bottom: 0px; font-stretch: normal; line-height: normal; font-family: \'Helvetica Neue\'; color: #1d1d1d; min-height: 16px;">&nbsp;</p>\r\n<p style="margin-bottom: 0px; font-stretch: normal; line-height: normal; font-family: \'Helvetica Neue\'; color: #115db2;"><span style="color: #1d1d1d;"><b>Apologies</b>: <a href="joomlers/273-anibal-sanchez"><span style="text-decoration: underline; color: #115db2;">Anibal Sanchez</span></a>, <a href="joomlers/125-sigrid-suski"><span style="text-decoration: underline; color: #115db2;">Sigrid Suski</span></a>, <a href="joomlers/300-sebastien-lapoux"><span style="text-decoration: underline; color: #115db2;">Sebastien Lapoux</span></a></span>, <a href="joomlers/1815-wilco-alsemgeest"><span style="text-decoration: underline;">Wilco Alsemgeest</span></a></p>\r\n<p style="margin-bottom: 0px; font-stretch: normal; line-height: normal; font-family: \'Helvetica Neue\'; color: #1d1d1d; min-height: 16px;">&nbsp;</p>\r\n<p style="margin-bottom: 0px; font-stretch: normal; line-height: normal; font-family: \'Helvetica Neue\'; color: #1d1d1d;"><b>Proxies</b>: Radek Suski for Sigrid Suski, Jaz Parkyn for Anibal Sanchez</p>\r\n<p style="margin-bottom: 0px; font-stretch: normal; line-height: normal; font-family: \'Helvetica Neue\'; color: #1d1d1d; min-height: 16px;">&nbsp;</p>\r\n<p style="margin-bottom: 0px; font-stretch: normal; font-size: 18.4px; line-height: normal; font-family: \'Helvetica Neue\'; color: #1d1d1d;"><b>Department Coordinator:</b></p>\r\n<p style="margin-bottom: 0px; font-stretch: normal; font-size: 18.4px; line-height: normal; font-family: \'Helvetica Neue\'; color: #1d1d1d; min-height: 23px;">&nbsp;</p>\r\n<ol>\r\n<li style="margin: 0px; font-stretch: normal; line-height: normal; font-family: \'Helvetica Neue\'; color: #1d1d1d;">Radek reminded everyone that he has been elected as treasurer and ensured help to the successor to warrant smooth transition.</li>\r\n</ol>\r\n<p style="margin-bottom: 0px; font-stretch: normal; line-height: normal; font-family: \'Helvetica Neue\'; color: #1d1d1d; min-height: 16px;">&nbsp;</p>\r\n<p style="margin-bottom: 0px; font-stretch: normal; line-height: normal; font-family: \'Helvetica Neue\'; color: #1d1d1d; min-height: 16px;">&nbsp;</p>\r\n<p style="margin-bottom: 0px; font-stretch: normal; font-size: 18.4px; line-height: normal; font-family: \'Helvetica Neue\'; color: #1d1d1d;"><b>Community Magazine Team</b></p>\r\n<p style="margin-bottom: 0px; font-stretch: normal; font-size: 18.4px; line-height: normal; font-family: \'Helvetica Neue\'; color: #1d1d1d; min-height: 23px;">&nbsp;</p>\r\n<ol>\r\n<li style="margin: 0px; font-stretch: normal; line-height: normal; font-family: \'Helvetica Neue\'; color: #1d1d1d;">September issue published</li>\r\n<li style="margin: 0px; font-stretch: normal; line-height: normal; font-family: \'Helvetica Neue\'; color: #1d1d1d;">The latest team meeting was on August 27. The meeting report can be found at <a href="teams/joomla-community-magazine-team/reports/1314-jcm-meeting-report-august-27th-2020"><span style="color: #115db2;">Volunteers Portal</span></a></li>\r\n<li style="margin: 0px; font-stretch: normal; line-height: normal; font-family: \'Helvetica Neue\'; color: #1d1d1d;">The team is working on the next issue</li>\r\n</ol>\r\n<p style="margin-bottom: 0px; font-stretch: normal; line-height: normal; font-family: \'Helvetica Neue\'; color: #1d1d1d; min-height: 16px;">&nbsp;</p>\r\n<p style="margin-bottom: 0px; font-stretch: normal; line-height: normal; font-family: \'Helvetica Neue\'; color: #1d1d1d; min-height: 16px;">&nbsp;</p>\r\n<p style="margin-bottom: 0px; font-stretch: normal; font-size: 18.4px; line-height: normal; font-family: \'Helvetica Neue\'; color: #1d1d1d;"><b>Extensions Directory Team</b></p>\r\n<p style="margin-bottom: 0px; font-stretch: normal; font-size: 18.4px; line-height: normal; font-family: \'Helvetica Neue\'; color: #1d1d1d; min-height: 23px;">&nbsp;</p>\r\n<ol>\r\n<li style="margin: 0px; font-stretch: normal; line-height: normal; font-family: \'Helvetica Neue\'; color: #1d1d1d;">The latest meeting was on August 28th. The meeting report can be found at <a href="teams/extensions-directory-team/reports/1319-jed-team-meeting-august-28th-2020"><span style="color: #115db2;">Volunteers Portal</span></a>. Next meeting is planned for this Friday</li>\r\n<li style="margin: 0px; font-stretch: normal; line-height: normal; font-family: \'Helvetica Neue\'; color: #1d1d1d;">JED is preparing to clean all inactive users among other reason to comply with the GDPR</li>\r\n</ol>\r\n<p style="margin-bottom: 0px; font-stretch: normal; line-height: normal; font-family: \'Helvetica Neue\'; color: #1d1d1d; min-height: 16px;">&nbsp;</p>\r\n<p style="margin-bottom: 0px; font-stretch: normal; font-size: 18.4px; line-height: normal; font-family: \'Helvetica Neue\'; color: #1d1d1d;"><b>Forum Team</b></p>\r\n<p style="margin-bottom: 0px; font-stretch: normal; font-size: 18.4px; line-height: normal; font-family: \'Helvetica Neue\'; color: #1d1d1d; min-height: 23px;">&nbsp;</p>\r\n<ol>\r\n<li style="margin: 0px; font-stretch: normal; line-height: normal; font-family: \'Helvetica Neue\'; color: #1d1d1d;">The team is successfully managed to improve increasing bounces from the forum website</li>\r\n<li style="margin: 0px; font-stretch: normal; line-height: normal; font-family: \'Helvetica Neue\'; color: #1d1d1d;">Also the possible conflict with particular user (as mentioned in previous meeting) has been solved</li>\r\n</ol>\r\n<p style="margin-bottom: 0px; font-stretch: normal; line-height: normal; font-family: \'Helvetica Neue\'; color: #1d1d1d; min-height: 16px;">&nbsp;&nbsp;</p>\r\n<p style="margin-bottom: 0px; font-stretch: normal; line-height: normal; font-family: \'Helvetica Neue\'; color: #1d1d1d; min-height: 16px;">&nbsp;</p>\r\n<p style="margin-bottom: 0px; font-stretch: normal; font-size: 18.4px; line-height: normal; font-family: \'Helvetica Neue\'; color: #1d1d1d;"><b>Template Directory Team</b></p>\r\n<p style="margin-bottom: 0px; font-stretch: normal; font-size: 18.4px; line-height: normal; font-family: \'Helvetica Neue\'; color: #1d1d1d; min-height: 23px;">&nbsp;</p>\r\n<ol>\r\n<li style="margin: 0px; font-stretch: normal; line-height: normal; font-family: \'Helvetica Neue\'; color: #1d1d1d;">Currently no activity due to unclear situation. Awaiting further direction.</li>\r\n</ol>\r\n<p style="margin-bottom: 0px; font-stretch: normal; line-height: normal; font-family: \'Helvetica Neue\'; color: #1d1d1d; min-height: 16px;">&nbsp;</p>\r\n<p style="margin-bottom: 0px; font-stretch: normal; line-height: normal; font-family: \'Helvetica Neue\'; color: #1d1d1d; min-height: 16px;">&nbsp;</p>\r\n<p style="margin-bottom: 0px; font-stretch: normal; font-size: 18.4px; line-height: normal; font-family: \'Helvetica Neue\'; color: #1d1d1d;"><b>Volunteers Portal Team&nbsp;</b></p>\r\n<p style="margin-bottom: 0px; font-stretch: normal; font-size: 18.4px; line-height: normal; font-family: \'Helvetica Neue\'; color: #1d1d1d; min-height: 23px;">&nbsp;</p>\r\n<ol>\r\n<li style="margin: 0px; font-stretch: normal; line-height: normal; font-family: \'Helvetica Neue\'; color: #1d1d1d;">The team is working on better solution for displaying users (members) who didn’t gave a consent for displaying public data.</li>\r\n<li style="margin: 0px; font-stretch: normal; line-height: normal; font-family: \'Helvetica Neue\'; color: #1d1d1d;">Olaf noted issues with removing inactive members if he doesn’t know which account he has to remove. Sander explained that those data is anonymised and nobody actually shall nor know the real data. Is had been advised to simply remind all team’s members to update their profiles.</li>\r\n<li style="margin: 0px; font-stretch: normal; line-height: normal; font-family: \'Helvetica Neue\'; color: #1d1d1d;">Sander has been reminded about requirements for reporting. Sender mentioned that it would limit his volunteer time to work on other issues. He suggested to discuss it with the board.</li>\r\n</ol>\r\n<p style="margin-bottom: 0px; font-stretch: normal; line-height: normal; font-family: \'Helvetica Neue\'; color: #1d1d1d; min-height: 16px;">&nbsp;</p>\r\n<p style="margin-bottom: 0px; font-stretch: normal; font-size: 18.4px; line-height: normal; font-family: \'Helvetica Neue\'; color: #1d1d1d;"><b>Webmasters Team</b></p>\r\n<ol>\r\n<li style="margin: 0px; font-stretch: normal; line-height: normal; font-family: \'Helvetica Neue\'; color: #1d1d1d;">Reports published after last department meeting:</li>\r\n<li style="margin: 0px; font-stretch: normal; line-height: normal; font-family: \'Helvetica Neue\'; color: #1d1d1d;">Working on UpTimeRobot, they gave us a free pro account. It’s running next to Pingdom for which we need to pay. Anibal is helping with a header value test to also get warnings for a website which loads, but displays a blank page.&nbsp;</li>\r\n<li style="margin: 0px; font-stretch: normal; line-height: normal; font-family: \'Helvetica Neue\'; color: #1d1d1d;">Motion will be sent for the “Identity Access Management” policy which is required, according to the Joomla Property Management policy Article 8 <a href="https://www.opensourcematters.org/organisation/directors/policies/297-joomla-property-management-policy.html"><span style="text-decoration: underline;">https://www.opensourcematters.org/organisation/directors/policies/297-joomla-property-management-policy.html</span></a></li>\r\n<li style="margin: 0px; font-stretch: normal; line-height: normal; font-family: \'Helvetica Neue\'; color: #1d1d1d;">The “Privacy &amp; Security Settings” policy will be finalised after point 2</li>\r\n<li style="margin: 0px; font-stretch: normal; line-height: normal; font-family: \'Helvetica Neue\'; color: #1d1d1d;">Supporting Hugh together with Jaz, to define, configure and test the Advertisement structure based on decisions by the board.</li>\r\n<li style="margin: 0px; font-stretch: normal; line-height: normal; font-family: \'Helvetica Neue\'; color: #1d1d1d;">Working with Hugh, Luca and Jaz to define and build a new JRD structure within CJO, which has the working name “Service Providers Directory”, will get monetisation options and can provide more listed companies than on the current website.&nbsp;</li>\r\n</ol>\r\n<p style="margin-bottom: 0px; font-stretch: normal; line-height: normal; font-family: \'Helvetica Neue\'; color: #1d1d1d; min-height: 16px;">&nbsp;</p>\r\n<p style="margin-bottom: 0px; font-stretch: normal; line-height: normal; font-family: \'Helvetica Neue\'; color: #1d1d1d;">On behalf of the department I would like to thank all participants for their time and efforts and participation in our meeting.</p>', NULL, 1, 1018, 3, 449, '2020-09-24 13:18:35', 449, '2020-10-20 09:27:24', 0, '0000-00-00 00:00:00'),
	(1333, 58, 0, 'OSM, Inc. - Full Board Meeting - September 10, 2020', 'osm-inc-full-board-meeting-september-10-2020', '<h2 id="docs-internal-guid-009f0b1b-7fff-9f89-3b41-285cc498f7e3" dir="ltr">Open Source Matters, Inc.<br />Full Board of Directors and Officers Meeting</h2>\r\n<p dir="ltr">Date: September 10, 2020<br />Time: 17:00 UTC<br />Total time of meeting: 1 hour 40 minutes</p>\r\n<h4 dir="ltr">Attendees:</h4>\r\n<p dir="ltr">Brian Mitchell (President), Elisa Foltyn (Vice President), Luca Marzo (Secretary), Marco Dings (Director), Hugh Douglas-Smith (Director), Djamel Kherbi (Director), Jaz Parkyn (Director), Radek Suski (Director).</p>\r\n<h4 dir="ltr">Apologies:</h4>\r\n<p dir="ltr">Eric Lamy (Director)</p>\r\n<h4 dir="ltr">Proxies:</h4>\r\n<p dir="ltr">Radek Suski for Eric Lamy.</p>\r\n<h4 dir="ltr">Invited Guests:</h4>\r\n<p dir="ltr">Justine Ayebale Abunyanga (Elected Vice President), David Opati Aswani (Elected Director), Marianela Quemé Alegre (Elected Director).</p>\r\n<h2 dir="ltr">Discussion outline</h2>\r\n<h3 dir="ltr">President</h3>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Bookwork for 2019/2020 tax filing is nearly complete. It should be ready by the September 24 OSM board meeting. OSM bookkeeper will have detailed expense reports available for review by the board. This will include information about what exactly was expensed to each department.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Tax filing for 2019/2020 is due September 15. Brian asked our tax accountant to file an extension (this is common practice) to accommodate onboarding our new bookkeeper, to allow for board review of the filing prior to submission, and to allow for the new treasurer to review the filings.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Brian had a very good conversation with Carlos Camara Mora last week re: a11y for j4. He recommended that we have one of his team members write an article discussing the focus on and improvements of Accessibility for Joomla 4.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Brian will work to reignite the Advisory Board, engaging with the current members.</p>\r\n</li>\r\n</ul>\r\n<h3 dir="ltr">Treasurer</h3>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">&nbsp;Brian shared the bank balances as of today.</p>\r\n</li>\r\n</ul>\r\n<h3 dir="ltr">Production Department</h3>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Positive feedback and growing participation following the publication of the Joomla 4 minor releases plan.&nbsp;</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">The Production Department is looking forward to collaborating with VET to find out new volunteers for some specific teams.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Basic migration for continuous integration towards Drone completed, thanks to Hannes Papenberg.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Discussions are ongoing with marketing on the Joomla focus for the future.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Accessibility team <a href="https://magazine.joomla.org/all-issues/august-2020/accessible-resources-for-joomla-3-x%20t">article published</a> in the magazine.</p>\r\n</li>\r\n</ul>\r\n<h3 dir="ltr">Legal &amp; Finance Department</h3>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Hugh drafted a proposal for a Hosting page/directory sponsorship. The Board reviewed the proposal. Further discussion and decisions on that will be taken at the next meeting.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Hugh has been in touch with Carrie Megginson, she is aware of the resolution at the last board meeting and is no longer appointed as closing sponsorship agreements. She wishes us well.</p>\r\n</li>\r\n</ul>\r\n<h3 dir="ltr">Marketing &amp; Communications Department</h3>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Marianela reported that there was low progress on the Joomla 4 landing page. Marianela started working on a document to help define this within marketing and will seek help from Production and Operations to get that executed.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Teams are working on campaigns that will promote Joomla 4 and Joomla in our social media channels.</p>\r\n</li>\r\n</ul>\r\n<h3 dir="ltr">Events Department</h3>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">The development of the new Joomla! Users Group directory has started with the help of Wilco, Jaz and Andrea, work is under good progress.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">First handover meeting with David Aswani elected events DC was held on September 3th, David has access to all the shared folders belonging to the events department, David has been introduced to all the tasks which are under progress under events as well the responsibilities of the Events DC.</p>\r\n</li>\r\n</ul>\r\n<h3 dir="ltr">Operations Department</h3>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">No major updates since last meeting.</p>\r\n</li>\r\n</ul>\r\n<h3 dir="ltr">Programs Department</h3>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">[<strong>Certification Team</strong>] Jeroen has fixed a couple of showstopper bugs in the question management system, meaning that questions can now be added into Shika.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">[<strong>Volunteer Engagement</strong>] have been working on finalising their mission and goals.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Jaz has been working on a document to define the responsibilities of Department Coordinators, thank you to those who contributed ideas.&nbsp;</p>\r\n</li>\r\n</ul>\r\n<h3 dir="ltr">Vice President</h3>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Elisa welcomed Justine and started the handover process to get her ready for the role.</p>\r\n</li>\r\n</ul>\r\n<h3 dir="ltr">Secretary</h3>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">[<strong>RFPs</strong>] The deadline for the Shop and Conflict Handling RFPs has been extended to September 12, 2020.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">[<strong>Elections</strong>] The elections of Vice President, Marketing DC and Events DC have been completed. Justine Ayebale Abunyanga has been elected as Vice President, Marianela Queme Alegre has been elected as Marketing &amp; Communications DC, David Opati Aswani has been elected as Events DC. The election of the Treasurer is still ongoing and has 2 candidates.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">[<strong>Endorsements</strong>] OSM Board Members should not endorse professionals or companies on behalf of the Project. This can cause unfair reviews and feelings within the community.&nbsp;</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">[<strong>Foundation</strong>] There is the need to submit the Annual Report. We would need to have a meeting in which we operate the election of the board of the Foundation and proceed with the input of the information to the incorporation service.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">[<strong>AdSense</strong>] The access to the OSM AdSense account has been cleaned up. Legal &amp; Finance DC has access. Treasurer and Secretary are copied on service, administrative and payment email notifications.</p>\r\n</li>\r\n</ul>\r\n<h3 dir="ltr">Motions taken during this meeting</h3>\r\n<p dir="ltr"><strong>#2020/119 - Approve the Department Coordinator Responsibilities Policy and adopt it as official corporate Policy<br /></strong>Proposed by Jaz Parkyn, seconded by Luca Marzo.<br /><em>The motion passed with majority consent and 1 against. Brian Mitchell voted against the motion.</em></p>\r\n<p dir="ltr"><strong>#2020/120 - The Secretary proposes to adjourn the meeting.</strong><br />Proposed by Luca Marzo, seconded by Hugh Douglas-Smith.<br /><em>The motion passed with unanimous consent.</em></p>\r\n<h3 dir="ltr">Motions taken offline before this meeting</h3>\r\n<p dir="ltr"><strong>#2020/118 - Approve and publish the Board Meeting Minutes.</strong><br />Proposed by Luca Marzo, seconded by Jaz Parkyn.<br /><em>The motion passed with unanimous consent.</em></p>', NULL, 1, 1019, 1, 669, '2020-09-30 07:45:58', 0, '2020-09-30 07:47:03', 0, '0000-00-00 00:00:00'),
	(1334, 0, 120, 'Privacy Compliance Team Meeting September 30, 2020', 'privacy-compliance-team-meeting-september-30-2020', '<p style="overflow-wrap: break-word;">This meeting of the Joomla! Privacy Compliance Team has been held on Glip on September 30, 2020 at 16:30 CET.&nbsp;</p>\r\n<p style="overflow-wrap: break-word;"><strong>Participants&nbsp;</strong></p>\r\n<p style="overflow-wrap: break-word;">In attendance:&nbsp;</p>\r\n<ul>\r\n<li>Donata Kalnenaite&nbsp;</li>\r\n<li>Luca Marzo</li>\r\n<li>Roland Dalmulder&nbsp;</li>\r\n<li>Sander Potjer</li>\r\n</ul>\r\n<p style="overflow-wrap: break-word;"><strong>Discussion Outline&nbsp;</strong></p>\r\n<ul>\r\n<li>Luca has added a new volunteer to the Privacy Compliance Team and he will be joining us at our next meeting;&nbsp;</li>\r\n<li>Donata asked the team members to review the articles that still need to be written for the Joomla Community Magazine and encouraged them to write some articles so that we can finish that task;&nbsp;</li>\r\n<li>Sander is almost done with his JVP task;&nbsp;</li>\r\n<li>Donata shared the call for a team volunteer who can help with the cookie script task with Davide, who will add it into the next Joomla-wide call for volunteers;&nbsp;</li>\r\n<li>The team discussed what should happen to the team members who did not renew their consents. Luca will be performing an analysis of the bylaws and, once we know what the bylaws require and what the consequences of not renewing consent are, we will determine the best way to proceed.&nbsp;</li>\r\n</ul>', NULL, 1, 1020, 1, 6279, '2020-09-30 15:22:44', 0, '2020-09-30 15:27:39', 0, '0000-00-00 00:00:00'),
	(1335, 58, 0, 'OSM, Inc. - Full Board Meeting - September 24, 2020', 'osm-inc-full-board-meeting-september-24-2020', '<h2 id="docs-internal-guid-5c65ad84-7fff-eafd-1d6d-f58d0776951b" dir="ltr">Open Source Matters, Inc.<br />Full Board of Directors and Officers Meeting</h2>\r\n<p dir="ltr">Date: September 24, 2020<br />Time: 17:00 UTC<br />Total time of meeting: 1 hour 45 minutes</p>\r\n<h4 dir="ltr">Attendees:</h4>\r\n<p dir="ltr">Brian Mitchell (President), Elisa Foltyn (Vice President), Luca Marzo (Secretary), Marco Dings (Director), Hugh Douglas-Smith (Director), Djamel Kherbi (Director), Jaz Parkyn (Director), Radek Suski (Director), Eric Lamy (Director).</p>\r\n<h4 dir="ltr">Apologies:</h4>\r\n<p dir="ltr">-</p>\r\n<h4 dir="ltr">Proxies:</h4>\r\n<p dir="ltr">-</p>\r\n<h4 dir="ltr">Invited guests:</h4>\r\n<p dir="ltr">Justine Ayebale Abunyanga (Elected Officer), Marianela Queme Alegre (Elected Director), David Opati Aswani (Elected Director).</p>\r\n<h2 dir="ltr">Discussion outline</h2>\r\n<h3 dir="ltr">President</h3>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Brian welcomed the new Board Members and provided them updates on items and actions that are in progress.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">2019-2020 Books will be available for full board review at the October 8 board meeting. This is in preparation for tax filing for the previous fiscal year.</p>\r\n</li>\r\n</ul>\r\n<h3 dir="ltr">Treasurer</h3>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Brian provided the bank balances as of today.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Brian to take care of the onboarding of Radek as new Treasurer.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Bank statements are available to the Board for review.</p>\r\n</li>\r\n</ul>\r\n<h3 dir="ltr">Production Department</h3>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation"><a href="https://www.joomla.org/announcements/release-news/5822-joomla-4-0-0-beta4-and-joomla-3-10-alpha2.html">Joomla 4 Beta 4 and Joomla 3.10 Alpha 2 were released</a></p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Llewellyn van der Merwe stepped up to cover some responsibilities of the Department Coordinator during the past week given the unavailability of the DC.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Llewellyn proposed some ideas to improve and support the Volunteers Engagement Team.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">The Board discussed the removal of Google Analytics / Google Tag Manager from some websites managed from the Production Department, specifically: issues.joomla.org, help.joomla.org and framework.joomla.org.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">The Production Department is holding a new vote on the creation of a “backports repository”.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Marianela to have a meeting with Marco to discuss the Joomla 4 landing page.</p>\r\n</li>\r\n</ul>\r\n<h3 dir="ltr">Legal &amp; Finance Department</h3>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Hugh provided updates on his proposal to have an Hosting Provider Page/Directory. After discussing with Jaz and Wilco, they came up with the idea to merge this with Joomla Resource Directory and have a unique “Service Provider Directory” that would also include paid featured listings.</p>\r\n</li>\r\n</ul>\r\n<h3 dir="ltr">Marketing &amp; Communications Department</h3>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">The Instagram contest is in progress.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Ongoing planning for a new LinkedIn campaign.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">All the social media campaigns are being monitored and measured by the Team.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Marianela is working with a copywriter on a new brochure for Joomla.</p>\r\n</li>\r\n</ul>\r\n<h3 dir="ltr">Events Department</h3>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">The Events Department leadership meeting was held on <a href="departments/events/reports/1331-september-21-2020-events-department-meeting-report">September 21</a>.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Handover process with David has been completed.</p>\r\n</li>\r\n</ul>\r\n<h3 dir="ltr">Operations Department</h3>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Last department meeting was held on Monday (21st September).<a href="departments/operations/reports/1332-september-21st-2020-operations-department-meeting"> Report</a> has been published today</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation"><strong>Community Magazine Team</strong></p>\r\n</li>\r\n</ul>\r\n<ul>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">September issue published</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">The latest team meeting was on August 27. The meeting report can be found at<a href="teams/joomla-community-magazine-team/reports/1314-jcm-meeting-report-august-27th-2020"> Volunteers Portal</a></p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">The team is working on the next issue</p>\r\n</li>\r\n</ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation"><strong>Extensions Directory Team</strong></p>\r\n</li>\r\n</ul>\r\n<ul>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">The latest meeting was on August 28th. The meeting report can be found at<a href="teams/extensions-directory-team/reports/1319-jed-team-meeting-august-28th-2020"> Volunteers Portal</a>. Next meeting is planned for this Friday.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">JED is preparing to clean all inactive users among other reason to comply with the GDPR</p>\r\n</li>\r\n</ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation"><strong>Forum Team</strong></p>\r\n</li>\r\n</ul>\r\n<ul>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">The team successfully managed to improve increasing bounces from the forum website</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Also the possible conflict with particular user (as mentioned in previous meeting) has been solved</p>\r\n</li>\r\n</ul>\r\n</ul>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation"><strong>Volunteers Portal Team&nbsp;</strong></p>\r\n</li>\r\n</ul>\r\n<ul>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">The team is working on a better solution for displaying users (members) who didn’t provide consent for displaying public data.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Sander has been reminded about requirements for reporting. Sander mentioned that it would limit his volunteer time to work on other issues.</p>\r\n</li>\r\n</ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation"><strong>Webmasters Team</strong></p>\r\n</li>\r\n</ul>\r\n<ul>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Team is working on UpTimeRobot, they gave us a free pro account.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">The “Privacy &amp; Security Settings” policy will be finalised after point 2</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Team is working with Hugh, Luca and Jaz to define and build a new JRD structure within community.joomla.org.</p>\r\n</li>\r\n</ul>\r\n</ul>\r\n<h3 dir="ltr">Programs Department</h3>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Investigating the benefits and processes involved with becoming a Brave Content Creator - waiting for Radek’s handover as Treasurer to set up the necessary financial accounts.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">7 candidates recently passed their Joomla Administrator exam.</p>\r\n</li>\r\n</ul>\r\n<h3 dir="ltr">Vice President</h3>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Elisa highlighted the need to improve the communication within the Project.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Ruchika, Davide and Todd are meeting about the Idea to create a Joomla Students “Club” - I fully support the idea to have a place for students to meet and having it as an educational outreach place.&nbsp;</p>\r\n</li>\r\n</ul>\r\n<h3 dir="ltr">Secretary</h3>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">[<strong>Elections</strong>] Radek Suski has been elected as Treasurer of OSM. The Call for Manifestos for the replacement as Operations DC has been published.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">[<strong>Google Partnership</strong>] Hugh and Luca attended a call with Google representatives to kick off the partnership and start the collaboration. An announcement about the agreement will be published on Joomla.org. A featured article in the Magazine will be included monthly starting from October.&nbsp;</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">[<strong>Correspondence</strong>] Received documents from Citibank and Venable and shared with the Board.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">[<strong>Foundation</strong>] Luca had a meeting with our incorporation consultant to seek support with the follow up to the IRS for the Joomla Foundation on Sept. 22. She advised to complete several actions prior to resubmitting the package to the IRS.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">[<strong>Foundation</strong>] We need to renew our “compliance package”, it will cost 1200 USD and includes the support during the incorporation phase as well as the management of the annual reports. Such funding should be recorded as a loan from OSM to the Foundation and should be transferred back when the 501(c)(3) will be fully operational.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">[<strong>Governance</strong>] Luca had a meeting with Jenn Gress (former Ombudsman) and we started working together on a proposal to update the bylaws to better define and describe the role of the Ombudsman. The proposal will be shared with the Board for review as soon as it’s ready.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">[<strong>Google Adsense</strong>] Google AdSense payment was issued on September 21. Luca and Wilco are working on the account review to identify the reason for the invalid ads.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">[<strong>Shop RFP</strong>] No additional proposals were submitted during the extension period. The only proposal received is available to Board members. A motion has been prepared to decide whether to assign or not the RFP. The draft for the service agreement is ready.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">[<strong>Communication</strong>] Effective September 29, 2020, the Full Board of Directors and Officers of Open Source Matters, Inc. will be composed by:</p>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Brian J. Mitchell from United States - President</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Justine Ayebale Abunyanga from Uganda - Vice President</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Luca Marzo from Italy - Secretary</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Radek Suski from Germany - Treasurer</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Marco Dings from the Netherlands - Director</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Hugh Douglas-Smith from United Kingdom - Director</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Jasmine Zoe Parkyn from United Kingdom - Director</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">David Opati Aswani from Kenya - Director</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Marianela Queme Alegre from Netherlands - Director</p>\r\n</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<h3 dir="ltr">Other business</h3>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">[<strong>Procedures</strong>] Elisa and Luca discussed a general improvement in some of the approval procedures for several Joomla Directories (JED, JRD, Events, Trademark). The suggestion is to implement the following steps:</p>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Review the rules published in order to ensure that everything is clear (that doesn\'t mean changing them, but ensure that they are readable).</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Add a FAQs section to clarify specific questions that are common.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">In case of complaints, allow people to have a call to clarify requests and doubts.</p>\r\n</li>\r\n</ul>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">[<strong>Greetings</strong>] The Board would like to thank the outgoing members Elisa Foltyn, Djamel Kherbi and Eric Lamy for their service and contributions.&nbsp;</p>\r\n</li>\r\n</ul>\r\n<h3 dir="ltr">Motions taken during this meeting</h3>\r\n<p dir="ltr"><strong>#2020/122 - Authorise the Webmasters Team to further develop the Service Provider Directory to support paid featured listings.</strong><br />Proposed by Hugh Douglas-Smith, Seconded by Jaz Parkyn.<br /><em>The motion passed with unanimous consent.</em></p>\r\n<p dir="ltr"><strong>#2020/123 - The Secretary proposes to adjourn the meeting.</strong><br />Proposed by Luca Marzo, seconded by Djamel Kherbi.<br /><em>The motion passed with unanimous consent.</em></p>\r\n<h3 dir="ltr">Motions taken offline before this meeting</h3>\r\n<p dir="ltr"><strong>#2020/121 - Approve and publish the Board Meeting Minutes.</strong><br />Proposed by Luca Marzo, seconded by Radek Suski.<br /><em>The motion passed with unanimous consent.</em></p>', NULL, 1, 1021, 1, 669, '2020-10-01 12:56:43', 0, '2020-10-01 12:58:36', 0, '0000-00-00 00:00:00'),
	(1336, 0, 4, 'JCM Meeting Report - September 28th, 2020', 'jcm-meeting-report-september-28th-2020', '<p><em>The meeting started at 14:30 (CEST)</em></p>\r\n<p><strong>Participants:</strong> Anja de Crom, Hans van der Meer, Patrick Jackson, Luca Marzo,&nbsp; Wilco Alsemgeest, Andrea Gentil</p>\r\n<p><strong>Apologies</strong>: Sandra Decoux, Mark Bender</p>\r\n<h3>Discussion outline</h3>\r\n<ul>\r\n<li>September issue published on time: thanks to the whole team for the work done</li>\r\n<li>October issue planning started.</li>\r\n<li>Review of monthly stats from Analytics stats: Data studio ready.</li>\r\n<li>Review the tag structure to facilitate the search of articles by topic.</li>\r\n<li>Pending: Call for authors: set up a mailing list for the registered authors in JCM website to invite them to write again for the JCM. Also, add the list of possible topics to the Author Guidelines (and this will be added in the Editor\'s intro every month).</li>\r\n<li>Pending: Onboarding process for authors: final review to implement it.</li>\r\n</ul>\r\n<p><br /><em>The meeting ended at 15:45 (CEST)</em></p>', NULL, 1, 1022, 1, 1459, '2020-10-02 09:49:58', 0, '2020-10-02 09:53:13', 0, '0000-00-00 00:00:00'),
	(1337, 0, 6, 'Forum Meeting 28/9/2020', 'forum-meeting-28-9-2020', '<p>Meeting Notes:</p>\r\n<ul>\r\n<li>Discussions about forum behavior of certain users</li>\r\n<li>Discussing locking/archiving Community Blog Discussion forum (this forum is archived now)</li>\r\n<li>Discussing Sandbox SSL Certifiacte</li>\r\n<li>Discussing Additional Word Censors</li>\r\n<li>Next Meeting: Monday 26th Oct 2020</li>\r\n</ul>', NULL, 1, 1023, 1, 503, '2020-10-02 09:51:52', 0, '2020-10-02 09:53:58', 0, '0000-00-00 00:00:00'),
	(1338, 0, 57, 'Joomla Accessibility Team - September meeting minutes ', 'joomla-accessibility-team-september-meeting-minute', '<p><strong>Date:</strong> September 28</p>\r\n<p><strong>Atendeess:</strong> Carlos Cámara (Team Leader), Christiane Maier-Stadtherr (Contributor), Vito Disimino (Contributor), Hans Kuijpers (Member), Donato Matturro (Member), Hans Kuijpers (Member), Stefan Wajda (Member), Luca Marzo (OSM Secretary)</p>\r\n<p><strong>Apologies:</strong> Davide Messia (Assistant Team Leader), Justyna Michallek (Member), Marco Dings (Department coordinator), Armen Mnatsian (Contributor)</p>\r\n<h2>Topics</h2>\r\n<h3>Request to Social Media Team to write more accessible posts</h3>\r\n<p>TL has contacted Social Media team requesting to pay attention to some A11y guidelines from here: <a href="https://www.socialmediaexaminer.com/how-to-optimize-social-posts-for-visually-impaired/">https://www.socialmediaexaminer.com/how-to-optimize-social-posts-for-visually-impaired/</a> . They accepted the recommendation gladly.</p>\r\n<h3>JAT magazine articles</h3>\r\n<p>Last month Donato and Vito coordinated the 2part on the J3 Accessible resources series and they will work on the new article for next magazine issue. Last month all the team suggested resources and Donato and Vito could write a much better article. We will do the same for next issue.</p>\r\n<h3>New possible contributors</h3>\r\n<p>We keep seeking for testers and people who want to contribute to A11y in Joomla. We have 2 possible contributors and we will add them to the team as contributors.</p>\r\n<h3>Status of A11y in Cassiopeia</h3>\r\n<p>Hans and Christiane reported they keep working on making Cassiopeia more accessible and they have found some issues related to the core itself and not to the frontend template. We need more testers for Cassiopeia.</p>\r\n<h3>A11y issues of Workflow component</h3>\r\n<p>No updates on this report so we will try to get a push this month.</p>\r\n<h3>A11y online events</h3>\r\n<p>We mentioned the latest A11y events around the world:</p>\r\n<ul>\r\n<li>https://inclusivedesign24.org/2020/</li>\r\n<li><a href="http://wpaccessibilityday.org">Wpaccessibilityday.org </a></li>\r\n</ul>\r\n<p>Both worth attending and keep learning.</p>\r\n<h3>Test report model</h3>\r\n<p>We discussed about the test report model we are trying to set up so that we can offer an easy starting point for testers and bugs and fun participants to test A11y. We do think it’s not a perfect solution but hopefully it will at least raise awareness and help people find more A11y issues.</p>\r\n<h3>Some issues we have been working on</h3>\r\n<ul>\r\n<li>Best way to add icons <a href="https://github.com/joomla/joomla-cms/pull/30792">https://github.com/joomla/joomla-cms/pull/30792</a></li>\r\n<li>Flags issue: <a href="https://github.com/joomla/joomla-cms/issues/30061">https://github.com/joomla/joomla-cms/issues/30061</a></li>\r\n<li>A11y System plugin: https://github.com/joomla/joomla-cms/issues/30598</li>\r\n</ul>', NULL, 1, 1024, 4, 632, '2020-10-02 11:13:54', 632, '2020-10-02 11:30:10', 0, '0000-00-00 00:00:00'),
	(1339, 0, 25, 'Joomla Bug Squad Team Meeting - October 02, 2020 ', 'joomla-bug-squad-team-meeting-october-02-2020', '<p id="docs-internal-guid-b1617f8c-7fff-9d13-8f1a-0563ea0d2fb4" dir="ltr" style="line-height: 1.9871999999999999; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: underline; -webkit-text-decoration-skip: none; text-decoration-skip-ink: none; vertical-align: baseline; white-space: pre-wrap;">Date:</span><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;"> October&nbsp; 02, 2020</span></p>\r\n<p dir="ltr" style="line-height: 1.9871999999999999; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: underline; -webkit-text-decoration-skip: none; text-decoration-skip-ink: none; vertical-align: baseline; white-space: pre-wrap;">Time:</span><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;"> 19:05 CEST</span></p>\r\n<p dir="ltr" style="line-height: 1.9871999999999999; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: underline; -webkit-text-decoration-skip: none; text-decoration-skip-ink: none; vertical-align: baseline; white-space: pre-wrap;">Total time of meeting:</span><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;"> 1 hour</span></p>\r\n<p dir="ltr" style="line-height: 1.9871999999999999; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Attendees:</span></p>\r\n<p dir="ltr" style="line-height: 1.9871999999999999; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Richard Fath, Nicola Galgano, Christiane Maier-Stadtherr, Luca Racchetti, SharkyKZ, Quy Ton, Tobias Zulauf</span></p>\r\n<p>&nbsp;</p>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Discussion outline</span><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">&nbsp;&nbsp;&nbsp; </span><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">&nbsp;&nbsp;&nbsp; </span><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">&nbsp;&nbsp;&nbsp; </span></p>\r\n<p><br /><br /></p>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Current Status</span></p>\r\n<p>&nbsp;</p>\r\n<p dir="ltr" style="line-height: 1.38; text-align: justify; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">The number of open issues is quite stable from the last meeting, about (</span><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">780</span><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">). The most worrying data is relative to the number of the </span><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Release Blocker</span><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">, we have </span><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">27</span><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;"> issues and </span><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">6</span><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;"> pull requests. We all agree that the maximum priority is to lower the number of Release Blockers, without this it will be difficult to reach the Release Candidate status soon.</span></p>\r\n<p>&nbsp;</p>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Old and New members</span></p>\r\n<p>&nbsp;</p>\r\n<p dir="ltr" style="line-height: 1.38; text-align: justify; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">We would like to thank Astrid for all the contributions and time spent in the project, she informed us that she left the Team because of lack of free time. We hope it\'s only a goodbye.</span></p>\r\n<p>&nbsp;</p>\r\n<p dir="ltr" style="line-height: 1.38; text-align: justify; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">We have evaluated the two candidatures to join the team that we have received lately, unanimously we have decided to stand-by for the moment, we would like to see more activities from their side, we remember them that valid contributions are not only tests and coding, but writing documentation or translating in their languages is warmly welcomed.</span></p>\r\n<p><br /><br /></p>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">New features for 4.1</span></p>\r\n<p>&nbsp;</p>\r\n<p dir="ltr" style="line-height: 1.38; text-align: justify; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">We have briefly discussed the proposal from Luca, to have a kind of Credit page to list all libraries, extensions etc., Luca will publish a Request For Comment on Github to get community feedback.</span></p>\r\n<p>&nbsp;</p>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">The already planned feature for 4.1 </span><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Poor mans CRON </span><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">is quite ready see: </span><a href="https://github.com/joomla/joomla-cms/pull/29592" style="text-decoration: none;"><span style="font-size: 11pt; font-family: Arial; color: #1155cc; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: underline; -webkit-text-decoration-skip: none; text-decoration-skip-ink: none; vertical-align: baseline; white-space: pre-wrap;">https://github.com/joomla/joomla-cms/pull/29592</span></a></p>\r\n<p dir="ltr" style="line-height: 1.38; text-align: justify; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">it needs some tests to be merged on 4.1 branch, as it is a base for further enhancements, we would like to invite everyone interested to collaborate with us.</span></p>\r\n<p><br /><br /><br /></p>\r\n<p dir="ltr" style="line-height: 1.656; text-align: justify; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Pizza Bug and Fun + HacktoberFest</span></p>\r\n<p>&nbsp;</p>\r\n<p dir="ltr" style="line-height: 1.656; text-align: justify; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">We would like to invite you to participate on 17 October to the Pizza Bug and Fun, for more detail </span><a href="https://pizza-bugs-fun.com/en/" style="text-decoration: none;"><span style="font-size: 11pt; font-family: Arial; color: #1155cc; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: underline; -webkit-text-decoration-skip: none; text-decoration-skip-ink: none; vertical-align: baseline; white-space: pre-wrap;">https://pizza-bugs-fun.com/en/</span></a><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;"> . Also we remind you that you can subscribe to Hacktoberfest </span><a href="https://hacktoberfest.digitalocean.com/" style="text-decoration: none;"><span style="font-size: 11pt; font-family: Arial; color: #1155cc; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: underline; -webkit-text-decoration-skip: none; text-decoration-skip-ink: none; vertical-align: baseline; white-space: pre-wrap;">https://hacktoberfest.digitalocean.com/</span></a><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">&nbsp; with 4 pull request you’ll gain a limited edition T-Shirt or plant a tree , plus you contribute to Open Source.</span></p>\r\n<p>&nbsp;</p>\r\n<p dir="ltr" style="line-height: 1.656; text-align: justify; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">I’m very happy to inform you that with the help of the JUG we have organized&nbsp; a series of free online night talks to prepare you for the PBF and illustrate the new Joomla 4 features. For more details :</span></p>\r\n<p dir="ltr" style="line-height: 1.656; text-align: justify; margin-top: 0pt; margin-bottom: 0pt;"><a href="https://organize.mlh.io/participants/events/4999-joomla-user-groups-night" style="text-decoration: none;"><span style="font-size: 11pt; font-family: Arial; color: #1155cc; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: underline; -webkit-text-decoration-skip: none; text-decoration-skip-ink: none; vertical-align: baseline; white-space: pre-wrap;">https://organize.mlh.io/participants/events/4999-joomla-user-groups-night</span></a></p>\r\n<p dir="ltr" style="line-height: 1.656; text-align: justify; margin-top: 0pt; margin-bottom: 0pt;"><a href="https://night.jugproject.net/" style="text-decoration: none;"><span style="font-size: 11pt; font-family: Arial; color: #1155cc; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: underline; -webkit-text-decoration-skip: none; text-decoration-skip-ink: none; vertical-align: baseline; white-space: pre-wrap;">https://night.jugproject.net/</span></a></p>\r\n<p>&nbsp;</p>\r\n<p dir="ltr" style="line-height: 1.656; text-align: justify; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Next Meeting</span></p>\r\n<p>&nbsp;</p>\r\n<p dir="ltr" style="line-height: 1.656; text-align: justify; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">The next team meeting will be: Fri, 23 October at 7:00 PM CEST</span></p>', NULL, 1, 1025, 2, 526, '2020-10-03 07:24:37', 526, '2020-10-10 13:43:51', 0, '0000-00-00 00:00:00'),
	(1340, 0, 44, 'JED Meeting - September 25th, 2020', 'jed-meeting-september-25th-2020', '<p class="western" style="margin-bottom: 0.24in; direction: ltr; color: #000000; line-height: 16.8667px; background: transparent; font-family: Arial, serif; font-size: 11pt; border: none; padding: 0in;"><span style="color: #333333;"><span style="font-size: 10pt;">The Joomla! Extensions Directory (JED) team met on JED Meeting - September 25th, 2020, at 15hs UTC, via Glip to discuss our progress of current tasks.</span></span></p>\r\n<ul style="color: #000000; font-size: medium;">\r\n<li>\r\n<p class="western" style="margin-bottom: 0in; direction: ltr; line-height: 22.7333px; background: transparent; font-family: Arial, serif; font-size: 11pt; border: none; padding: 0in;"><span style="color: #333333;"><span style="font-size: 10pt;"><b>In attendance</b></span></span><span style="color: #333333;"><span style="font-size: 10pt;">: Anibal Sanchez, Jaz Parkyn, Carlos Cámara, Claire Mandville,&nbsp;Hugh Douglas-Smith, Luca Marzo, Mark Lee, Wilco Alsemgeest (Assistant to the Department Coordinator).</span></span></p>\r\n</li>\r\n<li>\r\n<p class="western" style="margin-bottom: 0in; direction: ltr; line-height: 22.7333px; background: transparent; font-family: Arial, serif; font-size: 11pt; border: none; padding: 0in;"><span style="color: #333333;"><span style="font-size: 10pt;"><b>Absent</b></span></span><span style="color: #333333;"><span style="font-size: 10pt;">: Daniel Dubois and Dénes Székely</span></span></p>\r\n</li>\r\n</ul>\r\n<h2 class="western" style="margin-top: 0.25in; margin-bottom: 0.06in; direction: ltr; color: #000000; line-height: 29.8667px; break-inside: avoid; background: transparent; break-after: avoid; font-family: Arial, serif; font-size: 16pt; font-weight: normal; border: none; padding: 0in;"><a name="h.4jvcf96bui6y"></a>&nbsp;&nbsp;<span style="color: #333333;"><span style="font-size: 17pt;"><b>Team Organization</b></span></span></h2>\r\n<ul style="color: #000000; font-size: medium;">\r\n<li>\r\n<p class="western" style="margin-bottom: 0in; direction: ltr; line-height: 22.7333px; background: transparent; font-family: Arial, serif; font-size: 11pt; border: none; padding: 0in;"><span style="color: #333333;"><span style="font-size: 10pt;">The </span></span><span style="color: #333333;"><span style="font-size: 10pt;"><b>email templates and the email operation</b></span></span><span style="color: #333333;"><span style="font-size: 10pt;"> have been migrated to ElasticMail</span></span></p>\r\n</li>\r\n<li>\r\n<p class="western" style="margin-bottom: 0in; direction: ltr; line-height: 22.7333px; background: transparent; font-family: Arial, serif; font-size: 11pt; border: none; padding: 0in;"><span style="color: #333333;"><span style="font-size: 10pt;">The </span></span><span style="color: #333333;"><span style="font-size: 10pt;"><b>merge of VEL operation</b></span></span><span style="color: #333333;"><span style="font-size: 10pt;"> is almost completed. We are polishing the final steps. We are reviewing the pending issues</span></span></p>\r\n</li>\r\n<li>\r\n<p class="western" style="margin-bottom: 0in; direction: ltr; line-height: 22.7333px; background: transparent; font-family: Arial, serif; font-size: 11pt; border: none; padding: 0in;"><span style="color: #333333;"><span style="font-size: 10pt;">We are also reviewing the </span></span><span style="color: #333333;"><span style="font-size: 10pt;"><b>backup policy</b></span></span><span style="color: #333333;"><span style="font-size: 10pt;"> and the associated costs.</span></span></p>\r\n</li>\r\n<li>\r\n<p class="western" style="margin-bottom: 0in; direction: ltr; line-height: 22.7333px; background: transparent; font-family: Arial, serif; font-size: 11pt; border: none; padding: 0in;"><span style="color: #333333;"><span style="font-size: 10pt;"><b>Data Cleansing</b></span></span><span style="color: #333333;"><span style="font-size: 10pt;">, we are preparing a cleanup of old reviews and the anonymization of old accounts without activity.</span></span></p>\r\n</li>\r\n<li>\r\n<p class="western" style="margin-bottom: 0in; direction: ltr; line-height: 22.7333px; background: transparent; font-family: Arial, serif; font-size: 11pt; border: none; padding: 0in;"><span style="color: #333333;"><span style="font-size: 10pt;"><b>Call for JED4 Developers</b></span></span><span style="color: #333333;"><span style="font-size: 10pt;">. We are going to submit a JCM article to call for JED4 Developers. To contribute to the project, </span></span><span style="color: #0088cc;"><span style="font-size: 10pt;"><a href="https://github.com/joomla/com_jed4/" style="color: #000080; text-decoration-line: underline;">join</a><a href="https://github.com/joomla/com_jed4/" style="color: #000080; text-decoration-line: underline;"> </a><a href="https://github.com/joomla/com_jed4/" style="color: #000080; text-decoration-line: underline;">the</a><a href="https://github.com/joomla/com_jed4/" style="color: #000080; text-decoration-line: underline;"> </a><a href="https://github.com/joomla/com_jed4/" style="color: #000080; text-decoration-line: underline;">team</a><a href="https://github.com/joomla/com_jed4/" style="color: #000080; text-decoration-line: underline;">!</a></span></span></p>\r\n</li>\r\n<li>\r\n<p class="western" style="margin-bottom: 0in; direction: ltr; line-height: 22.7333px; background: transparent; font-family: Arial, serif; font-size: 11pt; border: none; padding: 0in;"><span style="color: #333333;"><span style="font-size: 10pt;">We are processing the queue of new extensions and pending tickets within the seven days processing window. With the addition of new members, we are improving the processing times.</span></span></p>\r\n</li>\r\n</ul>\r\n<h2 class="western" style="margin-top: 0.25in; margin-bottom: 0.06in; direction: ltr; color: #000000; line-height: 29.8667px; break-inside: avoid; background: transparent; break-after: avoid; font-family: Arial, serif; font-size: 16pt; font-weight: normal; border: none; padding: 0in;"><a name="h.p306e4olo4ex"></a>&nbsp;&nbsp;<span style="color: #333333;"><span style="font-size: 17pt;"><b>Plans</b></span></span></h2>\r\n<ul style="color: #000000; font-size: medium;">\r\n<li>\r\n<p class="western" style="margin-bottom: 0in; direction: ltr; line-height: 22.7333px; background: transparent; font-family: Arial, serif; font-size: 11pt; border: none; padding: 0in;"><span style="color: #333333;"><span style="font-size: 10pt;"><b>IP Detective Deprecation: </b></span></span><span style="color: #333333;"><span style="font-size: 10pt;">New method of review validations based on hCaptcha and CleanTalk / October - November.</span></span></p>\r\n</li>\r\n<li>\r\n<p class="western" style="margin-bottom: 0in; direction: ltr; line-height: 22.7333px; background: transparent; font-family: Arial, serif; font-size: 11pt; border: none; padding: 0in;"><span style="color: #333333;"><span style="font-size: 10pt;"><b>JED LinkChecker - Next Phases: </b></span></span><span style="color: #333333;"><span style="font-size: 10pt;">we are adding stricter validations to the link validations</span></span></p>\r\n</li>\r\n<li>\r\n<p class="western" style="margin-bottom: 0in; direction: ltr; line-height: 22.7333px; background: transparent; font-family: Arial, serif; font-size: 11pt; border: none; padding: 0in;"><span style="color: #333333;"><span style="font-size: 10pt;"><b>JED Design Updates: </b></span></span><span style="color: #333333;"><span style="font-size: 10pt;">General review of the site design for mobile and desktop. Renewal of JED general visual technology and design. </span></span><a href="https://github.com/joomla/jed-issues/issues/139" style="color: #000080; text-decoration-line: underline;"><span style="color: #0088cc;"><span style="font-size: 10pt;">#139</span></span></a></p>\r\n</li>\r\n<li>\r\n<p class="western" style="margin-bottom: 0in; direction: ltr; line-height: 22.7333px; background: transparent; font-family: Arial, serif; font-size: 11pt; border: none; padding: 0in;"><span style="color: #333333;"><span style="font-size: 10pt;"><b>List Q1: Quality Requirements of Extensions </b></span></span><span style="color: #333333;"><span style="font-size: 10pt;">To improve the current extension quality requirements, we are defining a new set of minimum rules. The idea is to improve the minimum information submitted by the extension developers. </span></span><a href="https://github.com/joomla/jed-issues/issues/149" style="color: #000080; text-decoration-line: underline;"><span style="color: #0088cc;"><span style="font-size: 10pt;">#149</span></span></a></p>\r\n<ul>\r\n<li>\r\n<p class="western" style="margin-bottom: 0in; direction: ltr; line-height: 22.7333px; background: transparent; font-family: Arial, serif; font-size: 11pt; border: none; padding: 0in;"><span style="color: #333333;"><span style="font-size: 10pt;">At this point, these are the rules that we are currently proposing:</span></span></p>\r\n<ul>\r\n<li>\r\n<p class="western" style="margin-bottom: 0in; direction: ltr; line-height: 22.7333px; background: transparent; font-family: Arial, serif; font-size: 11pt; border: none; padding: 0in;"><span style="color: #333333;"><span style="font-size: 10pt;">Listings in English</span></span></p>\r\n</li>\r\n<li>\r\n<p class="western" style="margin-bottom: 0in; direction: ltr; line-height: 22.7333px; background: transparent; font-family: Arial, serif; font-size: 11pt; border: none; padding: 0in;"><span style="color: #333333;"><span style="font-size: 10pt;">Project or Download page at least in English</span></span></p>\r\n</li>\r\n<li>\r\n<p class="western" style="margin-bottom: 0in; direction: ltr; line-height: 22.7333px; background: transparent; font-family: Arial, serif; font-size: 11pt; border: none; padding: 0in;"><span style="color: #333333;"><span style="font-size: 10pt;">Screenshots in English</span></span></p>\r\n</li>\r\n<li>\r\n<p class="western" style="margin-bottom: 0in; direction: ltr; line-height: 22.7333px; background: transparent; font-family: Arial, serif; font-size: 11pt; border: none; padding: 0in;"><span style="color: #333333;"><span style="font-size: 10pt;">Provide English language files</span></span></p>\r\n</li>\r\n<li>\r\n<p class="western" style="margin-bottom: 0in; direction: ltr; line-height: 22.7333px; background: transparent; font-family: Arial, serif; font-size: 11pt; border: none; padding: 0in;"><span style="color: #333333;"><span style="font-size: 10pt;">Links linking to the right page (E.g. the demo link must target a proper demo)</span></span></p>\r\n</li>\r\n<li>\r\n<p class="western" style="margin-bottom: 0in; direction: ltr; line-height: 22.7333px; background: transparent; font-family: Arial, serif; font-size: 11pt; border: none; padding: 0in;"><span style="color: #333333;"><span style="font-size: 10pt;">Screenshots with logo and/or an accurate representation of the extension</span></span></p>\r\n</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n</li>\r\n<li>\r\n<p class="western" style="margin-bottom: 0in; direction: ltr; line-height: 22.7333px; background: transparent; font-family: Arial, serif; font-size: 11pt; border: none; padding: 0in;"><span style="color: #333333;"><span style="font-size: 10pt;"><b>JED Checker CI Online. </b></span></span><span style="color: #333333;"><span style="font-size: 10pt;">This is project scope: </span></span><span style="color: #0088cc;"><span style="font-size: 10pt;"><a href="https://github.com/joomla/jed-issues/wiki/JED-Extensions-Checker-CI" style="color: #000080; text-decoration-line: underline;">https</a><a href="https://github.com/joomla/jed-issues/wiki/JED-Extensions-Checker-CI" style="color: #000080; text-decoration-line: underline;">://</a><a href="https://github.com/joomla/jed-issues/wiki/JED-Extensions-Checker-CI" style="color: #000080; text-decoration-line: underline;">github</a><a href="https://github.com/joomla/jed-issues/wiki/JED-Extensions-Checker-CI" style="color: #000080; text-decoration-line: underline;">.</a><a href="https://github.com/joomla/jed-issues/wiki/JED-Extensions-Checker-CI" style="color: #000080; text-decoration-line: underline;">com</a><a href="https://github.com/joomla/jed-issues/wiki/JED-Extensions-Checker-CI" style="color: #000080; text-decoration-line: underline;">/</a><a href="https://github.com/joomla/jed-issues/wiki/JED-Extensions-Checker-CI" style="color: #000080; text-decoration-line: underline;">joomla</a><a href="https://github.com/joomla/jed-issues/wiki/JED-Extensions-Checker-CI" style="color: #000080; text-decoration-line: underline;">/</a><a href="https://github.com/joomla/jed-issues/wiki/JED-Extensions-Checker-CI" style="color: #000080; text-decoration-line: underline;">jed</a><a href="https://github.com/joomla/jed-issues/wiki/JED-Extensions-Checker-CI" style="color: #000080; text-decoration-line: underline;">-</a><a href="https://github.com/joomla/jed-issues/wiki/JED-Extensions-Checker-CI" style="color: #000080; text-decoration-line: underline;">issues</a><a href="https://github.com/joomla/jed-issues/wiki/JED-Extensions-Checker-CI" style="color: #000080; text-decoration-line: underline;">/</a><a href="https://github.com/joomla/jed-issues/wiki/JED-Extensions-Checker-CI" style="color: #000080; text-decoration-line: underline;">wiki</a><a href="https://github.com/joomla/jed-issues/wiki/JED-Extensions-Checker-CI" style="color: #000080; text-decoration-line: underline;">/</a><a href="https://github.com/joomla/jed-issues/wiki/JED-Extensions-Checker-CI" style="color: #000080; text-decoration-line: underline;">JED</a><a href="https://github.com/joomla/jed-issues/wiki/JED-Extensions-Checker-CI" style="color: #000080; text-decoration-line: underline;">-</a><a href="https://github.com/joomla/jed-issues/wiki/JED-Extensions-Checker-CI" style="color: #000080; text-decoration-line: underline;">Extensions</a><a href="https://github.com/joomla/jed-issues/wiki/JED-Extensions-Checker-CI" style="color: #000080; text-decoration-line: underline;">-</a><a href="https://github.com/joomla/jed-issues/wiki/JED-Extensions-Checker-CI" style="color: #000080; text-decoration-line: underline;">Checker</a><a href="https://github.com/joomla/jed-issues/wiki/JED-Extensions-Checker-CI" style="color: #000080; text-decoration-line: underline;">-</a><a href="https://github.com/joomla/jed-issues/wiki/JED-Extensions-Checker-CI" style="color: #000080; text-decoration-line: underline;">CI</a></span></span><span style="color: #333333;"><span style="font-size: 10pt;">. At the FFTF, Andy Gaskell and Llewellyn van der Merwe joined the JED Dev Team to support the effort. </span></span><a href="https://github.com/joomla/jed-issues/issues/67" style="color: #000080; text-decoration-line: underline;"><span style="color: #0088cc;"><span style="font-size: 10pt;">#67</span></span></a><span style="color: #333333;"><span style="font-size: 10pt;"> and </span></span><a href="https://github.com/joomla/jed-issues/issues/133" style="color: #000080; text-decoration-line: underline;"><span style="color: #0088cc;"><span style="font-size: 10pt;">#133</span></span></a></p>\r\n</li>\r\n</ul>\r\n<h2 class="western" style="margin-top: 0.25in; margin-bottom: 0.06in; direction: ltr; color: #000000; line-height: 29.8667px; break-inside: avoid; background: transparent; break-after: avoid; font-family: Arial, serif; font-size: 16pt; font-weight: normal; border: none; padding: 0in;"><a name="h.cp13nur83l8m"></a>&nbsp;&nbsp;<span style="color: #333333;"><span style="font-size: 17pt;"><b>JED 4 Project</b></span></span></h2>\r\n<ul style="color: #000000; font-size: medium;">\r\n<li>\r\n<p class="western" style="margin-bottom: 0in; direction: ltr; line-height: 22.7333px; background: transparent; font-family: Arial, serif; font-size: 11pt; border: none; padding: 0in;"><span style="color: #333333;"><span style="font-size: 10pt;">JED4 development has been continuing. To contribute to the project, </span></span><span style="color: #0088cc;"><span style="font-size: 10pt;"><a href="https://github.com/joomla/com_jed4/" style="color: #000080; text-decoration-line: underline;">join</a><a href="https://github.com/joomla/com_jed4/" style="color: #000080; text-decoration-line: underline;"> </a><a href="https://github.com/joomla/com_jed4/" style="color: #000080; text-decoration-line: underline;">the</a><a href="https://github.com/joomla/com_jed4/" style="color: #000080; text-decoration-line: underline;"> </a><a href="https://github.com/joomla/com_jed4/" style="color: #000080; text-decoration-line: underline;">team</a><a href="https://github.com/joomla/com_jed4/" style="color: #000080; text-decoration-line: underline;">!</a></span></span></p>\r\n</li>\r\n<li>\r\n<p class="western" style="margin-bottom: 0in; direction: ltr; line-height: 22.7333px; background: transparent; font-family: Arial, serif; font-size: 11pt; border: none; padding: 0in;"><span style="color: #333333;"><span style="font-size: 10pt;">The board approved the proposal to make the new JED 4 codebase and project information available publicly.</span></span></p>\r\n</li>\r\n<li>\r\n<p class="western" style="margin-bottom: 0in; direction: ltr; line-height: 22.7333px; background: transparent; font-family: Arial, serif; font-size: 11pt; border: none; padding: 0in;"><span style="color: #333333;"><span style="font-size: 10pt;">The Review Detective and Review Scores will be kept private within the JED team.</span></span></p>\r\n</li>\r\n<li>\r\n<p class="western" style="margin-bottom: 0in; direction: ltr; line-height: 22.7333px; background: transparent; font-family: Arial, serif; font-size: 11pt; border: none; padding: 0in;"><span style="color: #333333;"><span style="font-size: 10pt;">This is the </span></span><span style="color: #0088cc;"><span style="font-size: 10pt;"><a href="https://github.com/joomla/jed-issues/wiki/JED-4-Specifications-Summary" style="color: #000080; text-decoration-line: underline;">JED</a><a href="https://github.com/joomla/jed-issues/wiki/JED-4-Specifications-Summary" style="color: #000080; text-decoration-line: underline;"> 4 </a><a href="https://github.com/joomla/jed-issues/wiki/JED-4-Specifications-Summary" style="color: #000080; text-decoration-line: underline;">Project</a><a href="https://github.com/joomla/jed-issues/wiki/JED-4-Specifications-Summary" style="color: #000080; text-decoration-line: underline;"> </a><a href="https://github.com/joomla/jed-issues/wiki/JED-4-Specifications-Summary" style="color: #000080; text-decoration-line: underline;">Scope</a><a href="https://github.com/joomla/jed-issues/wiki/JED-4-Specifications-Summary" style="color: #000080; text-decoration-line: underline;"> </a><a href="https://github.com/joomla/jed-issues/wiki/JED-4-Specifications-Summary" style="color: #000080; text-decoration-line: underline;">Summary</a></span></span><span style="color: #333333;"><span style="font-size: 10pt;">.</span></span></p>\r\n</li>\r\n<li>\r\n<p class="western" style="margin-bottom: 0in; direction: ltr; line-height: 22.7333px; background: transparent; font-family: Arial, serif; font-size: 11pt; border: none; padding: 0in;"><span style="color: #333333;"><span style="font-size: 10pt;">The </span></span><span style="color: #333333;"><span style="font-size: 10pt;"><b>JED 4 development repository</b></span></span><span style="color: #333333;"><span style="font-size: 10pt;"> has been published: </span></span><span style="color: #0088cc;"><span style="font-size: 10pt;"><a href="https://github.com/joomla/com_jed4/" style="color: #000080; text-decoration-line: underline;">https</a><a href="https://github.com/joomla/com_jed4/" style="color: #000080; text-decoration-line: underline;">://</a><a href="https://github.com/joomla/com_jed4/" style="color: #000080; text-decoration-line: underline;">github</a><a href="https://github.com/joomla/com_jed4/" style="color: #000080; text-decoration-line: underline;">.</a><a href="https://github.com/joomla/com_jed4/" style="color: #000080; text-decoration-line: underline;">com</a><a href="https://github.com/joomla/com_jed4/" style="color: #000080; text-decoration-line: underline;">/</a><a href="https://github.com/joomla/com_jed4/" style="color: #000080; text-decoration-line: underline;">joomla</a><a href="https://github.com/joomla/com_jed4/" style="color: #000080; text-decoration-line: underline;">/</a><a href="https://github.com/joomla/com_jed4/" style="color: #000080; text-decoration-line: underline;">com</a><a href="https://github.com/joomla/com_jed4/" style="color: #000080; text-decoration-line: underline;">_</a><a href="https://github.com/joomla/com_jed4/" style="color: #000080; text-decoration-line: underline;">jed</a><a href="https://github.com/joomla/com_jed4/" style="color: #000080; text-decoration-line: underline;">4</a></span></span><span style="color: #333333;"><span style="font-size: 10pt;">. Please, check the list of issues and collaborate with the development.</span></span></p>\r\n</li>\r\n<li>\r\n<p class="western" style="margin-bottom: 0in; direction: ltr; line-height: 22.7333px; background: transparent; font-family: Arial, serif; font-size: 11pt; border: none; padding: 0in;"><span style="color: #333333;"><span style="font-size: 10pt;">According to the approved </span></span><span style="color: #333333;"><span style="font-size: 10pt;"><b>Joomla Property Management Policy, </b></span></span><span style="color: #333333;"><span style="font-size: 10pt;">JED4 will also manage templates.</span></span></p>\r\n</li>\r\n</ul>\r\n<h2 class="western" style="margin-top: 0.25in; margin-bottom: 0.06in; direction: ltr; color: #000000; line-height: 29.8667px; break-inside: avoid; background: transparent; break-after: avoid; font-family: Arial, serif; font-size: 16pt; font-weight: normal; border: none; padding: 0in;"><a name="h.aku0bzo7bos3"></a>&nbsp;&nbsp;<span style="color: #333333;"><span style="font-size: 17pt;"><b>Proposed JED Guidelines for Joomla 4</b></span></span></h2>\r\n<ul style="color: #000000; font-size: medium;">\r\n<li>\r\n<p class="western" style="margin-bottom: 0in; direction: ltr; line-height: 22.7333px; background: transparent; font-family: Arial, serif; font-size: 11pt; border: none; padding: 0in;"><span style="color: #0088cc;"><span style="font-size: 10pt;"><a href="https://docs.joomla.org/Potential_backward_compatibility_issues_in_Joomla_4" style="color: #000080; text-decoration-line: underline;">Potential</a><a href="https://docs.joomla.org/Potential_backward_compatibility_issues_in_Joomla_4" style="color: #000080; text-decoration-line: underline;"> </a><a href="https://docs.joomla.org/Potential_backward_compatibility_issues_in_Joomla_4" style="color: #000080; text-decoration-line: underline;">backward</a><a href="https://docs.joomla.org/Potential_backward_compatibility_issues_in_Joomla_4" style="color: #000080; text-decoration-line: underline;"> </a><a href="https://docs.joomla.org/Potential_backward_compatibility_issues_in_Joomla_4" style="color: #000080; text-decoration-line: underline;">compatibility</a><a href="https://docs.joomla.org/Potential_backward_compatibility_issues_in_Joomla_4" style="color: #000080; text-decoration-line: underline;"> </a><a href="https://docs.joomla.org/Potential_backward_compatibility_issues_in_Joomla_4" style="color: #000080; text-decoration-line: underline;">issues</a><a href="https://docs.joomla.org/Potential_backward_compatibility_issues_in_Joomla_4" style="color: #000080; text-decoration-line: underline;"> </a><a href="https://docs.joomla.org/Potential_backward_compatibility_issues_in_Joomla_4" style="color: #000080; text-decoration-line: underline;">in</a><a href="https://docs.joomla.org/Potential_backward_compatibility_issues_in_Joomla_4" style="color: #000080; text-decoration-line: underline;"> </a><a href="https://docs.joomla.org/Potential_backward_compatibility_issues_in_Joomla_4" style="color: #000080; text-decoration-line: underline;">Joomla</a><a href="https://docs.joomla.org/Potential_backward_compatibility_issues_in_Joomla_4" style="color: #000080; text-decoration-line: underline;"> 4</a></span></span></p>\r\n</li>\r\n<li>\r\n<p class="western" style="margin-bottom: 0in; direction: ltr; line-height: 22.7333px; background: transparent; font-family: Arial, serif; font-size: 11pt; border: none; padding: 0in;"><span style="color: #0088cc;"><span style="font-size: 10pt;"><a href="https://github.com/joomla/jed-issues/wiki/Frequently-Answered-Questions-about-Joomla-4" style="color: #000080; text-decoration-line: underline;">Frequently</a><a href="https://github.com/joomla/jed-issues/wiki/Frequently-Answered-Questions-about-Joomla-4" style="color: #000080; text-decoration-line: underline;"> </a><a href="https://github.com/joomla/jed-issues/wiki/Frequently-Answered-Questions-about-Joomla-4" style="color: #000080; text-decoration-line: underline;">Answered</a><a href="https://github.com/joomla/jed-issues/wiki/Frequently-Answered-Questions-about-Joomla-4" style="color: #000080; text-decoration-line: underline;"> </a><a href="https://github.com/joomla/jed-issues/wiki/Frequently-Answered-Questions-about-Joomla-4" style="color: #000080; text-decoration-line: underline;">Questions</a><a href="https://github.com/joomla/jed-issues/wiki/Frequently-Answered-Questions-about-Joomla-4" style="color: #000080; text-decoration-line: underline;"> </a><a href="https://github.com/joomla/jed-issues/wiki/Frequently-Answered-Questions-about-Joomla-4" style="color: #000080; text-decoration-line: underline;">about</a><a href="https://github.com/joomla/jed-issues/wiki/Frequently-Answered-Questions-about-Joomla-4" style="color: #000080; text-decoration-line: underline;"> </a><a href="https://github.com/joomla/jed-issues/wiki/Frequently-Answered-Questions-about-Joomla-4" style="color: #000080; text-decoration-line: underline;">Joomla</a><a href="https://github.com/joomla/jed-issues/wiki/Frequently-Answered-Questions-about-Joomla-4" style="color: #000080; text-decoration-line: underline;"> 4</a></span></span></p>\r\n</li>\r\n<li>\r\n<p class="western" style="margin-bottom: 0in; direction: ltr; line-height: 22.7333px; background: transparent; font-family: Arial, serif; font-size: 11pt; border: none; padding: 0in;"><span style="color: #0088cc;"><span style="font-size: 10pt;"><i><a href="https://github.com/joomla/jed-issues/issues/67" style="color: #000080; text-decoration-line: underline;">New</a><a href="https://github.com/joomla/jed-issues/issues/67" style="color: #000080; text-decoration-line: underline;"> </a><a href="https://github.com/joomla/jed-issues/issues/67" style="color: #000080; text-decoration-line: underline;">extensions</a><a href="https://github.com/joomla/jed-issues/issues/67" style="color: #000080; text-decoration-line: underline;"> </a><a href="https://github.com/joomla/jed-issues/issues/67" style="color: #000080; text-decoration-line: underline;">guidelines</a><a href="https://github.com/joomla/jed-issues/issues/67" style="color: #000080; text-decoration-line: underline;"> </a><a href="https://github.com/joomla/jed-issues/issues/67" style="color: #000080; text-decoration-line: underline;">for</a><a href="https://github.com/joomla/jed-issues/issues/67" style="color: #000080; text-decoration-line: underline;"> </a><a href="https://github.com/joomla/jed-issues/issues/67" style="color: #000080; text-decoration-line: underline;">J</a><a href="https://github.com/joomla/jed-issues/issues/67" style="color: #000080; text-decoration-line: underline;">4 </a><a href="https://github.com/joomla/jed-issues/issues/67" style="color: #000080; text-decoration-line: underline;">and</a><a href="https://github.com/joomla/jed-issues/issues/67" style="color: #000080; text-decoration-line: underline;"> </a><a href="https://github.com/joomla/jed-issues/issues/67" style="color: #000080; text-decoration-line: underline;">JED</a><a href="https://github.com/joomla/jed-issues/issues/67" style="color: #000080; text-decoration-line: underline;"> </a><a href="https://github.com/joomla/jed-issues/issues/67" style="color: #000080; text-decoration-line: underline;">Checker</a><a href="https://github.com/joomla/jed-issues/issues/67" style="color: #000080; text-decoration-line: underline;"> </a><a href="https://github.com/joomla/jed-issues/issues/67" style="color: #000080; text-decoration-line: underline;">CI</a><a href="https://github.com/joomla/jed-issues/issues/67" style="color: #000080; text-decoration-line: underline;"> </a><a href="https://github.com/joomla/jed-issues/issues/67" style="color: #000080; text-decoration-line: underline;">for</a><a href="https://github.com/joomla/jed-issues/issues/67" style="color: #000080; text-decoration-line: underline;"> </a><a href="https://github.com/joomla/jed-issues/issues/67" style="color: #000080; text-decoration-line: underline;">Joomla</a><a href="https://github.com/joomla/jed-issues/issues/67" style="color: #000080; text-decoration-line: underline;"> 4</a></i></span></span><a href="https://github.com/joomla/jed-issues/issues/67" style="color: #000080; text-decoration-line: underline;"><span style="color: #0088cc;"><span style="font-size: 10pt;"><b>. </b></span></span></a><a href="https://github.com/joomla/jed-issues/issues/67" style="color: #000080; text-decoration-line: underline;"><span style="color: #0088cc;"><span style="font-size: 10pt;">#67</span></span></a></p>\r\n</li>\r\n<li>\r\n<p class="western" style="margin-bottom: 0in; direction: ltr; line-height: 22.7333px; background: transparent; font-family: Arial, serif; font-size: 11pt; border: none; padding: 0in;"><span style="color: #333333;"><span style="font-size: 10pt;"><b>Minimum technical requirements</b></span></span><span style="color: #333333;"><span style="font-size: 10pt;">: We have been discussing the steps to manage extensions for Joomla 3, and Joomla 4 after Joomla 4 is released, to keep compatibility with minimum technical requirements for Joomla 3 and encourage the usage of PHP latest stable version and security practices.</span></span></p>\r\n</li>\r\n<li>\r\n<p class="western" style="margin-bottom: 0in; direction: ltr; line-height: 22.7333px; background: transparent; font-family: Arial, serif; font-size: 11pt; border: none; padding: 0in;"><span style="color: #333333;"><span style="font-size: 10pt;"><b>Override Everything</b></span></span><span style="color: #333333;"><span style="font-size: 10pt;">: All assets should go through HTML helper or web assets. In this way, the assets are overridable, and scripts can be put in the footer and still work. Restrict handwritten script tags in the output (This needs to go through addScriptDeclaration). Include assets ONLY in the layouts, not in the main logic.</span></span></p>\r\n</li>\r\n</ul>\r\n<h2 class="western" style="margin-top: 0.25in; margin-bottom: 0.06in; direction: ltr; color: #000000; line-height: 29.8667px; break-inside: avoid; background: transparent; break-after: avoid; font-family: Arial, serif; font-size: 16pt; font-weight: normal; border: none; padding: 0in;"><a name="h.6onjwv2oyef1"></a>&nbsp;&nbsp;<span style="color: #333333;"><span style="font-size: 17pt;"><b>Scoring Issues</b></span></span></h2>\r\n<ul style="color: #000000; font-size: medium;">\r\n<li>\r\n<p class="western" style="margin-bottom: 0in; direction: ltr; line-height: 22.7333px; background: transparent; font-family: Arial, serif; font-size: 11pt; border: none; padding: 0in;"><span style="color: #333333;"><span style="font-size: 10pt;">An issue on the extensions scoring has been reported. We are looking for a developer to evaluate the current algorithm and fix the issue. For more information </span></span><a href="https://github.com/joomla/jed-issues/issues/84" style="color: #000080; text-decoration-line: underline;"><span style="color: #0088cc;"><span style="font-size: 10pt;">#84</span></span></a></p>\r\n</li>\r\n<li>\r\n<p class="western" style="margin-bottom: 0in; direction: ltr; line-height: 22.7333px; background: transparent; font-family: Arial, serif; font-size: 11pt; border: none; padding: 0in;"><span style="color: #333333;"><span style="font-size: 10pt;">We have fixed urgent issues caused by internal changes in the stats. </span></span><a href="https://github.com/joomla/jed-issues/issues/196" style="color: #000080; text-decoration-line: underline;"><span style="color: #1155cc;"><span style="font-size: 10pt;"><span style="text-decoration: underline;">#196</span></span></span></a><span style="color: #333333;"><span style="font-size: 10pt;">.</span></span></p>\r\n</li>\r\n</ul>\r\n<h2 class="western" style="margin-top: 0.25in; margin-bottom: 0.06in; direction: ltr; color: #000000; line-height: 29.8667px; break-inside: avoid; background: transparent; break-after: avoid; font-family: Arial, serif; font-size: 16pt; font-weight: normal; border: none; padding: 0in;"><a name="h.rmit27awka6s"></a>&nbsp;&nbsp;<span style="color: #333333;"><span style="font-size: 17pt;"><b>Spam Checks for Reviews</b></span></span></h2>\r\n<p class="western" style="margin-bottom: 0.24in; direction: ltr; color: #000000; line-height: 16.8667px; background: transparent; font-family: Arial, serif; font-size: 11pt; border: none; padding: 0in;"><span style="color: #333333;"><span style="font-size: 10pt;">The current system to process Spam Review needs to be revamped. The current implementation has several shortcomings that must be addressed.</span></span></p>\r\n<p class="western" style="margin-bottom: 0.24in; direction: ltr; color: #000000; line-height: 16.8667px; background: transparent; font-family: Arial, serif; font-size: 11pt; border: none; padding: 0in;"><span style="color: #333333;"><span style="font-size: 10pt;">In the medium term, we are planning to redefine the system and implement a new checkup in the context of the JED4 project. We are updating the progress in the implementation here </span></span><a href="https://github.com/joomla/jed-issues/issues/85" style="color: #000080; text-decoration-line: underline;"><span style="color: #0088cc;"><span style="font-size: 10pt;">#65</span></span></a><span style="color: #333333;"><span style="font-size: 10pt;">.</span></span></p>\r\n<p class="western" style="margin-bottom: 0.24in; direction: ltr; color: #000000; line-height: 16.8667px; background: transparent; font-family: Arial, serif; font-size: 11pt; border: none; padding: 0in;"><span style="color: #333333;"><span style="font-size: 10pt;"><b>IP Detective Deprecation: </b></span></span><span style="color: #333333;"><span style="font-size: 10pt;">New method of review validations based on hCaptcha and CleanTalk / October - November.</span></span></p>', NULL, 1, 1026, 2, 628, '2020-10-04 10:35:24', 628, '2020-10-04 14:57:25', 0, '0000-00-00 00:00:00'),
	(1341, 0, 15, 'Webmasters Team Report - September, 2020', 'webmasters-team-report-september-2020', '<p><strong>Information:</strong><br />The webmasters team mainly consists of leaders and liaisons from other teams, with the purpose to effectively work together on all aspects of our properties and infrastructure.<br />The team meets as needed, because managing the properties is a daily task and communication is done through Glip, Mails and ADoodle.<br /><br />Our reports will include general information, actions performed and motions taken over a period of time.<br />The reports will not be about just one meeting which is planned every month like most teams.<br /><br /></p>\r\n<h2><strong>General:</strong></h2>\r\n<ul>\r\n<li>We are supporting the Events department in creating a new JUG Directory and Event Directory within the Community website.<a href="https://extensions.joomla.org/vulnerable-extensions/about/"><br /><br /></a></li>\r\n<li>We are supporting the Legal &amp; Finance department in improving the Partners and Sponsors information on JOrg and Community website.<br /><br /></li>\r\n<li>A new and improved Service Providers Directory is being designed (together with Legal) to consume the Resources Listing website and provide more options for the Joomla Service Providers in the world.<br /><br /></li>\r\n<li>Tobias Zulauf is creating a special GitDeploy plugin to automatically deploy changes from GitHub to our sites.<br /><a href="https://github.com/zero-24/plg_system_gitdeploy">https://github.com/zero-24/plg_system_gitdeploy</a></li>\r\n</ul>\r\n<h2><strong>Improvements / tasks / actions:</strong></h2>\r\n<ul>\r\n<li>Supporting Hugh (Legal), to define, configure and test the Advertisement structure based on BuySellAds as decided by the board.<br /><br /></li>\r\n<li>The “Identity Access Management” policy which is required according to the Joomla Property Management article 8 (https://www.opensourcematters.org/organisation/directors/policies/297-joomla-property-management-policy.html)&nbsp; has been created and a motion will be conducted.<br /><br /></li>\r\n<li>The "Privacy &amp; Security Settings" policy has been created and is being finalized depending on other topics.<br /><br /></li>\r\n<li>A htaccess baseline has been created to be used on the Joomla based properties (websites). <a href="https://github.com/joomla/joomla-websites/blob/master/configuration-baselines/.htaccess-joomla">https://github.com/joomla/joomla-websites/blob/master/configuration-baselines/.htaccess-joomla</a> <br /><br /></li>\r\n<li>The htaccess baseline and currently used version on the following websites are merged.<br />Magazine, Community, Foundation, Certification and OSM.</li>\r\n</ul>', NULL, 1, 1027, 3, 2198, '2020-09-30 23:50:49', 2198, '2020-10-04 11:55:17', 0, '0000-00-00 00:00:00'),
	(1342, 0, 78, 'Certification Program: team meeting - September 2020', 'certification-program-team-meeting-september-2020', '<p>The meeting started September 30th 2020 at 17:00 (UTC).</p>\r\n<h3 style="font-family: \'Helvetica Neue\', Helvetica, Arial, sans-serif; color: #333333;">Particpants:</h3>\r\n<p><em>Attendees:</em><span style="caret-color: auto;"></span><span style="caret-color: auto;">Andrea Gentil, Jeroen Moolenschot and Chris Keen.</span></p>\r\n<p><em>Guest:</em>&nbsp;Jaz Parkyn (Dept. Co-ordinator) - online<br /><br /><em>Absent with apologies:&nbsp;</em><span style="caret-color: auto;"></span><span style="caret-color: auto;">Luca Marzo</span></p>\r\n<h2 style="font-family: \'Helvetica Neue\', Helvetica, Arial, sans-serif; color: #333333;">General points</h2>\r\n<p><span id="docs-internal-guid-db13cfb0-7fff-1e7f-2d9c-50751520fef3"><span style="font-size: 11pt; font-family: Roboto, sans-serif; color: #000000; background-color: transparent; font-weight: bold; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;">Exam sessions:<br /><br /><span style="font-size: 14.6667px; font-weight: 400;">There was an exam held on </span></span></span>September 11th 2020 in Zurich, Switzerland. There were 9 candidates&nbsp; all of which took the German language version of the exam. Seven of the nine candidates passed.&nbsp;<br /><span><span style="font-size: 11pt; font-family: Roboto, sans-serif; color: #000000; background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;"><span id="docs-internal-guid-384ff348-7fff-b2c0-8131-af9f5ed59744"></span><br />There is one exam confirmed in the upcoming period.</span></span></p>\r\n<ul>\r\n<li>October 16th 2020&nbsp; -&nbsp; Chicago, USA.<span style="caret-color: auto;">(number of candidates not yet known).</span><span style="caret-color: auto;"></span></li>\r\n</ul>\r\n<p><span id="docs-internal-guid-db13cfb0-7fff-1e7f-2d9c-50751520fef3"><span style="font-size: 11pt; font-family: Roboto, sans-serif; color: #000000; background-color: transparent; font-weight: bold; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;">Website and Exam platform<br /></span></span><span id="docs-internal-guid-fa6f5df6-7fff-6772-8e79-e390e9ee64f5"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;">We have identified and fixed the blocking issue with our new question management system. </span></span><span style="color: #000000; font-family: Arial; font-size: 14.6667px; white-space: pre-wrap; caret-color: auto;">The issue with the exam platform itself has also been identified and we are working with TechJoomla to remedy this as soon as possible.<br /></span><span style="color: #000000; font-family: Arial; font-size: 14.6667px; white-space: pre-wrap; caret-color: auto;"><span style="color: #000000; font-family: Roboto, sans-serif; font-size: 14.6667px; font-weight: bold; white-space: pre-wrap;"><br />Operations and Marketing</span><span style="color: #000000; font-family: Arial;"><span style="font-size: 14.6667px; white-space: pre-wrap;"><br />We also updated the Certification FAQs to include information about the exam second chance initiative which gives candidates a discount on their second attempt. ( Point 7 - What happens if I don\'t pass the exam ?)</span></span><br /><span style="color: #000000; font-family: Arial;"><span style="font-size: 14.6667px; white-space: pre-wrap;"><a href="https://certification.joomla.org/faq">https://certification.joomla.org/faq</a></span></span></span></p>\r\n<p><span style="color: #000000; font-family: Arial; font-size: 14.6667px; white-space: pre-wrap; caret-color: auto;"><span id="docs-internal-guid-db13cfb0-7fff-1e7f-2d9c-50751520fef3" style="color: #333333; font-family: \'Helvetica Neue\', Helvetica, Arial, sans-serif; font-size: 14px; white-space: normal;"><span style="font-size: 11pt; font-family: Roboto, sans-serif; color: #000000; background-color: transparent; font-weight: bold; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;">J!4 exam(s)<br /></span></span><span id="docs-internal-guid-fa6f5df6-7fff-6772-8e79-e390e9ee64f5" style="color: #333333; font-family: \'Helvetica Neue\', Helvetica, Arial, sans-serif; font-size: 14px; white-space: normal;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;">We are now starting preparations for the J!4 exam(s). The J!4 Administrator Exam should be available approximately 6 months after the J!4 release date, this gives the JCP team enough time to complete the questions and answers based on the released version (including any initial bug fixes). This period will also give users sufficient time to use and learn J!4 and to prepare for the exam.<br /></span></span>We will be shortly looking for volunteers to help us (re)write the Administrator exams Q&amp;A\'s, if you\'re interested please contact us!</span></p>\r\n<p><strong>Next meeting:&nbsp;</strong>will be held on October 28th, 2020<strong>.</strong></p>', NULL, 1, 1028, 3, 1093, '2020-10-05 18:04:28', 1093, '2020-10-05 18:48:38', 0, '0000-00-00 00:00:00'),
	(1343, 0, 2, 'Volunteers Portal - October 2020 report', 'volunteers-portal-october-report', '<p>A status update about the Volunteers Portal activity&nbsp;last month:</p>\r\n<h3>Github Issues</h3>\r\n<p>The Volunteers Portal has a public repository and issue tracker on&nbsp;<a href="https://github.com/joomla/volunteers.joomla.org">https://github.com/joomla/volunteers.joomla.org</a>. The following issues have been worked on this month:</p>\r\n<ul>\r\n<li>#248&nbsp;<a href="https://github.com/joomla/volunteers.joomla.org/issues/248">Remove non confirmed user from the "Add Joomler" list&nbsp;</a>: fixed, list no longer shows Private Profiles</li>\r\n<li>#240&nbsp;<a href="https://github.com/joomla/volunteers.joomla.org/issues/240">Mark volunteers profile when deceased</a>&nbsp;: won\'t fix, as this is handled by consent expirations</li>\r\n<li>#194&nbsp;<a href="https://github.com/joomla/volunteers.joomla.org/issues/194">[jvols] use copy to clipboard plugin for banners [enhancement]</a>: implemented, thanks for the plugin Carlos</li>\r\n<li>#1485&nbsp;<a href="https://github.com/joomla/joomla-websites/issues/1485">[JVP] - address checkbox state lost after IDP migration</a>: research on issue, needs additional changes on the Identity Portal first</li>\r\n<li>#1534 <a href="https://github.com/joomla/joomla-websites/issues/1534">[JVP] Allow the Joomler to decide what should be shown</a>: needs a OSM decision&nbsp;/ clarification first</li>\r\n<li>#1533 <a href="https://github.com/joomla/joomla-websites/issues/1533">[JVP] Volunteers Portal - Guest team members</a>: the following changes have been made to the Volunteers Portal to address this issue:\r\n<ul>\r\n<li>Joomlers page no longer displays "Private Profiles" <a href="joomlers">https://volunteers.joomla.org/joomlers</a>&nbsp;</li>\r\n<li>Teams page no longer displays "Private Profiles" <a href="teams">https://volunteers.joomla.org/teams</a>&nbsp;</li>\r\n<li>On the team page itself, the "Private Profiles" are listed for the record (<strong>and team leaders to check - and set end dates if needed to update your teams</strong>)</li>\r\n<li>Profiles without consents are no longer displayed as "Guest" on the Volunteers Portal, instead "Private Profile" is used</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<p>Thanks for those reported issues and provided input. Please feel free to report issues, suggest features, or even work on the issues listed in the repository. Let me know if you need help to get started!</p>\r\n<h3>Other updates</h3>\r\n<ul>\r\n<li>Spam reports handled (2 accounts)</li>\r\n<li>Joomla &amp; extensions updated&nbsp;</li>\r\n</ul>', NULL, 1, 1029, 3, 430, '2020-10-07 18:21:48', 430, '2020-12-22 17:58:54', 0, '0000-00-00 00:00:00'),
	(1344, 0, 150, 'Educational Outreach Working Group Meeting - September 26, 2020', 'eowg-9-2020', '<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 3pt;"><span style="font-size: 26pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Joomla Educational Outreach</span></p>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 16pt;"><span style="font-size: 15pt; font-family: Arial; color: #666666; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Conversation update and sync - Meeting Notes</span><span style="font-size: 15pt; font-family: Arial; color: #666666; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;"><br /></span><span style="font-size: 15pt; font-family: Arial; color: #666666; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">September 26, 2020 - 16:00 UTC</span></p>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">In attendance: AK Shehu, Chris Keen, Todd Woodward, Shirielle “SD” Williams</span></p>\r\n<p><b id="docs-internal-guid-9e770ec2-7fff-9f28-bccd-7c4e31e7b23e" style="font-weight: normal;">&nbsp;</b></p>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Apologies: Laura Gordon</span><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;"><br /><br /></span></p>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Absent: Daniel Dubois, Brian Mitchell, Brian Ronnow, Elisa Foltyn, Holger Kremers, Robbie Adair, Rowan Hoskyns Abrahal, Marco Dings, Daniel Dubois</span></p>\r\n<p><b style="font-weight: normal;">&nbsp;</b></p>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Invited Guests: </span><span style="font-size: 11.5pt; font-family: Arial; color: #2f2f2f; background-color: #ffffff; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Ruchika Nagarkar, Benjamin Trenkle</span></p>\r\n<h3 dir="ltr" style="line-height: 1.38; margin-top: 16pt; margin-bottom: 4pt;"><span style="font-size: 13.999999999999998pt; font-family: Arial; color: #434343; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Summary:</span></h3>\r\n<p dir="ltr" style="line-height: 1.38; margin-left: 36pt; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">The meeting focused on understanding the proposed Joomla Student Club with guests Ruchika Nagarkar and Benjamin Trenkle.</span></p>\r\n<h2 dir="ltr" style="line-height: 1.38; margin-top: 18pt; margin-bottom: 6pt;"><span style="font-size: 16pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Topics Discussed</span></h2>\r\n<ol style="margin-top: 0; margin-bottom: 0;">\r\n<li dir="ltr" style="list-style-type: decimal; font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre;">\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Joomla Community Magazine (JCM)&nbsp;</span></p>\r\n<ul style="margin-top: 0; margin-bottom: 0;">\r\n<li dir="ltr" style="list-style-type: circle; font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre;">\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">&nbsp;AK will submit for October edition&nbsp;</span></p>\r\n</li>\r\n</ul>\r\n</li>\r\n<li dir="ltr" style="list-style-type: decimal; font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre;">\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Joomla Student Club</span></p>\r\n<ul style="margin-top: 0; margin-bottom: 0;">\r\n<li dir="ltr" style="list-style-type: circle; font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre;">\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Campus Clubs with Joomla Advocates trained by JEO to mentor on-campus student volunteers</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: circle; font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre;">\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Encourage student volunteers</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: circle; font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre;">\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">One path could be to teach Joomla Coding Standards for developers, other paths should be offered as well</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: circle; font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre;">\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">An onboarding process needs as well as ongoing support&nbsp; and retention of volunteers</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: circle; font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre;">\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Purposeful stretch goals to achieve milestones for example to complete a milestone, present at a JoomlaDay event</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: circle; font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre;">\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Collaboration needed between Volunteer Engagement Team (VET), JEO, etc</span></p>\r\n</li>\r\n</ul>\r\n</li>\r\n</ol>\r\n<h2 dir="ltr" style="line-height: 1.38; margin-top: 18pt; margin-bottom: 6pt;"><span style="font-size: 16pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Action Items&nbsp;</span></h2>\r\n<ul style="margin-top: 0; margin-bottom: 0;">\r\n<li dir="ltr" style="list-style-type: disc; font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre;">\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Todd: Share document compilation of the mission feedback from the group</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre;">\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Chris: Work with Benjamin and Ruchika to further outline the idea so that JEO and provide feedback on how they can help</span></p>\r\n</li>\r\n</ul>\r\n<h2 dir="ltr" style="line-height: 1.38; margin-top: 18pt; margin-bottom: 6pt;"><span style="font-size: 16pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Next Meeting:&nbsp;</span></h2>\r\n<ul style="margin-top: 0; margin-bottom: 0;">\r\n<li dir="ltr" style="list-style-type: disc; font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre;">\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Working Group Mission Statement&nbsp;</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre;">\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Review the rough outline for the Joomla Student Club and discuss how can help&nbsp;</span></p>\r\n</li>\r\n</ul>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;"></span></p>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Working Group Meeting</span></p>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Friday, October 23, 2020 at 16:00 UTC via Google Meet.&nbsp;</span></p>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Invite will be sent to team distribution and posted in Glip.</span></p>\r\n<p>&nbsp;</p>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Add items to the </span><a href="https://drive.google.com/open?id=1T_8n4f3UPqqFF8JetZ5KvLTUudpLyroYQ_GHkxOwj1w" style="text-decoration: none;"><span style="font-size: 11pt; font-family: Arial; color: #1155cc; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: underline; -webkit-text-decoration-skip: none; text-decoration-skip-ink: none; vertical-align: baseline; white-space: pre-wrap;">Agenda Document</span></a><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">&nbsp; by Wednesday, October 21st.</span></p>\r\n<p><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;"></span></p>', NULL, 1, 1030, 1, 1715, '2020-09-26 17:21:02', 0, '2020-10-08 13:23:25', 0, '0000-00-00 00:00:00'),
	(1345, 0, 120, 'Privacy Compliance Team Meeting October 14, 2020', 'privacy-compliance-team-meeting-october-14-2020', '<p style="overflow-wrap: break-word;">This meeting of the Joomla! Privacy Compliance Team has been held on Glip on October 14, 2020 at 16:30 CET.&nbsp;</p>\r\n<p style="overflow-wrap: break-word;"><strong>Participants&nbsp;</strong></p>\r\n<p style="overflow-wrap: break-word;">In attendance:&nbsp;</p>\r\n<ul>\r\n<li>Donata Kalnenaite&nbsp;</li>\r\n<li>Luca Marzo</li>\r\n<li>Roland Dalmulder&nbsp;</li>\r\n<li>Sander Potjer</li>\r\n<li>Sandra Decoux&nbsp;</li>\r\n<li>Stasys Svabas</li>\r\n</ul>\r\n<p style="overflow-wrap: break-word;"><strong>Discussion Outline&nbsp;</strong></p>\r\n<ul>\r\n<li><span style="caret-color: auto;">The team welcomed a new member - Stasys Svabas;&nbsp;</span></li>\r\n<li><span style="caret-color: auto;">Donata sent Stasys some documentation on the team\'s outstanding tasks and encouraged Stasys to write an article for the Joomla Community Magazine;&nbsp;</span></li>\r\n<li><span style="caret-color: auto;">The team discussed and agreed that the Cookie Policy has been finalized;&nbsp;</span></li>\r\n<li><span style="caret-color: auto;">The team discussed the Cookie Script and the fact that we are still looking for a new team member that could help us with this task. Stasys offered to help with the organization of the documents and tasks for the Cookie Script;&nbsp;</span></li>\r\n<li><span style="caret-color: auto;">Luca sent Donata a list of all of the countries in which our users are located. Donata will review the list and help in determining what privacy laws we need to comply with;&nbsp;</span></li>\r\n<li>Luca is still working on the consents analysis;&nbsp;</li>\r\n<li>Roland is working on the "consent to all" button;&nbsp;</li>\r\n<li>On the Volunteers Portal, Sander has made changes for the "guest users" Issue. On the Identity Portal, Sander has updated the profile view and edit page layout to use sliders like for the consents, as requested by users.&nbsp;</li>\r\n</ul>', NULL, 1, 1031, 1, 6279, '2020-10-14 17:56:48', 0, '2020-10-14 18:00:05', 0, '0000-00-00 00:00:00'),
	(1346, 51, 0, 'Production Dept. Meeting MINUTES - August 25, 2020', 'production-dept-meeting-minutes-august-25-2020', '<h1 style="text-align: right;">Production Dept. Meeting MINUTES <br />- August 25, 2020</h1>\r\n<p style="text-align: right;">1900 CEST, UTC+2</p>\r\n<p><strong>Participants:</strong> Benjamin Trenkle (Team Leader), Carlos Cámara (Team Leader), Hannes Papenberg (Team Leader), Harald Leithner (Release Lead 3.9), Mike Brandner (Team Leader), Llewellyn van der Merwe (Assistant Department Coordinator), Niels Braczek (Team Leader), Philip Walton (Team Leader), Robert Deutz (Team Leader),<br /> <strong>Absent with notice:</strong> David Jardin (Team Leader), Marco Dings (Department Coordinator), Tobias Zulauf (Release Lead 3.10)<br /><strong>Absent without notice: </strong>George Wilson (Release Lead 4.0), Nicola Galgano (Team Leader),&nbsp; Ilagnayeru Manickam (Team Leader)</p>\r\n<h2>Agenda Items &amp; minutes</h2>\r\n<hr />\r\n<h3>Joomla <a href="teams/accessibility-team">Accessibility Team</a> (Carlos Cámara Mora)</h3>\r\n<h4>Talk Topics:</h4>\r\n<ol>\r\n<li>New article in the Magazine about A11y: <a href="https://magazine.joomla.org/all-issues/august-2020/accessible-resources-for-joomla-3-x">https://magazine.joomla.org/all-issues/august-2020/accessible-resources-for-joomla-3-x</a> this is the kick off about a series of articles produced by the Joomla Accessibility Team, to highlight the resources immediately available to develop an accessible site in Joomla! 3, in the next issue Donato and Vito will prepare new articles on the Joomla! 3.0 accessibility resources</li>\r\n<li>Reviewing A11y issues on the media manager to pass to Benjamin Trenkle</li>\r\n<li>Requested all members to renew consent at identity portal</li>\r\n</ol><hr />\r\n<h3><a href="teams/automated-tests-working-group">Automated Testing Team</a> (Hannes Papenberg)</h3>\r\n<h4>Updates/Proposed Topics:</h4>\r\n<ol>\r\n<li>Switching from Travis to Drone is progressing VERY slowly:\r\n<ul>\r\n<li>Hi, a quick update from my side. As I\'ve been bitching about consolidating our CI systems, I got Harald, Robert and me to start migrating the tasks from Jenkins to Drone. We dove into the issue and I learned after some time that I\'m not exactly the right person to do this, since I would need quite a lot of access to different systems which I currently don\'t have and don\'t really want to have. So I looked at migrating repos from Travis to Drone instead, while Harald and Robert worked on Jenkins.</li>\r\n<li>Turns out I dove down a rabbit hole. I thought I might want to start with the framework project, since the repos in theory should be pretty identical and after setting up one, it should be quick to adapt all the others. When I think something this stupid next time, please someone hit me... It starts with the issue that we don\'t have working docker images for PHP &lt;7.2 and even above that, the images weren\'t really good. It is also not easy to get a working PHP 5.3 or 5.4 image to build properly nowadays. But I can proudly say that I successfully built images for all PHP versions from 5.3 to 8.0, which all include all extensions which we require, as well as composer and PHPUnit.</li>\r\n<li>I then also started creating a configuration for the application repo. That became ... interesting ... rather quickly, since indeed the drone configuration is a lot more verbose than the travis configuration. (Something around 10 times the line count.) However that configuration also contains a great speed boost by having a composer cache and it contains phpcs, phpmd, phpstan, phploc and phpcpd. I rather like the configuration that I came up with here. Since 400 lines of drone configuration is not really easy to read, I used the meta-configuration language that they support, JSONNET, which "compresses" this down to a 140 lines... The PR can be found here:<a href="https://github.com/joomla-framework/application/pull/89/files"> </a><a href="https://github.com/joomla-framework/application/pull/89/files">https://github.com/joomla-framework/application/pull/89/files</a></li>\r\n<li>And the result of that configuration as a build of drone can be found here:<a href="https://ci.joomla.org/joomla-framework/application/17"> </a><a href="https://ci.joomla.org/joomla-framework/application/17">https://ci.joomla.org/joomla-framework/application/17</a></li>\r\n<li>If you look at the build, you see a few things:<ol>\r\n<li>phpmd, phpstan (and if you were to run it on a few other repos, also phpcpd) do throw a bunch of errors/warnings about "bad" code. There are a few legitimate reports among these.</li>\r\n<li>PHP8 isn\'t working.</li>\r\n</ol></li>\r\n<li>Especially the later is a real issue. The error reported in the PHP8 build is something that we have fixed for 4.0 with a nasty patch, but even when you fix that here, there are dozens of other issues in nearly every package here, which prevent this from running on PHP8. Since the CMS is depending on these packages, this means that</li>\r\n<li>!!!JOOMLA WONT RUN ON PHP8 ANY TIME SOON!!!</li>\r\n<li>Going through some of the repos, I stumbled over differences here and there, one important one: Some of the repos use<a href="http://scrutinizer-ci.org/"> scrutinizer-ci.org</a>. For example<a href="https://scrutinizer-ci.com/g/joomla-framework/view/?branch=2.0-dev"> </a><a href="https://scrutinizer-ci.com/g/joomla-framework/view/?branch=2.0-dev">https://scrutinizer-ci.com/g/joomla-framework/view/?branch=2.0-dev</a> I actually have no idea what the big benefit of this is. In my opinion it doesn\'t really provide anything more valuable than what we already have with the above mentioned tools now available for example for the application package. Should we keep this? Who has the credentials for this account?</li>\r\n<li>Do we need this service, especially when we have a website like<a href="https://framework.joomla.org/"> </a><a href="https://framework.joomla.org">https://framework.joomla.org</a>, which provides a bunch of the necessary info? In any case, when we want to switch from Travis to Drone, we need to update that site as well to show the status of Drone instead of Travis. So I tried installing it locally to play around with it, following the instructions in the readme. Turns out that this fails right now. I\'ve not debugged this yet, but the database setup fails, which means that we should really make sure that we have a working backup of that site, because we can\'t quickly set it up again from the repo. (Maybe it works in another setup than mine, but my confidence in this is rather low, when it doesn\'t work on my system...)</li>\r\n<li>The framework website is good, but maybe we could extend it a little bit, while we are at it. Maybe upload the phploc results, so that we have the same as<a href="http://scrutinizer-ci.org/"> scrutinizer-ci.org</a>... And when you look at the repo, you see a PR from Michael, where he seems to have done quite a bit of work to pull in the documentation of each package into the site. I\'m not sure what still needs to be done and/or how far this is along.<a href="https://github.com/joomla/framework.joomla.org/pull/46"> </a><a href="https://github.com/joomla/framework.joomla.org/pull/46">https://github.com/joomla/framework.joomla.org/pull/46</a> But it looks like something that we really want to have in the site and should push for adopting.</li>\r\n<li>Why am I writing all of this? I already invested 2 weeks into getting where I am and a lot still has to be done:\r\n<ul>\r\n<li>Create drone configurations based on the application packages example I already did for all version 1 of the 44 framework repos.</li>\r\n<li>Do that all again for the version 2 branches of the frameworks.</li>\r\n<li>Fix all the issues for PHP8 in all 88 branches... (Just so that we have a chance of getting 3.x running on PHP8)</li>\r\n<li>Drop<a href="http://scrutinizer-ci.org/"> scrutinizer-ci.org</a>?</li>\r\n<li>Fix<a href="http://framework.joomla.org/"> framework.joomla.org</a> setup</li>\r\n<li>Adopt the PR from Michael and</li>\r\n<li>Maybe add the metrics from phploc to<a href="http://framework.joomla.org/"> framework.joomla.org</a> as well</li>\r\n<li>I won’t be able to get this all done myself and was hoping that we could find a way to get more people involved...</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n</li>\r\n</ol><hr />\r\n<h3><a href="teams/software-architecture-strategy-working-group">Software Architecture &amp; Strategy</a> Team (Niels Braczek)</h3>\r\n<h4>Talk Topics:</h4>\r\n<ol>\r\n<li>Still waiting for the RfC-RfC motion.</li>\r\n</ol><hr />\r\n<h3><a href="teams/cms-release-team">CMS Release</a> Team (Philip Walton)</h3>\r\n<h4>Talk Topics:</h4>\r\n<ol>\r\n<li>Dev Twitter account<ol>\r\n<li>[NB] We can use this channel for communication about RfCs.</li>\r\n<li>[LvdM] CMS to post requests to get testing done.</li>\r\n</ol></li>\r\n</ol><ol>\r\n<li>Yoursites use by team<ol>\r\n<li>Check out the partner program</li>\r\n</ol></li>\r\n</ol>\r\n<h4>Updates/Proposed Topics:</h4>\r\n<ol>\r\n<li>Release today (3.9.21) so far no issues reported. Extensive testing by the team</li>\r\n</ol>', NULL, 1, 1032, 3, 1755, '2020-10-17 19:36:07', 1755, '2021-02-02 16:57:44', 0, '0000-00-00 00:00:00'),
	(1347, 51, 0, 'Production Dept. Meeting MINUTES September 8, 2020', 'production-dept-meeting-minutes-september-8-2020', '<h2 style="text-align: right;">Production Dept. Meeting MINUTE</h2>\r\n<h2 style="text-align: right;">- September 08, 2020</h2>\r\n<p style="text-align: right;">1900 CEST, UTC+2</p>\r\n<p><strong>Participants:</strong> Benjamin Trenkle (Team Leader), Carlos Cámara (Team Leader), David Jardin (Team Leader), Hannes Papenberg (Team Leader), Marco Dings (Department Coordinator), Mike Brandner (Team Leader), Llewellyn van der Merwe (Assistant Department Coordinator), Nicola Galgano (Team Leader), Niels Braczek (Team Leader), Philip Walton (Team Leader), Tobias Zulauf (Release Lead 3.10),<br /> <strong>Absent with notice:</strong> George Wilson (Release Lead 4.0), Robert Deutz (Team Leader), Harald Leithner (Release Lead 3.9)<br /> <strong>Absent without notice:</strong> Ilagnayeru Manickam (Team Leader)</p>\r\n<h2>Agenda Items &amp; minutes</h2>\r\n<hr />\r\n<h3><a href="departments/production">Production Department</a> Coordinator (Marco Dings)</h3>\r\n<h4>Talk Topics:</h4>\r\n<ol>\r\n<li>Recruiting of maintainers for sub-projects/repos [Hannes]</li>\r\n</ol><hr />\r\n<h3>Joomla <a href="teams/accessibility-team">Accessibility Team</a> (Carlos Cámara Mora)</h3>\r\n<h4>Talk Topics:</h4>\r\n<ol>\r\n<li>Removed all subgroups from Volunteers Portal</li>\r\n<li>Added Hans as full rights member</li>\r\n<li>Recovered access to github Joomla Accessibility Team (Thanks Robert!!)</li>\r\n<li>We keep working in several issues</li>\r\n</ol><hr />\r\n<h3>Joomla! <a href="teams/joomla-enhancement-development-team">Enhancement Development Team</a> (Benjamin Trenkle)</h3>\r\n<h4>Talk Topics:</h4>\r\n<ol>\r\n<li>Trying to get teams set up for each point on the roadmap</li>\r\n<li>Roadmap brought more new potential volunteers if interested to join, contact: <a href="teams/joomla-enhancement-development-team">https://volunteers.joomla.org/teams/joomla-enhancement-development-team</a></li>\r\n</ol><hr />\r\n<h3><a href="teams/security-strike-team">Security Strike Team</a> (David Jardin)</h3>\r\n<h4>Talk Topics:</h4>\r\n<ol>\r\n<li>Where to host backported packages?</li>\r\n</ol><hr />\r\n<h3><a href="teams/automated-tests-working-group">Automated Testing Team</a> (Hannes Papenberg)</h3>\r\n<h4>Talk Topics:</h4>\r\n<ol>\r\n<li>API-docs, framework website: What is the future?</li>\r\n</ol>\r\n<h4>Updates/Proposed Topics:</h4>\r\n<ol>\r\n<li>joomla/joomla-cms has been fully migrated to Drone.</li>\r\n<li>Several repos of joomla-framework have PRs to migrate the master branch to Drone.</li>\r\n</ol><hr />\r\n<h3>Releaseleads (George Wilson, Tobias Zulauf Harald Leithner)</h3>\r\n<h4>* Releaselead 3.10 (Tobias Zulauf):</h4>\r\n<ol>\r\n<li>The upgrade paths from 3.10-alpha1 to 4.0-beta4-dev as well as any newer 4.0-beta have been broken by the child template feature merged.</li>\r\n<li>A workaround has been implemented into 3.10-alpha2-dev onwards so upgrades from 3.10-alpha2(-dev) to 4.0-beta4(-dev) are possible again as well as 3.10-alpha1 to 3.10-alpha2 to 4.0-beta4.</li>\r\n<li>We are in the preparations for an 3.10-alpha2 and 4.0-beta4 release on the 15.09.2020</li>\r\n</ol><hr />\r\n<h3><a href="teams/bug-squad">Bug Squad </a>(Nicola Galgano)</h3>\r\n<h4>Talk Topics:</h4>\r\n<ol>\r\n<li>Final decision on Allow sanitized SVG&nbsp; on 3.X <a href="https://github.com/joomla/joomla-cms/pull/29728">https://github.com/joomla/joomla-cms/pull/29728</a></li>\r\n</ol>\r\n<h4>Updates/Proposed Topics:</h4>\r\n<ol>\r\n<li>Point 2 of Other Business : <a href="https://github.com/joomla/joomla-cms/issues/30569">lessphp vs php ⅞</a></li>\r\n<li>We will dismiss the jbs twitter account</li>\r\n</ol><hr />\r\n<h3><a href="teams/software-architecture-strategy-working-group">Software Architecture &amp; Strategy</a> Team (Niels Braczek)</h3>\r\n<h4>Updates/Proposed Topics:</h4>\r\n<ol>\r\n<li>Agreed with marketing to concentrate on companies and corporations to start with. Later, developers/site builders should be addressed separately.</li>\r\n</ol><hr />\r\n<h3>Other Business</h3>\r\n<h4>Talk Topics:</h4>\r\n<ol>\r\n<li>[TZ]: I would like to archive the old and unused joomla-projects projects as they are not updated nor used but generate regular reports from GitHub about outdated dependencies. They can always be reopened when needed.</li>\r\n<li>[TZ]: Do we want to backport combat issues in libs that we ship in 3.x but that are no longer maintained? Example: <a href="https://github.com/joomla/joomla-cms/issues/30569#issue-693193984">https://github.com/joomla/joomla-cms/issues/30569#issue-693193984</a></li>\r\n<li>[TZ]: What is the plan on translations for weblinks and search in the joomla-extension org? There should be a setup with crowdin right? When this is the case anyone can translate where needed and on any new translation crowdin can send a PR to update the files similar to the patchtester.</li>\r\n</ol>', NULL, 1, 1033, 15, 1755, '2020-10-17 20:25:27', 1755, '2021-02-02 16:57:27', 0, '0000-00-00 00:00:00'),
	(1348, 51, 0, 'Production Dept. Meeting MINUTES - September 22, 2020', 'production-dept-meeting-minutes-september-22-2020', '<h1 style="text-align: right;">Production Dept. Meeting MINUTES<br />- September 22, 2020</h1>\r\n<p style="text-align: right;">1900 CEST, UTC+2</p>\r\n<p><strong>Participants:</strong> Benjamin Trenkle (Team Leader), Carlos Cámara (Team Leader), David Jardin (Team Leader), George Wilson (Release Lead 4.0), Harald Leithner (Release Lead 3.9), Llewellyn van der Merwe (Assistant Department Coordinator), Nicola Galgano (Team Leader), Philip Walton (Team Leader), Robert Deutz (Team Leader)<br /> <strong>Absent with notice:</strong> Hannes Papenberg (Team Leader), Tobias Zulauf (Release Lead 3.10)<br /><strong>Absent without notice:</strong> Ilagnayeru Manickam (Team Leader), Marco Dings (Department Coordinator), Mike Brandner (Team Leader), Niels Braczek (Team Leader)</p>\r\n<h2>Agenda Items &amp; minutes</h2>\r\n<hr />\r\n<h3><a href="departments/production">Production Department </a>Coordinator (Marco Dings)</h3>\r\n<h4>Updates/Proposed Topics:</h4>\r\n<ol>\r\n<li>Information about the state of the frontend template Working Group [RD]</li>\r\n</ol><hr />\r\n<h3>Joomla! <a href="teams/joomla-enhancement-development-team">Enhancement Development Team</a> (Benjamin Trenkle)</h3>\r\n<h4>Working groups</h4>\r\n<ul>\r\n<li>Multi Domain (JEDT - MD)<ol>\r\n<li>Concept is created</li>\r\n</ol></li>\r\n<li>Guided Tour (JEDT - GT)<ol>\r\n<li>Going probably frontend</li>\r\n</ol></li>\r\n<li>Frontend Template (JEDT - FT)<ol>\r\n<li>PR is coming soon</li>\r\n</ol></li>\r\n</ul>\r\n<hr />\r\n<h3>Joomla <a href="teams/accessibility-team">Accessibility Team</a> (Carlos Cámara Mora)</h3>\r\n<h4>Talk Topics:</h4>\r\n<ol>\r\n<li>Issues with the A11y plugin</li>\r\n<li>Discussing internally A11y best practices for social media posting&nbsp;</li>\r\n<li>New article in latest magazine issue</li>\r\n</ol><hr />\r\n<h3><a href="teams/security-strike-team">Security Strike Team</a> (David Jardin)</h3>\r\n<h4>Updates/Proposed Topics:</h4>\r\n<ol>\r\n<li>In discussion with MITRE about becoming a CNA to better manage CVE id’s</li>\r\n<li>Removed two inactive team members</li>\r\n</ol><hr />\r\n<h3><a href="teams/automated-tests-working-group">Automated Testing Team</a> (Hannes Papenberg)</h3>\r\n<h4>Updates/Proposed Topics:</h4>\r\n<ol>\r\n<li>Moving more and more framework packages to drone</li>\r\n</ol><hr />\r\n<h3>Releaseleads (George Wilson, Tobias Zulauf Harald Leithner)</h3>\r\n<h4>* Releaselead 4.0 (George Wilson)</h4>\r\n<ol>\r\n<li>4.0 Beta 4 released</li>\r\n<li>Continued working on component for Downloads site to allow J4 language packs to start being submitted. This is now functionally ready - just needs some work on how it routes/breadcrumbs into ARS</li>\r\n<li>Continued work going on with icons to try and future proof</li>\r\n<li>Issues largely stabilising. Taking this as a sign that things are improving and we can start to slowly work through release blockers towards a RC - Maybe think about targeting a first RC in a couple of months depending on how release blocker list starts to go down.</li>\r\n</ol>\r\n<h4>* Releaseleads 3.10 (Tobias Zulauf):</h4>\r\n<ol>\r\n<li>3.10-alpha2 released</li>\r\n</ol><hr />\r\n<h3><a href="teams/documentation-working-group">Documentation Team </a>(Mike Brandner)</h3>\r\n<h4>Updates/Proposed Topics:</h4>\r\n<ol>\r\n<li>Help screens for 4.0 being actively worked on</li>\r\n</ol><hr />\r\n<h3><a href="teams/bug-squad">Bug Squad </a>(Nicola Galgano)</h3>\r\n<h4>Talk Topics:</h4>\r\n<ol>\r\n<li>Response time for pull requests marked as RTC</li>\r\n</ol><hr />\r\n<h3><a href="teams/software-architecture-strategy-working-group">Software Architecture &amp; Strategy</a> Team (Niels Braczek)</h3>\r\n<h4>Talk Topics:</h4>\r\n<ol>\r\n<li>Waiting on feedback on RFC motion</li>\r\n</ol><hr />\r\n<h3><a href="teams/cms-release-team">CMS Release</a> Team (Philip Walton)</h3>\r\n<h4>Talk Topics:</h4>\r\n<ol>\r\n<li>Twitter account working. Its grown by to since using it 12 days ago 17 tweets <br />Tweet impressions 24.4K up 2,783.9% Profile visits 411 up 395.2% All ok with progress so far and is there anything else we should be pushing apart from releases which I will do</li>\r\n<li>Pushing out the components/modules etc that pass the compatibility test in 3.10 to spread the word</li>\r\n</ol>', NULL, 1, 1034, 2, 1755, '2020-10-17 22:28:27', 1755, '2021-02-02 16:57:10', 0, '0000-00-00 00:00:00'),
	(1349, 51, 0, 'Production Dept. Meeting MINUTES - October 06, 2020', 'production-dept-meeting-minutes-october-6-2020', '<h1 style="text-align: right;">Production Dept. Meeting MINUTES <br />- October&nbsp; 06, 2020</h1>\r\n<p style="text-align: right;">1900 CEST, UTC+2</p>\r\n<p><strong>Participants:</strong> Benjamin Trenkle (Team Leader), Carlos Cámara (Team Leader), George Wilson (Release Lead 4.0) Hannes Papenberg (Team Leader), Harald Leithner (Release Lead 3.9), Llewellyn van der Merwe (Assistant Department Coordinator), Nicola Galgano (Team Leader), Niels Braczek (Team Leader), Philip Walton (Team Leader), Robert Deutz (Team Leader), Tobias Zulauf (Release Lead 3.10),<br /> <strong>Absent with notice:</strong> David Jardin (Team Leader), Marco Dings (Department Coordinator)<br /> <strong>Absent without notice:</strong> Mike Brandner (Team Leader), Ilagnayeru Manickam (Team Leader)</p>\r\n<h2>Agenda Items &amp; minutes</h2>\r\n<hr />\r\n<h3><a href="departments/production">Production Department </a>Coordinator (Marco Dings)</h3>\r\n<h4>Talk Topics:</h4>\r\n<ol>\r\n<li>Google Analytics/Tag Manager, Scrutinizer, Greenkeeper, Pingdom: Remove? Keep? [Hannes]\r\n<ul>\r\n<li>Since the Google analytics/tag manager is a Board decision, a request needs to be made to the Board to remove it everywhere and replace it with a self-hosted, opensource, GDPR compliant solution. [Harald]</li>\r\n<li>Agreed to remove Scrutinizer, Pingdom, Greenkeeper.</li>\r\n</ul>\r\n</li>\r\n</ol><hr />\r\n<h3>Joomla <a href="teams/accessibility-team">Accessibility Team</a> (Carlos Cámara Mora)</h3>\r\n<h4>Updates/Proposed Topics:</h4>\r\n<ol>\r\n<li>Working on Cassiopeia accessibility: Some issues are related to Joomla core itself.</li>\r\n</ol><hr />\r\n<h3><a href="teams/automated-tests-working-group">Automated Testing Team</a> (Hannes Papenberg)</h3>\r\n<h4>Talk Topics:</h4>\r\n<ol>\r\n<li>We need a dedicated maintainer for the framework project.</li>\r\n</ol>\r\n<h4>Updates/Proposed Topics:</h4>\r\n<ol>\r\n<li>60 branches of joomla-framework have been converted to Drone, 10 are still to do. Help would be greatly appreciated.&nbsp;</li>\r\n<li>4 Repos in github.com/joomla are still not converted to Drone, several don’t have any CI at all.</li>\r\n<li><a href="https://framework.joomla.org">https://framework.joomla.org</a> has been moved to Drone badges</li>\r\n</ol><hr />\r\n<h3>Releaseleads (Benjamin Trenkle, George Wilson, Tobias Zulauf, Harald Leithner)</h3>\r\n<h4>* Release lead 4.1 (Benjamin Trenkle)</h4>\r\n<ol>\r\n<li>Motion on Benjamin Trenkle as new Release Lead for Joomla! 4.1<br />Proposed by Harald Leithner, Seconded: Robert Deutz\r\n<ul>\r\n<li>Accepted</li>\r\n</ul>\r\n</li>\r\n</ol>\r\n<h4>* Release lead 4.0 (George Wilson)</h4>\r\n<ol>\r\n<li>No major changes since last meeting. Numbers of issues are still relatively stable and will have conversations with Tobias to go through current release blockers</li>\r\n</ol>\r\n<h4>* Releaseleads 3.10 (Tobias Zulauf):</h4>\r\n<ol>\r\n<li>Right now there are discussions about how to improve the pre upgrade checker and com_joomlaupdate regarding not compatible system plugins that might brick the upgrade:<br /><a href="https://github.com/joomla/joomla-cms/issues/30930">https://github.com/joomla/joomla-cms/issues/30930</a><br /><a href="https://github.com/joomla/joomla-cms/issues/30913">https://github.com/joomla/joomla-cms/issues/30913</a></li>\r\n<li>The PR queue for 3.10-dev has been cleaned up. Only 5 PRs are still open, where three need tests and the other two are WIP right now: <a href="https://github.com/joomla/joomla-cms/pulls?q=is%3Aopen+is%3Apr+label%3APR-3.10-dev">https://github.com/joomla/joomla-cms/pulls?q=is%3Aopen+is%3Apr+label%3APR-3.10-dev</a></li>\r\n</ol>\r\n<h4>* Releaseleads 3.9 (Harald Leithner):</h4>\r\n<ol>\r\n<li>Joomla! 3.9.22 released today.</li>\r\n</ol><hr />\r\n<h3><a href="teams/bug-squad">Bug Squad </a>(Nicola Galgano)</h3>\r\n<h4>Talk Topics:</h4>\r\n<ol>\r\n<li>RFC <a href="https://github.com/joomla/joomla-cms/issues/30815#">accessibility checker</a> for 4.1 <a href="https://github.com/joomla/joomla-cms/issues/30815">https://github.com/joomla/joomla-cms/issues/30815</a></li>\r\n</ol><hr />\r\n<h3><a href="teams/software-architecture-strategy-working-group">Software Architecture &amp; Strategy</a> Team (Niels Braczek)</h3>\r\n<h4>Updates/Proposed Topics:</h4>\r\n<ol>\r\n<li>Still no motion about the RfC-RfC…</li>\r\n<li>Still need a repo for the external framework review...</li>\r\n</ol><hr />\r\n<h3><a href="teams/cms-release-team">CMS Release</a> Team (Philip Walton)</h3>\r\n<h4>Talk Topics:</h4>\r\n<ol>\r\n<li>Work and thought needed to make the upgrade path smoother <a href="https://github.com/joomla/joomla-cms/issues/30930">https://github.com/joomla/joomla-cms/issues/30930</a></li>\r\n</ol>\r\n<h4>Updates/Proposed Topics:</h4>\r\n<ol>\r\n<li>J3.9.22 released and so far so good.</li>\r\n</ol><hr />\r\n<h3>Other Business</h3>\r\n<h4>Talk Topics:</h4>\r\n<ol>\r\n<li>PBF coming in 11 days, please join</li>\r\n<li>Motion: What should happen with abandoned joomla dependencies? (Tobias)</li>\r\n</ol>', NULL, 1, 1035, 4, 1755, '2020-10-17 22:30:28', 1755, '2021-02-02 16:56:53', 0, '0000-00-00 00:00:00'),
	(1350, 58, 0, 'OSM, Inc. - Full Board Meeting - October 08, 2020', 'osm-inc-full-board-meeting-october-08-2020', '<h2 id="docs-internal-guid-ba20ae3b-7fff-0445-54d7-aeff81494ce8" dir="ltr">Open Source Matters, Inc.<br />Full Board of Directors and Officers Meeting</h2>\r\n<p dir="ltr">Date: October 08, 2020<br />Time: 17:00 UTC<br />Total time of meeting: 1 hour 35 minutes</p>\r\n<h4 dir="ltr">Attendees:</h4>\r\n<p dir="ltr">Brian Mitchell (President), Justine Ayebale Abunyanga (Vice President), Luca Marzo (Secretary), Radek Suski (Treasurer), Marco Dings (Director), Hugh Douglas-Smith (Director), David Opati Aswani (Director), Jaz Parkyn (Director), Marianela Queme (Director).</p>\r\n<h4 dir="ltr">Apologies:</h4>\r\n<p dir="ltr">-</p>\r\n<h4 dir="ltr">Proxies:</h4>\r\n<p dir="ltr">-</p>\r\n<h2 dir="ltr">Discussion outline</h2>\r\n<h3 dir="ltr">Treasurer</h3>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">The onboarding process of Radek as Treasurer is still in progress.&nbsp;</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">The onboarding process for the bank account can be done without travelling to the USA, even though it requires a lot of paperwork.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Bank Statements are current through the end of September 2020 and are available in drive for board review.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Radek shared the bank balances as of today.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">D&amp;O Insurance renewal has been paid.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Venable invoices have been paid.</p>\r\n</li>\r\n</ul>\r\n<h3 dir="ltr">Production Department</h3>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Benjamin Trenkle volunteered as Joomla 4.1 Release Lead and motion was passed to accept his appointment.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation"><a href="https://www.joomla.org/announcements/release-news/5824-joomla-3-9-22.html">Joomla 3.9.22 Released</a></p>\r\n</li>\r\n</ul>\r\n<h3 dir="ltr">Legal &amp; Finance Department</h3>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Hugh conducted a thorough review of the Sponsorship status. During the first quarter of this Fiscal year, income from sponsorship has been higher than expected.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Hugh prepared a spreadsheet to track income from Sponsors, including a comparison with previous year.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">New sponsorship enquiries are now being managed with the Capital team through Hubspot, we have a free licence and it makes sense to track everything in a way that will provide an archive to future L&amp;F Coordinators.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Hugh is reviewing the Corporate Sponsorship status and checking the renewal opportunities with them.</p>\r\n</li>\r\n</ul>\r\n<h3 dir="ltr">Marketing &amp; Communications Department</h3>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Marianela highlighted the current issues with the Shop.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Marianela presented the results of the Instagram contest campaign. Followers and engagement increased.&nbsp;</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">There will be a specific campaign on LinkedIn this month.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Marianela to have a meeting with Marco and Luca on Monday to work on the requirements of the landing page for Joomla 4.</p>\r\n</li>\r\n</ul>\r\n<h3 dir="ltr">Events Department</h3>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">On boarding/Off boarding complete with Djamel.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Election of new Events Team Leaders complete.</p>\r\n</li>\r\n</ul>\r\n<h3 dir="ltr">Operations Department</h3>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Wilco is taking care of daily operations of the department while the election process for the replacement as DC is in progress.</p>\r\n</li>\r\n</ul>\r\n<h3 dir="ltr">Programs Department</h3>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Quarterly / Year End report is in progress.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Department meeting postponed by one week due to unexpected availability of the majority of team leaders.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Certification Team has a meeting planned with TechJoomla to review the current blockers with testing the new exam component.</p>\r\n</li>\r\n</ul>\r\n<h3 dir="ltr">Vice President</h3>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Justine is working on the voice over for the PBF with Benjamin.</p>\r\n</li>\r\n</ul>\r\n<h3 dir="ltr">Secretary</h3>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">[<strong>Partnership</strong>] The announcement of the partnership with Google has been published. <a href="https://joom.la/rma">https://joom.la/rma</a>. They sent the PO and are waiting for an invoice to pay for the sponsorship.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">[<strong>Reporting</strong>] Board Members reporting due date:</p>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">October 04, 2020 - 180 day Reporting for Group 2 Members.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">October 12, 2020 - Year End Report for outgoing/confirmed Group 1 members.</p>\r\n</li>\r\n</ul>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">[<strong>Deadlines</strong>]</p>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">October 12, 2020 - Annual Filing for Charitable organisations (New York State) - Form CHAR500</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">October 14, 2020 - U.S. Corporation Income Tax (Form 1120)</p>\r\n</li>\r\n</ul>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">[<strong>Elections</strong>] The replacement election for the Operations Department Coordinator is in progress. The Assistant to the DC is in charge of the daily operations, given that the former DC started effectively his new role as Treasurer of the organization.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">[<strong>Onboarding</strong>] The new Board Members have been onboarded to the OSM drive, folders and documentation with their functional mailboxes. This will ease the onboarding/offboarding procedures.</p>\r\n</li>\r\n</ul>\r\n<h3 dir="ltr">President</h3>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Brian requested to move the President\'s box at the end of the meeting.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Brian shared the financial reports classified by each department with the board for review before tax filing. This will increase financial transparency within the board and simplify the budgeting process.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Brian is supporting Radek during the onboarding process as the new Treasurer, updating the Treasurer Guide that includes all the transition documents and information.</p>\r\n</li>\r\n</ul>\r\n<h3 dir="ltr">Other business</h3>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Brian reported about the Leadership consultation connection with Elisa. We have an offer for free consultation services. We are consistently identifying sources of conflict/tension within the community. These are the types of items that this firm addresses. Brian suggested to work offline to define the issue that we want to address in a more clear way and then proceed with the consultation.</p>\r\n</li>\r\n</ul>\r\n<h3 dir="ltr">Motions taken during this meeting</h3>\r\n<p dir="ltr"><strong>#2020/125 - The Secretary proposes to adjourn the meeting.</strong><br />Proposed by Luca Marzo, seconded by Hugh Douglas-Smith.<br /><em>The motion passed with unanimous consent.</em></p>\r\n<h3 dir="ltr">Motions taken offline before this meeting</h3>\r\n<p dir="ltr"><strong>#2020/124 - Approve and publish the Board Meeting Minutes.</strong><br />Proposed by Luca Marzo, seconded by Jaz Parkyn .<br /><em>The motion passed with unanimous consent.</em></p>', NULL, 1, 1036, 1, 669, '2020-10-18 17:06:58', 0, '2020-10-18 17:08:11', 0, '0000-00-00 00:00:00'),
	(1351, 56, 0, 'Programs Department Meeting - 13th October 2020', 'programs-department-meeting-13th-october-2020', '<p dir="ltr">Participants: Jaz Parkyn, Chris Keen, Todd Woodward, Andrea Gentil</p>\r\n<p dir="ltr">Apologies: &nbsp;Davide Messia</p>\r\n<h2 dir="ltr">Minutes</h2>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Dept&nbsp;</p>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Jaz reminded everyone to post the minutes from their meetings on the Volunteer Portal and to reach out to anyone who hasn’t attended any meetings within the past few months.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Updates from Webmasters team for the Certification Team:</p>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">New htaccess updates being implemented soon</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">2FA plugin will be added and it will be compulsory for anyone to set up 2FA if they have backend access</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">A GitDeploy plugin will be released, which will result in the required use of GitHub for websites - commits done in GitHub will automatically be downloaded by the website.</p>\r\n</li>\r\n</ul>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Jaz asked each of the team leaders when their next meetings are and if they would like her to attend.</p>\r\n</li>\r\n</ul>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">JCP&nbsp;</p>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Exams - There was an exam held on September 11th 2020 in Zurich, Switzerland. There were 9 candidates&nbsp; all of which took the German language version of the exam. Seven of the nine candidates passed. The next planned exam is on October 16th in Chicago, USA.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Platform - The blocking issue with the jcpQM (question management system), of not being able to add questions to Shika has been fixed by Jereon (some typos in the code). We will now proceed with further testing of the jcpQM. The issue with Shika - the language strings not being translated is caused by the exam pages in Shika not using standard Joomla text strings. We have discussed this with TechJoomla and they are planning to supply a fix in a release at the end of October. Hopefully we can then restart testing at the beginning of November.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Second chance initiative - The Certification FAQs have been updated with rules and discounts available to candidates who fail the exam.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">J!4 exam - We are now starting work on defining the J!4 Administration exam content.</p>\r\n</li>\r\n</ul>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">VET&nbsp;</p>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">In Davide’s absence, Chris summarised the topics of the last team meeting, including onboarding, recruitment, help wanted page improvements, and meeting time adjustments. He also explained how the team is aiming to make better use of Github for planning and structuring ideas and projects.</p>\r\n</li>\r\n</ul>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">JEO</p>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Todd introduced some of the team’s ideas to the department, including training resource management, the concept of a ‘student club’, outreach to students, onboarding routes, and encouraging student volunteers.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">The team has also been working on its mission statement following submissions from the team members.</p>\r\n</li>\r\n</ul>\r\n</li>\r\n</ul>', NULL, 1, 1037, 1, 695, '2020-10-18 19:01:58', 0, '2020-10-18 19:03:21', 0, '0000-00-00 00:00:00'),
	(1352, 0, 25, 'Joomla Bug Squad Team Meeting - October 23, 2020', 'joomla-bug-squad-team-meeting-october-23-2020', '<p id="docs-internal-guid-11982654-7fff-d924-3926-7dbc40826b83" dir="ltr" style="line-height: 1.9871999999999999; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: underline; -webkit-text-decoration-skip: none; text-decoration-skip-ink: none; vertical-align: baseline; white-space: pre-wrap;">Date:</span><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;"> October 23, 2020</span></p>\r\n<p dir="ltr" style="line-height: 1.9871999999999999; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: underline; -webkit-text-decoration-skip: none; text-decoration-skip-ink: none; vertical-align: baseline; white-space: pre-wrap;">Time:</span><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;"> 19:10 CET</span></p>\r\n<p dir="ltr" style="line-height: 1.9871999999999999; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: underline; -webkit-text-decoration-skip: none; text-decoration-skip-ink: none; vertical-align: baseline; white-space: pre-wrap;">Total time of meeting:</span><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;"> 1.5 hours</span></p>\r\n<p dir="ltr" style="line-height: 1.9871999999999999; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Attendees:</span></p>\r\n<p dir="ltr" style="line-height: 1.9871999999999999; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Richard Fath, Nicola Galgano, Harald Leithner,Christiane Maier-Stadtherr, SharkyKZ, Jacob Waisner, Luca Racchetti, Tobias Zulauf</span></p>\r\n<p>&nbsp;</p>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Discussion outline</span><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">&nbsp;&nbsp;&nbsp; </span><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">&nbsp;&nbsp;&nbsp; </span><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">&nbsp;&nbsp;&nbsp; </span></p>\r\n<p>&nbsp;</p>\r\n<p dir="ltr" style="line-height: 1.38; text-align: justify; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">During this meeting we discussed the following topics:</span></p>\r\n<ul style="margin-top: 0; margin-bottom: 0;">\r\n<li dir="ltr" style="list-style-type: disc; font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre;">\r\n<p dir="ltr" style="line-height: 1.38; text-align: justify; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Current Status of Issues</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre;">\r\n<p dir="ltr" style="line-height: 1.38; text-align: justify; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">PHP 8 release</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre;">\r\n<p dir="ltr" style="line-height: 1.38; text-align: justify; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Proposal brought by Philip Walton to have testers and coders work closer</span></p>\r\n</li>\r\n</ul>\r\n<p><br /><br /></p>\r\n<p dir="ltr" style="line-height: 1.38; text-align: justify; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Current Status</span></p>\r\n<p>&nbsp;</p>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;"></span><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: underline; -webkit-text-decoration-skip: none; text-decoration-skip-ink: none; vertical-align: baseline; white-space: pre-wrap;">822 issues</span></p>\r\n<p dir="ltr" style="line-height: 1.38; text-indent: 36pt; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">&nbsp;&nbsp;&nbsp;146 on 3.x</span></p>\r\n<p dir="ltr" style="line-height: 1.38; text-indent: 36pt; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">&nbsp;&nbsp;&nbsp;128 on 4.x&nbsp;</span></p>\r\n<p dir="ltr" style="line-height: 1.38; text-indent: 36pt; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">&nbsp;&nbsp;&nbsp;12 on 4.x backend template</span></p>\r\n<p dir="ltr" style="line-height: 1.38; text-indent: 36pt; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;6 on 4.x frontend template</span></p>\r\n<p dir="ltr" style="line-height: 1.38; text-indent: 36pt; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;2 on 4.x media manager</span></p>\r\n<p dir="ltr" style="line-height: 1.38; text-indent: 36pt; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">&nbsp;&nbsp;&nbsp;20 on 4.x re-evaluate</span></p>\r\n<p dir="ltr" style="line-height: 1.38; text-indent: 36pt; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;9 on 4.x workflow</span></p>\r\n<p dir="ltr" style="line-height: 1.38; text-indent: 36pt; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">&nbsp;&nbsp;&nbsp;51 new feature</span></p>\r\n<p dir="ltr" style="line-height: 1.38; text-indent: 36pt; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">&nbsp;&nbsp;&nbsp;24 Release Blocker</span></p>\r\n<p dir="ltr" style="line-height: 1.38; text-indent: 36pt; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">424 unclassified</span></p>\r\n<p>&nbsp;</p>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">During the discussion we are looking to classify the issues to the proper version by using the proper labels, however current focus is not to classify at this time but to tackle the</span><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;"> 24 release blockers to ensure the platform is ready for 4.0 launch.</span></p>\r\n<p>&nbsp;</p>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">The current tracker we have on issues that we believe are to be focused will be reworked to break down the Release Blockers and who on the team has the skill set. This will then allow the team to focus on a few at a time versus trying to tackle all simultaneously.&nbsp;&nbsp;</span></p>\r\n<p><br /><br /><br /><br /></p>\r\n<p dir="ltr" style="line-height: 1.38; text-align: justify; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">PHP 8 release</span></p>\r\n<p>&nbsp;</p>\r\n<p dir="ltr" style="line-height: 1.38; text-align: justify; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">PHP 8 releases next month. The focus of discussion was around ensuring it will be compatible with preferably J 3.9.23 and additional testing is needed from not only the team but from the broad public when using J 3.9.23 + on PHP 8.</span></p>\r\n<p>&nbsp;</p>\r\n<p dir="ltr" style="line-height: 1.38; text-align: justify; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Working Closer With Coders &amp; Testers</span></p>\r\n<p>&nbsp;</p>\r\n<p dir="ltr" style="line-height: 1.38; text-align: justify; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">We discussed a proposal brought to us regarding how to get PRs tested faster and get as close to real-time feedback as possible by teaming up testers with coders and maintainers. The idea is still under discussion at this time.</span></p>\r\n<p>&nbsp;</p>\r\n<p dir="ltr" style="line-height: 1.9871999999999999; text-align: justify; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Next Meeting</span></p>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 12pt; margin-bottom: 12pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">The next team meeting will be: Fri, 6 November at 7:00 PM CEST</span></p>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">&nbsp;&nbsp;&nbsp; </span><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;"></span></p>', NULL, 1, 1038, 1, 1053, '2020-10-24 16:18:28', 0, '2020-10-24 16:19:06', 0, '0000-00-00 00:00:00'),
	(1353, 0, 32, 'JSST Sprint report 2020 (10.2020)', 'jsst-sprint-report-2020-10-2020', '<p><strong>Times:</strong></p>\r\n<ul>\r\n<li>Saturday, 24th of October 2020, 10am (UTC +2) - 6pm UTC (UTC +2)</li>\r\n<li>Sunday, 25th of October 2020, 10am (UTC +1) - 4pm (UTC +1)</li>\r\n</ul>\r\n<p><strong>Attendees:</strong> David Jardin, Benjamin Trenkle, Harald Leithner, Tobias Zulauf<br /><strong>Location:</strong> Google Meet</p>\r\n<p><strong>CNA / CVE Status</strong><br />We discussed the current status of the CNA approval process as well as the process changes required after becoming an CNA. This includes the updates and feedback that was requested by the MITRE after our onboarding meeting at 20.10.2020.</p>\r\n<p><strong>JSST Standard Operating Procedures</strong><br />The SOP document has been updated with various minor text and tool changes as well as responsibility changes. This also includes the documentation of our CNA process.</p>\r\n<p><strong>Discussion of security related Joomla 4 release blockers</strong><br />We had a discussion about the following security related topics for Joomla 4:</p>\r\n<ul>\r\n<li>SameSite Cookie Support (#25414) =&gt; PR updates required</li>\r\n<li>Possible issue with an unset rule (#29233) =&gt; PR #31228</li>\r\n<li>Outdated crypto lib in Joomla\\CMS\\Crypt (#29830) =&gt; PR #31231</li>\r\n</ul>\r\n<p><strong>- Redacted Topics - </strong></p>\r\n<p>Sorry folks, confidentiality is part of the nature of our work ;)</p>\r\n<p> </p>', NULL, 1, 1039, 4, 601, '2020-10-25 14:29:27', 601, '2020-10-25 14:34:08', 0, '0000-00-00 00:00:00'),
	(1354, 0, 15, 'Webmasters Team Report - October, 2020', 'webmasters-team-report-october-2020', '<p><strong>Information:</strong><br />The webmasters team mainly consists of leaders and liaisons from other teams, with the purpose to effectively work together on all aspects of our properties and infrastructure.<br />The team meets as needed, because managing the properties is a daily task and communication is done through Glip, Mails and ADoodle.<br /><br />Our reports will include general information, actions performed and motions taken over a period of time.<br />The reports will not be about just one meeting which is planned every month like most teams.<br /><br /></p>\r\n<h2><strong>General:</strong></h2>\r\n<ul>\r\n<li>We are working on making IPv6 available for all websites services including CDN.<br />The process involves multiple suppliers and will be performed in phases.<br />Rochen has prepared all servers and are creating the "AAAA" records for the websites, we are looking for a new CDN provider with IPv6 support, researching what changes are needed to use Amazon with IPv6 as well.<a href="https://extensions.joomla.org/vulnerable-extensions/about/"><br /><br /></a></li>\r\n<li>We are looking at the possibilities to join the Cloudflare OpenSource sponsorship program, which will give us the possibility to make our CDN IPv6 compatible as well.<br /><br /></li>\r\n<li>The team is analysing with Rochen what actions are needed for server OS and MySQL upgrades.<br /><br /></li>\r\n<li>The team is defining the future way of using SSL / TLS Certificates, the current one expires in May 2021 and new certificates are only valid for one year.<br /><br /></li>\r\n<li>Wilco has been elected as Operations Department Coordinator, the team will discuss the team structure and take the appropriate actions.</li>\r\n</ul>\r\n<h2><strong>Improvements / tasks / actions:</strong></h2>\r\n<ul>\r\n<li>The htaccess baseline and currently used version on the following websites are merged.<br />Extensions, Resources, Showcase and Main Site.<br /><br /></li>\r\n<li>All sites have been updated to report any Content Security Policy information to our sponsored <a href="https://report-uri.com/" target="_blank" rel="noopener">https://report-uri.com/</a> platform.<br /><br /></li>\r\n<li>The "Privacy &amp; Security Settings" policy has been created and is being finalized depending on other topics.<br /><br /></li>\r\n<li>The websites PHP version is being upgraded to PHP7.4<a href="https://github.com/joomla/joomla-websites/blob/master/configuration-baselines/.htaccess-joomla"></a>&nbsp;<br /><br /></li>\r\n<li>The DNS has been prepared to enable Joomla to become a Brave Content Creator, awaiting account verification by the President and Treasurer. (<a href="https://brave.com/" target="_blank" rel="noopener">https://brave.com/</a>) </li>\r\n</ul>', NULL, 1, 1040, 1, 2198, '2020-10-25 20:24:32', 0, '2020-10-25 20:34:40', 0, '0000-00-00 00:00:00'),
	(1355, 0, 6, 'Forum Meeting 26/10/2020', 'forum-meeting-26-10-2020', '<p>Meeting Notes:</p>\r\n<ul>\r\n<li>Discussions about forum behavior of various users</li>\r\n<li>Discussing Upgrading phpBB and php version</li>\r\n<li>Next Meeting: Monday 23rd Nov 2020</li>\r\n</ul>', NULL, 1, 1041, 1, 503, '2020-10-26 12:30:45', 0, '2020-10-26 12:32:56', 0, '0000-00-00 00:00:00'),
	(1356, 0, 9, 'October 2020', 'october-2020', '<ul>\r\n<li>Stats September: 28 sites submitted -&gt; 14 approved + 12 rejected + 2 corrections requested, 2 contact requests (answered)</li>\r\n<li>PHP upgrade to 7.3</li>\r\n<li>Htaccess changed</li>\r\n<li>Ads callback changed</li>\r\n<li>Working on implementation of the new rule to accept only httpS site</li>\r\n<li>Following New Cookie banner implementation (to respect GDPR) (started 1 year ago)</li>\r\n<li>Enable 2 factor authentification for registered users -&gt; planned</li>\r\n<li>Following SSO topic</li>\r\n<li>About the tool&nbsp;<a href="https://github.com/SIWECOS/Version-Scanner" target="_blank" rel="noopener">https://github.com/SIWECOS/Version-Scanner</a>&nbsp;which required Docker, we\'re working on the extraction of the app from the container.&nbsp;</li>\r\n</ul>', NULL, 1, 1042, 2, 657, '2020-10-26 18:51:54', 657, '2020-10-26 19:10:41', 0, '0000-00-00 00:00:00'),
	(1357, 0, 120, 'Privacy Compliance Team Meeting October 28, 2020', 'privacy-compliance-team-meeting-october-28-2020', '<p>This meeting of the Joomla! Privacy Compliance Team has been held on Glip on October 28, 2020 at 16:30 CET.&nbsp;</p>\r\n<p><strong>Participants</strong></p>\r\n<p>In attendance:&nbsp;</p>\r\n<ul>\r\n<li>Donata Kalnenaite</li>\r\n<li>Sandra Decoux</li>\r\n<li>Roland Dalmulder</li>\r\n<li>Luca Marzo</li>\r\n<li>Sander Potjer</li>\r\n</ul>\r\n<p>Discussion outline:&nbsp;</p>\r\n<ul>\r\n<li>Donata and Luca have been working on the "what privacy laws apply to us" analysis and asked the team members for assistance.&nbsp;</li>\r\n<li>Roland has moved the server to PHP 7.4 and reported that everything is running fine. He also began working on the consent all button and plans to finish that task this coming weekend. He also discussed the requirement of showing data on the JVP with Sander. He will be testing the website on the new MySQL version so the server can be upgraded.&nbsp;</li>\r\n<li>Luca is working on a new set of bylaws that will be more clear regarding removing team members after they fail to renew their consents. He shared some information on the bylaws regarding this issue and it appears that members can be removed if they fail to update their current email address.&nbsp;</li>\r\n<li>The team agreed to wait on the new bylaws to finalize the opinion regarding the removal of members who fail to renew their consents.&nbsp;</li>\r\n<li>Sandra has asked the SM team to do some posts to invite people to register on the Volunteers Portal.&nbsp;</li>\r\n</ul>', NULL, 1, 1043, 1, 6279, '2020-10-28 16:15:00', 0, '2020-10-28 16:21:08', 0, '0000-00-00 00:00:00'),
	(1358, 54, 0, 'October 28 2020, Events Department  Meeting', 'october-28-2020-events-department-meeting', '<p id="docs-internal-guid-91d66b41-7fff-2991-f9d6-ca530941cbb6" dir="ltr" style="overflow-wrap: break-word;">Participants:&nbsp; Djamel Kherbi, David Aswani, Brian Ronnon</p>\r\n<p dir="ltr" style="overflow-wrap: break-word;">Apologies:&nbsp; &nbsp;None</p>\r\n<p dir="ltr" style="margin-top: 0pt; margin-bottom: 0pt; overflow-wrap: break-word; line-height: 1.38;">Total time of the meeting:&nbsp; 45 minutes&nbsp;</p>\r\n<p dir="ltr" style="margin-top: 0pt; margin-bottom: 0pt; overflow-wrap: break-word; line-height: 1.38;">&nbsp;</p>\r\n<p>Venue: Google Meet</p>\r\n<p id="docs-internal-guid-77111eb8-7fff-8415-779b-6f89946f0cd5" dir="ltr" style="overflow-wrap: break-word;">The Events department held its monthly meeting where the following items were discussed.</p>\r\n<p dir="ltr" style="overflow-wrap: break-word;"><strong>Event Department</strong></p>\r\n<p dir="ltr" style="overflow-wrap: break-word;">1. Carlos has requested to step down from his position as Joomla Team Event member citing other commitments.&nbsp;</p>\r\n<p dir="ltr" style="overflow-wrap: break-word;">2. Encouraging online events / JoomlaDay and JUG. We will be reaching out to the organizers that are active especially in Europe and the UK to encourage virtual event participation.&nbsp;</p>\r\n<p dir="ltr" style="overflow-wrap: break-word;">3. Laura Gordon requested clearance to organize JoomlaDay USA. This is a brilliant idea. We will offer our support to them.&nbsp; We have requested them to go through the rules, have a date and a website before submitting the event.</p>\r\n<p><strong>Joomla User Group</strong></p>\r\n<p>1. Jug directory migration to DP calendar almost done.</p>\r\n<p><strong>Events Team</strong></p>\r\n<p>1. Getting new members to the events team. Make a call for volunteers for both events and the JUG team. Reach out to Andrea of Joomla Magazine to put a voice of Joomla Magazine.</p>\r\n<p>2. Djamel and Wilco take over DP Calendar migration.</p>', NULL, 1, 1044, 4, 901, '2020-10-29 13:49:26', 901, '2021-06-23 09:23:26', 0, '0000-00-00 00:00:00'),
	(1359, 0, 4, 'JCM Meeting Report - October 29, 2020', 'jcm-meeting-report-october-29-2020', '<p><em>The meeting started at 13:30 (UTC)</em></p>\r\n<p dir="ltr"><span style="text-decoration: underline;"><em>In attendance</em></span>: Luca Marzo, Anja de Crom, Hans van der Meer, Sandra Decoux, Wilco Alsemgeest.<br /><span style="text-decoration: underline;"><em>Apologies</em></span>: Andrea Gentil, Patrick Jackson</p>\r\n<h3 dir="ltr">Meeting notes</h3>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">October issue published on time: thanks to the whole team for the work done</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">The Team reviewed the status of the Release Plan for the November Issue of the JCM.&nbsp;</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Anja proposed to reinstate the article series: “Getting extensions ready for Joomla 4” and “Joomla turns 15”.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Andrea prepared a Google Data Studio with analytics data and other success metrics of the JCM.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">The Team reviewed the metrics about visits and engagement related to the past 30 days.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Upon team’s suggestions, the list of topics for which we aim to have articles have been added to the Editor’s Introduction. This will be done monthly to simplify the research of new authors, providing them topics.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">The Team proposed several additional articles for the November Issue.&nbsp;</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">The Team accepted the proposal to review and uniform the tags in the magazine as per the internal issue #89.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Andrea proposed to send a “Call for authors” through a newsletter. The call will be implemented also in the regular newsletter so it can increase the opportunities to find new authors.</p>\r\n</li>\r\n</ul>\r\n<p dir="ltr"><em>Meeting adjourned after 55 minutes.</em></p>', NULL, 1, 1045, 1, 669, '2020-10-29 14:29:26', 0, '2020-10-29 14:31:04', 0, '0000-00-00 00:00:00'),
	(1360, 0, 10, 'Changes on the Social Media Team', 'changes-on-the-social-media-team', '<p>I\'ve had a wonderful time over the last 6+ years and have met so many great people, but it\'s time for me to ease up on the volunteering a bit and take care of myself and my own business.&nbsp;</p>\r\n<p>That said, my amazing #1, Hans van der Meer will be taking over as the Team Lead of the Social Media Team.&nbsp;</p>\r\n<p>I will be stepping back to take care of the Joomlers Twitter account and continue to do some graphics for the team. This allows me to still help out at a more manageable level and give me a better volunteer/life balance.&nbsp;</p>\r\n<p>Congratulations Hans, you will be great!&nbsp;</p>\r\n<p><img src="images/reports/CongratsHans.jpg" alt="Congratulations Hans van der Meer" /></p>', NULL, 1, 1046, 1, 621, '2020-10-30 15:00:54', 0, '2020-10-30 15:10:05', 0, '0000-00-00 00:00:00'),
	(1361, 55, 0, 'Operations Department Meeting Minutes - October 26, 2020', 'operations-department-meeting-minutes-october-26-2', '<p><strong>Motions:</strong><br />#2020/OPS/004 - Allow exception for the Volunteers Portal team to provide quarterly reports instead of each month due to the specific nature of the team.&nbsp;  INSERT INTO `#__volunteers_reports` (`id`, `department`, `team`, `title`, `alias`, `description`, `notes`, `state`, `ordering`, `version`, `created_by`, `created`, `modified_by`, `modified`, `checked_out`, `checked_out_time`) VALUES
	(1376, 0, 150, 'Educational Outreach Working Group Meeting - November 20, 2020', 'eog-11-20-2020', '<h1 dir="ltr" style="line-height: 1.38; margin-top: 20pt; margin-bottom: 6pt;"><span style="font-size: 20pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Joomla Educational Outreach Working Group</span></h1>\r\n<h2 dir="ltr" style="line-height: 1.38; margin-top: 18pt; margin-bottom: 6pt;"><span style="font-size: 16pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Agenda - November 20, 2020 17:00 UTC</span></h2>\r\n<ul style="margin-top: 0; margin-bottom: 0;">\r\n<li dir="ltr" style="list-style-type: disc; font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre;">\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Structure and organization of the resources and tools to include in the hub</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre;">\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Determination of the types of materials</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre;">\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Review the rough outline for the Joomla Student Club and discuss how can help&nbsp;</span></p>\r\n</li>\r\n</ul>\r\n<p dir="ltr" style="line-height: 1.38; margin-left: 36pt; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">&nbsp;(Ruchika Nagarkar)<br /><br /></span></p>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Attendees</span><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">: Laura Gordon, Chris Keen, Todd Woodward</span></p>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Apologies</span><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">: AK Shehu, SD Williams<br /><br /></span></p>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Time Permitting</span></p>\r\n<ul style="margin-top: 0; margin-bottom: 0;">\r\n<li dir="ltr" style="list-style-type: disc; font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre;">\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Alert other ‘joomla members’ that this area exists?</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre;">\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Information Architecture and Content Strategy for teachjoomla.org</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre;">\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">New Business</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre;">\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Review topics for next meeting<br /><br /></span></p>\r\n</li>\r\n</ul>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Notes</span><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">:</span></p>\r\n<ul style="margin-top: 0; margin-bottom: 0;">\r\n<li dir="ltr" style="list-style-type: disc; font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre;">\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Laura - Suggestion, consider switching this meeting to early December, and have it replace the ‘end of December meeting’</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre;">\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Todd will reach out to the entire team to determine the date for the December Meeting.<br /><br /></span></p>\r\n</li>\r\n</ul>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Contact with non-active members:</span></p>\r\n<ul style="margin-top: 0; margin-bottom: 0;">\r\n<li dir="ltr" style="list-style-type: disc; font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre;">\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Todd - reached out to attendees that are not actively participating.&nbsp; Only 2 have responded that they could not be active.&nbsp; He hasn’t heard from the others.&nbsp; At this point, we will remove those that are not participating.</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre;">\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Will include a note to anyone that is no longer going to be in the meeting group on joomla.org, as a thank you for participating, and an open invite to join in the group again.<br /><br /></span></p>\r\n</li>\r\n</ul>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Review the rough outline for the Joomla Student Club and discuss how can help&nbsp; (Ruchika Nagarkar)</span></p>\r\n<ul style="margin-top: 0; margin-bottom: 0;">\r\n<li dir="ltr" style="list-style-type: disc; font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre;">\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">We need to review is this is still something that we can assist on</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre;">\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Todd will reach out to find out what her thoughts are on this<br /><br /></span></p>\r\n</li>\r\n</ul>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Check for Magazine Article</span></p>\r\n<ul style="margin-top: 0; margin-bottom: 0;">\r\n<li dir="ltr" style="list-style-type: disc; font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre;">\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Didn’t see the article from AK in the November Magazine<br /><br /></span></p>\r\n</li>\r\n</ul>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">December Meeting Thoughts</span></p>\r\n<ul style="margin-top: 0; margin-bottom: 0;">\r\n<li dir="ltr" style="list-style-type: disc; font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre;">\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Structure and organization of the resources and tools to include in the hub</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre;">\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Determination of the types of materials</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre;">\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Alert other ‘joomla members’ that this area exists?</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre;">\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Information Architecture and Content Strategy for teachjoomla.org</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre;">\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Think about having meetings earlier in the day to open up opportunities for folks in Australia to participate.</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre;">\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Confirm article from SD about group </span><a href="https://docs.google.com/document/d/1gBGf43w73sldmsd0irpxT02nLX2joBsBgOTLkAMJGBk/edit" style="text-decoration: none;"><span style="font-size: 11pt; font-family: Arial; color: #1155cc; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: underline; -webkit-text-decoration-skip: none; text-decoration-skip-ink: none; vertical-align: baseline; white-space: pre-wrap;">https://docs.google.com/document/d/1gBGf43w73sldmsd0irpxT02nLX2joBsBgOTLkAMJGBk/edit</span></a></p>\r\n</li>\r\n</ul>\r\n<p>&nbsp;</p>\r\n<ul style="margin-top: 0; margin-bottom: 0;">\r\n<ul style="margin-top: 0; margin-bottom: 0;">\r\n<li dir="ltr" style="list-style-type: circle; font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre;">\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Can we get individual photos of each member, and include with article?</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: circle; font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre;">\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Laura will look for photo from the Engagement team in Spain, plus we will include photos of the current team members.</span></p>\r\n</li>\r\n</ul>\r\n</ul>', NULL, 1, 1062, 1, 1715, '2020-11-20 19:18:01', 0, '2020-11-20 19:21:29', 0, '0000-00-00 00:00:00'),
	(1377, 0, 32, 'JSST Meeting Report (2020-04) ', 'jsst-meeting-report-2020-04', '<p><strong>Time:</strong> Tuesday, 03th of November 2020, 5pm UTC - 6pm UTC<br /> <strong>Attendees:</strong> David Jardin, Tobias Zulauf</p>\r\n<p><strong>MITRE / CNA</strong><br /> We have been accepted as CNA by MITRE and prepared a Press Release that is currently pending and waiting for approval from MITRE.</p>\r\n<p><strong>JSST / Webmasters Cooperation Process</strong><br /> The JSST and the Webmasters now formally wrote down the Cooperation Process between the two teams. Nothing is changing in terms of what we do but it is now written down and documented. Now we are awaiting the Webmasters Team and Operation DCs formal approval.</p>\r\n<p><strong>Security.txt Updates</strong><br /> The JSST is going to update the published security.txt file together with the webmasters team adding a “Hiring“ tag and fixing the “Contact” that was missing the mailto to comply with the security.txt standard.</p>\r\n<p><strong>- Redacted Topics -</strong></p>', NULL, 1, 1063, 2, 601, '2020-11-20 23:37:27', 601, '2020-11-20 23:39:47', 0, '0000-00-00 00:00:00'),
	(1378, 0, 120, 'Privacy Compliance Team Meeting November 25, 2020', 'privacy-compliance-team-meeting-november-25-2020', '<p>This meeting of the Joomla! Privacy Compliance Team has been held on Google Hangouts on November 25, 2020 at 16:30 CET.&nbsp;</p>\r\n<p><strong>Participants</strong></p>\r\n<p>In attendance:&nbsp;</p>\r\n<ul>\r\n<li>Donata Stroink-Skillrud</li>\r\n<li>Sandra Decoux</li>\r\n<li>Roland Dalmulder</li>\r\n<li>Sander Potjer</li>\r\n</ul>\r\n<p><strong>Discussion outline&nbsp;</strong></p>\r\n<ul>\r\n<li>Roland is working on finishing up the "Joomla and your consents" article;&nbsp;</li>\r\n<li>The team discussed a task that states that the Cookie Policy is incomplete/incorrect - we will review the task and update the Cookie Policy as needed;&nbsp;</li>\r\n<li>The team discussed the Incident Response Plan and the associated documentation and agreed to ask Luca who is responsible for the plan and documentation as those need to be updated;&nbsp;</li>\r\n<li>The team discussed what privacy laws apply to us and the need for implementing compliance with Brazil\'s and Uganda\'s privacy laws;&nbsp;</li>\r\n<li>The team discussed a year-end get together that will take place at the end of December;&nbsp;</li>\r\n<li>Roland agreed to take a look at the Properties Assessment Task and let us know if this task has been completed or if something else needs to be done;&nbsp;</li>\r\n<li>Roland is working on single sign on for the backend;&nbsp;</li>\r\n<li>Sander is working on having the Identity Portal collect signatures so that we can track who signed particular documents such as the Non-Disclosure Agreement.&nbsp;</li>\r\n</ul>', NULL, 1, 1064, 1, 6279, '2020-11-25 16:34:01', 0, '2020-11-25 16:38:29', 0, '0000-00-00 00:00:00'),
	(1379, 58, 0, 'OSM, Inc. - Full Board Meeting - November 19, 2020', 'osm-inc-full-board-meeting-november-19-2020', '<h2 id="docs-internal-guid-e6fb8473-7fff-ae21-654a-1b30885daf48" dir="ltr">Open Source Matters, Inc.<br />Full Board of Directors and Officers Meeting</h2>\r\n<p dir="ltr">Date: November 19, 2020<br />Time: 18:00 UTC<br />Total time of meeting: 1 hour 55 minutes</p>\r\n<h4 dir="ltr">Attendees:</h4>\r\n<p dir="ltr">Brian Mitchell (President), Justine Ayebale Abunyanga (Vice President), Luca Marzo (Secretary), Radek Suski (Treasurer), Marco Dings (Director), Hugh Douglas-Smith (Director), David Opati Aswani (Director), Jaz Parkyn (Director), Marianela Queme (Director), Wilco Alsemgeest (Director).</p>\r\n<h4 dir="ltr">Apologies:</h4>\r\n<p dir="ltr">-</p>\r\n<h4 dir="ltr">Proxies:</h4>\r\n<p dir="ltr">-</p>\r\n<h4 dir="ltr">Invited Guests:</h4>\r\n<p dir="ltr">Jennifer Gress.</p>\r\n<h2 dir="ltr">Discussion outline</h2>\r\n<h3 dir="ltr">Invited Guests</h3>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Jennifer Gress shared her report about the analysis done on the recent Code of Conduct Incident Reports, as result of the invitation to support the action of the Conflict Resolution Team.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Jennifer Gress shared her recommendation to improve the conflict handling process.</p>\r\n</li>\r\n</ul>\r\n<p dir="ltr" style="text-align: center;"><em><span style="text-decoration: underline;">Jennifer Gress left the meeting at 18.35 UTC.</span></em></p>\r\n<h3 dir="ltr">Treasurer</h3>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Radek is monitoring the status of some outstanding sponsorship payments.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Radek shared the bank balances as of today.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Bank Statements are current through the end of October 2020 and are available in drive for board review.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Radek and Brian are cleaning up and updating the Treasurer Guide.</p>\r\n</li>\r\n</ul>\r\n<h3 dir="ltr">Production Department</h3>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Press release regarding Joomla as CVE Numbering Authority should have been released on Wednesday 18th November. <a href="http://joom.la/rs1">http://joom.la/rs1</a>&nbsp;</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">The Production Department is now involved in rolling out the cookie consent implementation for Joomla assets.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Documenting of maintenance of “docs.joomla.org” is initiated, meeting was scheduled but postponed upon request of the current maintainer.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Article about “Joomla 3.10 Status &amp; Release Strategy” will be published in the next issue of the Magazine.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Niels Brazek with backup of Lewellyn van der Merwe have taken ownership of the Joomla Framework maintenance.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Sigrid Gramlinger has been elected team leader of CMS Release Team.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Google Summer of Code has 9 projects under consideration for 2021.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">A <a href="https://github.com/joomla-projects/com_release_checking">bespoke component</a> to facilitate coordinated testing/checking of releases has been made available to the CMS Release Team.</p>\r\n</li>\r\n</ul>\r\n<h3 dir="ltr">Legal &amp; Finance Department</h3>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">&nbsp;Two new Global Sponsorship have been signed and pending invoice:</p>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Global Bronze Sponsor.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Global Platinum Sponsor.</p>\r\n</li>\r\n</ul>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Hugh had a meeting with Rochen about the existing partnership.</p>\r\n</li>\r\n</ul>\r\n<h3 dir="ltr">Marketing &amp; Communications Department</h3>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Marianela attended a call with BrandIT about the status of domains.joomla.org.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Hans van der Meer has been elected as Team Leader of the Social Media Team.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Giovanni Genovino has been elected as Team Leader of the Community Translation Team.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">New Joomla Brochure for Joomla 4, but with a general perspective will soon be finished.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">A new video to promote Joomla 4 is being created.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Working on the contents for the Joomla 4 landing page with the Marketing Team.</p>\r\n</li>\r\n</ul>\r\n<h3 dir="ltr">Events Department</h3>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">No major updates from the Events Department.</p>\r\n</li>\r\n</ul>\r\n<h3 dir="ltr">Operations Department</h3>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Operations Department meetings are planned for every 3rd Monday in the month, the last one was on November 16th, the report is published: <a href="departments/operations/reports/1375-operations-department-meeting-minutes-november-16">https://volunteers.joomla.org/departments/operations/reports/1375-operations-department-meeting-minutes-november-16</a>&nbsp;</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">The Service Provider Directory replacing the Resources Directory website is online, it holds all options which were available in the old structure as well. The migration of data is in progress by the users themselves, this way old information is updated and invalid entries are removed. <a href="https://community.joomla.org/blogs/community/the-new-service-providers-directory-is-live.html">https://community.joomla.org/blogs/community/the-new-service-providers-directory-is-live.html</a>&nbsp;</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">The OpenSourceMatters.org domain DNS was switched from Rochen to CloudFlare on November 10, 2020.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">The Joomla.org domain DNS was switched from Rochen to CloudFlare on November 15, 2020.</p>\r\n</li>\r\n</ul>\r\n<h3 dir="ltr">Programs Department</h3>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">No major updates from the Programs Department.</p>\r\n</li>\r\n</ul>\r\n<h3 dir="ltr">Secretary</h3>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">[<strong>Bylaws</strong>] Bylaws review is continuing with Jennifer Gress. We’re at about 50%.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">[<strong>Privacy</strong>] Donata and Luca assessed the Privacy laws to which OSM should comply with because of the fact OSM processes PII of individuals residing in such countries. The proposal is to comply with rules of countries in which OSM has more than 3 members, as it might not be worth complying with all the countries’ regulations and most of them are less restrictive than GDPR.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">[<strong>Membership</strong>] Members who failed to renew their consents in the Identity Portal shall be removed as Members pursuant to Section 4.04(A)(6) of the Bylaws, as they did not keep up to date their membership record with a valid electronic email address.&nbsp;</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">[<strong>Principal Office</strong>] The principal office (and physical mailing address) of the organization is located in the offices of the Software Freedom Law Foundation. This had a sense when this decision was taken, given that a member of this Foundation was a Board member of OSM. For OSM this address is not monitored and nobody from OSM has access to it. It would be opportune to sign up for a “corporate address” service, to have a unique address that can be written in the Bylaws and would automatically forward any eventual mail to our PO Box that is constantly monitored. The proposal is to take a “Business address” in Wall Street, NY at roughly 250 USD per year. The Registered address of the organization wouldn’t change.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">[<strong>Sponsorship</strong>] Agreements for the 6 months Platinum and the 6 months Bronze Sponsors have been prepared and provided to Hugh for sponsor signature.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">[<strong>Sponsorship</strong>] Following the work done on the Bronze Sponsorship Agreement, also the Platinum Sponsorship Agreement has been standardized and prepared as Web Form in EchoSign to speed up the signing process for new Sponsors. A 6 month Standard Bronze Agreement template has been prepared too.</p>\r\n</li>\r\n</ul>\r\n<h3 dir="ltr">President</h3>\r\n<ul>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Brian requested feedback on the budget summary 2019-20 report.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Brian requested feedback on Communications/Leadership Consultancy offered by Elisa.</p>\r\n</li>\r\n<li dir="ltr">\r\n<p dir="ltr" role="presentation">Brian shared some concerns on the privacy aspects of the Honor Roll page.</p>\r\n</li>\r\n</ul>\r\n<h3 dir="ltr">Motions taken during this meeting</h3>\r\n<p dir="ltr"><strong>#2020/136 - Joomla is a non-political organisation and inclusive of everybody. In that context any public facing Joomla oriented communication should be void of political or otherwise offensive comments or references.<br /></strong>Proposed by Marco Dings, seconded by Luca Marzo.<br /><em>The motion passed with unanimous consent.</em></p>\r\n<p dir="ltr"><strong>#2020/137 - Give the mandate to the Privacy Compliance Team to pursue compliance with the Privacy regulations of countries in which OSM has more than 3 members.</strong><br />Proposed by Luca Marzo, seconded by Hugh Douglas-Smith.<br /><em>The motion passed with unanimous consent.</em></p>\r\n<p dir="ltr"><strong>#2020/138 - Purchase the “Business Address” service to have a principal office address in New York, as required by the Bylaws.<br /></strong>Proposed by Luca Marzo, seconded by Hugh Douglas-Smith.<br /><em>The motion passed with unanimous consent.</em></p>\r\n<p dir="ltr"><strong>#2020/139 - The Secretary proposes to adjourn the meeting.<br /></strong>Proposed by Luca Marzo, seconded by Hugh Douglas-Smith.<br /><em>The motion passed with unanimous consent.</em></p>\r\n<h3 dir="ltr">Motions taken offline before this meeting</h3>\r\n<p dir="ltr"><strong>#2020/135 - Approve and publish the Board Meeting Minutes.</strong><br />Proposed by Luca Marzo, seconded by Jaz Parkyn.<br /><em>The motion passed with unanimous consent.</em></p>', NULL, 1, 1065, 1, 669, '2020-11-28 16:02:33', 0, '2020-11-28 16:04:41', 0, '0000-00-00 00:00:00'),
	(1380, 0, 78, 'Certification Program: team meeting - November 25, 2020', 'certification-program-team-meeting-november-2020', '<p style="overflow-wrap: break-word;">THe JCP November meeting was held on November 25 2020 at 17:00 (UTC).</p>\r\n<h3 style="font-family: \'Helvetica Neue\', Helvetica, Arial, sans-serif; color: #333333;">Particpants:</h3>\r\n<p style="overflow-wrap: break-word;"><em>Attendees:</em><span style="caret-color: auto;"></span><span style="caret-color: auto;">Andrea Gentil, Jeroen Moolenschot,&nbsp;</span><span style="caret-color: auto;">Luca Marzo</span><span style="caret-color: auto;">&nbsp;and Chris Keen.</span></p>\r\n<p style="overflow-wrap: break-word;"><em>Guest:</em>&nbsp;Jaz Parkyn (Dept. Co-ordinator)&nbsp;<br /><br /><em>Absent with apologies:&nbsp;</em></p>\r\n<h2 style="font-family: \'Helvetica Neue\', Helvetica, Arial, sans-serif; color: #333333;">General points</h2>\r\n<p><span id="docs-internal-guid-db13cfb0-7fff-1e7f-2d9c-50751520fef3"><span style="font-size: 11pt; font-family: Roboto, sans-serif; color: #000000; background-color: transparent; font-weight: bold; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;">Exam sessions:<br /><br /><span style="font-size: 14.6667px; font-weight: 400;">There have been no exams held in the last month. At this moment there are no new exams planned.</span></span></span>.&nbsp;<br /><span style="font-size: 11pt; font-family: Roboto, sans-serif; color: #000000; background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;"><span id="docs-internal-guid-384ff348-7fff-b2c0-8131-af9f5ed59744"></span><br /></span><span id="docs-internal-guid-db13cfb0-7fff-1e7f-2d9c-50751520fef3"><span style="font-size: 11pt; font-family: Roboto, sans-serif; color: #000000; background-color: transparent; font-weight: bold; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;">Website and Exam platform</span></span></p>\r\n<p><strong>Shika (new exam platform):</strong><br />Preliminary tests with the most recent update from TechJoomla are positive. We are now able to test the other exam functionality needed to migrate to our new exam platform.&nbsp;</p>\r\n<p><strong>Sandbox: Upgrade Moodle from 3.4 to 3.9.x, on PHP7.4 and MySQL 5.7<strong style="color: inherit; font-family: inherit; caret-color: auto; font-size: 14px;">:</strong>(new exam platform)&nbsp;<br /></strong>Initial tests on the new configuration seem positive. We will continue to test this over the next few weeks, to confirm this configuation is fully usable for any upcoming exams</p>\r\n<p style="overflow-wrap: break-word;"><span style="color: #000000; font-family: Arial; font-size: 14.6667px; white-space: pre-wrap; caret-color: auto;"><span style="font-family: Roboto, sans-serif; font-size: 14.6667px; font-weight: bold;">Operations and Marketing</span><span style="font-size: 14.6667px;"><br />There are no updates from the Operations and Marketint team (mainly due to the lack of exams).</span><span style="font-size: 14.6667px;"></span></span></p>\r\n<p style="overflow-wrap: break-word;"><span style="color: #000000; font-family: Arial; font-size: 14.6667px; white-space: pre-wrap; caret-color: auto;"><span id="docs-internal-guid-db13cfb0-7fff-1e7f-2d9c-50751520fef3" style="color: #333333; font-family: \'Helvetica Neue\', Helvetica, Arial, sans-serif; font-size: 14px; white-space: normal;"><span style="font-size: 11pt; font-family: Roboto, sans-serif; color: #000000; background-color: transparent; font-weight: bold; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;">J!4 exam(s)<br /></span></span><span id="docs-internal-guid-fa6f5df6-7fff-6772-8e79-e390e9ee64f5" style="color: #333333; font-family: \'Helvetica Neue\', Helvetica, Arial, sans-serif; font-size: 14px; white-space: normal;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-variant-numeric: normal; font-variant-east-asian: normal; vertical-align: baseline; white-space: pre-wrap;">We are now starting preparations for the J!4 exam(s), we need first to decide how we will write and review the exam questions before we can begin actually writing the them.<br />The J!4 Administrator Exam should be available approximately 6 months after the J!4 release date, this gives the JCP team enough time to complete the questions and answers based on the released version (including any initial bug fixes). This period will also give users sufficient time to use and learn J!4 and to prepare for the exam.<br /></span></span>We will be shortly looking for volunteers to help us (re)write the Administrator exams Q&amp;A\'s, if you\'re interested please contact us!</span></p>\r\n<p style="overflow-wrap: break-word;"><strong>Next meeting: </strong>still to be formalised due to the Christmas holidays<strong>.</strong></p>', NULL, 1, 1066, 4, 1093, '2020-11-29 18:15:36', 1093, '2020-12-01 18:17:09', 0, '0000-00-00 00:00:00'),
	(1381, 0, 4, 'JCM Meeting Report - November 25th, 2020', 'jcm-meeting-report-november-25th-2020', '<p><em>The meeting started at 14:30 (CEST)</em></p>\r\n<p><strong>Participants</strong>:&nbsp; Anja de Crom, Wilco Alsemgeest, Hans Van Der Meer, Luca Marzo, Sandra Decoux, Andrea Gentil.</p>\r\n<p><strong>Apologies</strong>: Patrick Jackson</p>\r\n<h2>Discussion outline</h2>\r\n<ul>\r\n<li>November issue published on time: thanks to the whole team for the work done.</li>\r\n<li>December issue planning started.</li>\r\n<li>Review of monthly stats from Analytics stats: Data studio ready, including newsletter stats.</li>\r\n<li>Font sizes &amp; readability: Andrea will work on the improvements suggested by Elisa Foltyn in GH.</li>\r\n<li>Anja proposed a new article series: Newbie series: "Where to Start", "Good Resources", "What Next".</li>\r\n<li>Anja proposed also to have an article about Joomla standards for extensions written by a tech.</li>\r\n<li>Pending: Review the tag structure to facilitate the search of articles by topic.</li>\r\n<li>Pending: Call for authors: set up a mailing list for the registered authors on the JCM website to invite them to write again for the JCM.</li>\r\n<li>Pending: Onboarding process for authors: final review to implement it.</li>\r\n</ul>\r\n<p><em>The meeting ended at 15:45 (CEST)</em></p>', NULL, 1, 1067, 1, 1459, '2020-12-02 10:55:38', 0, '2020-12-02 10:59:37', 0, '0000-00-00 00:00:00'),
	(1382, 0, 44, 'JED Team Meeting - November 27th, 2020', 'jed-team-meeting-november-27th-2020', '<p class="western" style="margin-bottom: 0.24in; direction: ltr; color: #000000; line-height: 16.8667px; background: transparent; font-family: Arial, serif; font-size: 11pt; border: none; padding: 0in;"><span style="color: #333333;"><span style="font-size: 10pt;">The Joomla! Extensions Directory (JED) team met on November 27th, 2020, at 15hs UTC, via Glip to discuss our progress on current tasks.</span></span></p>\r\n<ul style="color: #000000; font-size: medium;">\r\n<li>\r\n<p class="western" style="margin-bottom: 0in; direction: ltr; line-height: 22.7333px; background: transparent; font-family: Arial, serif; font-size: 11pt; border: none; padding: 0in;"><span style="color: #333333;"><span style="font-size: 10pt;"><b>In attendance</b></span></span><span style="color: #333333;"><span style="font-size: 10pt;">: Anibal Sanchez, Jaz Parkyn, Claire Mandville, Dénes Székely, Hugh Douglas-Smith, Luca Marzo, Mark Lee, Llewellyn van der Merwe, Wilco Alsemgeest (Department Coordinator).</span></span></p>\r\n</li>\r\n<li>\r\n<p class="western" style="margin-bottom: 0in; direction: ltr; line-height: 22.7333px; background: transparent; font-family: Arial, serif; font-size: 11pt; border: none; padding: 0in;"><span style="color: #333333;"><span style="font-size: 10pt;"><b>Absent</b></span></span><span style="color: #333333;"><span style="font-size: 10pt;">: Carlos Cámara</span></span></p>\r\n</li>\r\n</ul>\r\n<h2 class="western" style="margin-top: 0.25in; margin-bottom: 0.06in; direction: ltr; color: #000000; line-height: 29.8667px; break-inside: avoid; background: transparent; break-after: avoid; font-family: Arial, serif; font-size: 16pt; font-weight: normal; border: none; padding: 0in;"><a name="h.4jvcf96bui6y"></a><span style="color: #333333;"><span style="font-size: 17pt;"><b>Team Organization</b></span></span></h2>\r\n<ul style="color: #000000; font-size: medium;">\r\n<li>\r\n<p class="western" style="margin-bottom: 0in; direction: ltr; line-height: 22.7333px; background: transparent; font-family: Arial, serif; font-size: 11pt; border: none; padding: 0in;"><span style="color: #333333;"><span style="font-size: 10pt;"><b>The issue on the processing of review reports and unpublishing of review</b></span></span><span style="color: #333333;"><span style="font-size: 10pt;">. We are aware of an issue affecting the workflow of review reports. We are investigating the issue.</span></span></p>\r\n</li>\r\n<li>\r\n<p class="western" style="margin-bottom: 0in; direction: ltr; line-height: 22.7333px; background: transparent; font-family: Arial, serif; font-size: 11pt; border: none; padding: 0in;"><span style="color: #333333;"><span style="font-size: 10pt;"><b>The 2021 Goals for JED: </b></span></span><span style="color: #333333;"><span style="font-size: 10pt;">The Joomla magazine is going to publish an article about the goals of each department. We are collaborating in a document to write down the team goals in the following week</span></span></p>\r\n</li>\r\n<li>\r\n<p class="western" style="margin-bottom: 0in; direction: ltr; line-height: 22.7333px; background: transparent; font-family: Arial, serif; font-size: 11pt; border: none; padding: 0in;"><span style="color: #333333;"><span style="font-size: 10pt;"><b>Extension Reports of incompatibilities with the latest Joomla versions</b></span></span><span style="color: #333333;"><span style="font-size: 10pt;">: Since Joomla 3 is having a long product life cycle, we are receiving reports about published extensions (for instance, approved on Joomla 3.0 times) that are no longer working on the latest Joomla 3.</span></span></p>\r\n<ul>\r\n<li>\r\n<p class="western" style="margin-bottom: 0in; direction: ltr; line-height: 22.7333px; background: transparent; font-family: Arial, serif; font-size: 11pt; border: none; padding: 0in;"><span style="color: #333333;"><span style="font-size: 10pt;">We have decided to suggest the users report the issue to the author.</span></span></p>\r\n</li>\r\n</ul>\r\n</li>\r\n<li>\r\n<p class="western" style="margin-bottom: 0in; direction: ltr; line-height: 22.7333px; background: transparent; font-family: Arial, serif; font-size: 11pt; border: none; padding: 0in;"><span style="color: #333333;"><span style="font-size: 10pt;">The </span></span><span style="color: #333333;"><span style="font-size: 10pt;"><b>merge of VEL operation</b></span></span><span style="color: #333333;"><span style="font-size: 10pt;"> is completed. The pending issues are managed on Github, following the general team practice.</span></span></p>\r\n</li>\r\n<li>\r\n<p class="western" style="margin-bottom: 0in; direction: ltr; line-height: 22.7333px; background: transparent; font-family: Arial, serif; font-size: 11pt; border: none; padding: 0in;"><span style="color: #333333;"><span style="font-size: 10pt;">The </span></span><span style="color: #333333;"><span style="font-size: 10pt;"><b>backup policy</b></span></span><span style="color: #333333;"><span style="font-size: 10pt;"> has been reviewed and it is now within the scope of the periodic tasks.</span></span></p>\r\n</li>\r\n<li>\r\n<p class="western" style="margin-bottom: 0in; direction: ltr; line-height: 22.7333px; background: transparent; font-family: Arial, serif; font-size: 11pt; border: none; padding: 0in;"><span style="color: #333333;"><span style="font-size: 10pt;"><b>Data Cleansing</b></span></span><span style="color: #333333;"><span style="font-size: 10pt;">, the activity is progressing well.</span></span></p>\r\n</li>\r\n<li>\r\n<p class="western" style="margin-bottom: 0in; direction: ltr; line-height: 22.7333px; background: transparent; font-family: Arial, serif; font-size: 11pt; border: none; padding: 0in;"><span style="color: #333333;"><span style="font-size: 10pt;"><b>Call for JED4 Developers</b></span></span><span style="color: #333333;"><span style="font-size: 10pt;">. We have submitted a position for the community openings JCM article to call for JED4 Developers. To contribute to the project, </span></span><span style="color: #0088cc;"><span style="font-size: 10pt;"><a href="https://github.com/joomla/com_jed4/" style="color: #000080; text-decoration-line: underline;">join</a><a href="https://github.com/joomla/com_jed4/" style="color: #000080; text-decoration-line: underline;"> </a><a href="https://github.com/joomla/com_jed4/" style="color: #000080; text-decoration-line: underline;">the</a><a href="https://github.com/joomla/com_jed4/" style="color: #000080; text-decoration-line: underline;"> </a><a href="https://github.com/joomla/com_jed4/" style="color: #000080; text-decoration-line: underline;">team</a><a href="https://github.com/joomla/com_jed4/" style="color: #000080; text-decoration-line: underline;">!</a></span></span></p>\r\n</li>\r\n<li>\r\n<p class="western" style="margin-bottom: 0in; direction: ltr; line-height: 22.7333px; background: transparent; font-family: Arial, serif; font-size: 11pt; border: none; padding: 0in;"><span style="color: #333333;"><span style="font-size: 10pt;"><b>Testing for PHP 7.4. </b></span></span><span style="color: #333333;"><span style="font-size: 10pt;">We are following the general organization guideline to update all services to PHP 7.4 and we are going to internally test the service until it is ready for the update.</span></span></p>\r\n</li>\r\n<li>\r\n<p class="western" style="margin-bottom: 0in; direction: ltr; line-height: 22.7333px; background: transparent; font-family: Arial, serif; font-size: 11pt; border: none; padding: 0in;"><span style="color: #333333;"><span style="font-size: 10pt;">We are processing the queue of new extensions and pending tickets within the seven days processing window. With the addition of new members, we are improving the processing times.</span></span></p>\r\n</li>\r\n<li>\r\n<p class="western" style="margin-bottom: 0in; direction: ltr; line-height: 22.7333px; background: transparent; font-family: Arial, serif; font-size: 11pt; border: none; padding: 0in;"><span style="color: #333333;"><span style="font-size: 10pt;">Llewellyn van der Merwe has recently joined the JED team as a Listing Specialist.</span></span></p>\r\n</li>\r\n</ul>\r\n<p class="western" style="margin-bottom: 0in; direction: ltr; color: #000000; line-height: 16.8667px; background: transparent; font-family: Arial, serif; font-size: 11pt; border: none; padding: 0in;">&nbsp;</p>', NULL, 1, 1068, 1, 628, '2020-12-04 11:23:34', 0, '2020-12-04 11:24:48', 0, '0000-00-00 00:00:00'),
	(1383, 0, 120, 'Privacy Compliance Team Meeting December 9, 2020', 'privacy-compliance-team-meeting-december-9-2020', '<p style="overflow-wrap: break-word;">This meeting of the Joomla! Privacy Compliance Team has been held on Glip on December 9, 2020 at 16:30 CET.&nbsp;</p>\r\n<p style="overflow-wrap: break-word;"><strong>Participants</strong></p>\r\n<p style="overflow-wrap: break-word;">In attendance:&nbsp;</p>\r\n<ul>\r\n<li>Donata Stroink-Skillrud</li>\r\n<li>Sandra Decoux</li>\r\n<li>Roland Dalmulder</li>\r\n<li>Sander Potjer</li>\r\n</ul>\r\n<p style="overflow-wrap: break-word;"><strong>Discussion outline&nbsp;</strong></p>\r\n<ul>\r\n<li>Roland has completed the "Joomla and your consents" article and will submit it to the Joomla Community Magazine;&nbsp;</li>\r\n<li>Sander is working on the development of the signed documents in the backend;&nbsp;</li>\r\n<li>Sandra will create a list of questions that team members can answer during our year-end hangout call so that we can get to know each other better;&nbsp;</li>\r\n<li>&nbsp;Roland has set up the dev environment for the Identity Portal and will be working on the Properties Assessment Task in the next few weeks.&nbsp;</li>\r\n<li>Luca and Donata will have to meet to discuss session cookies and updating the Cookies Policy.&nbsp;</li>\r\n</ul>', NULL, 1, 1069, 1, 6279, '2020-12-09 15:52:58', 0, '2020-12-09 15:55:46', 0, '0000-00-00 00:00:00'),
	(1384, 0, 2, 'Volunteers Portal - December 2020 report', 'volunteers-portal-december-2020-report', '<p>A status update about the Volunteers Portal activity last month:</p>\r\n<h3>Github Issues</h3>\r\n<p>The Volunteers Portal has a public repository and issue tracker on <a href="https://github.com/joomla/volunteers.joomla.org">https://github.com/joomla/volunteers.joomla.org</a>. We have been working on these issues this month:</p>\r\n<ul>\r\n<li><a href="https://github.com/joomla/volunteers.joomla.org/issues/239">[Field] - NDA Signed date field #239</a></li>\r\n</ul>\r\n<p>Thanks for those reported issues and provided input. Please feel free to report issues, suggest features, or even work on the issues listed in the repository. Let me know if you need help to get started!</p>\r\n<h3>Other updates</h3>\r\n<ul>\r\n<li>Spam reports handled (2 accounts)</li>\r\n<li>Joomla &amp; extensions updated&nbsp;</li>\r\n</ul>', NULL, 1, 1070, 2, 430, '2020-12-09 19:51:26', 430, '2020-12-22 17:58:39', 0, '0000-00-00 00:00:00'),
	(1385, 0, 150, 'Educational Outreach Working Group Meeting - December 11, 2020', 'eowg-12-2020', '<h1 dir="ltr" style="line-height: 1.38; margin-top: 20pt; margin-bottom: 6pt;"><span style="font-size: 20pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Joomla Educational Outreach Working Group Meeting</span></h1>\r\n<h2 dir="ltr" style="line-height: 1.38; margin-top: 18pt; margin-bottom: 6pt;"><span style="font-size: 16pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">December 11, 2020 17:00 UTC</span></h2>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><a href="https://meet.google.com/snq-gjix-jyo?authuser=1" style="text-decoration: none;"><span style="font-size: 11pt; font-family: Arial; color: #1155cc; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: underline; -webkit-text-decoration-skip: none; text-decoration-skip-ink: none; vertical-align: baseline; white-space: pre-wrap;">https://meet.google.com/snq-gjix-jyo?authuser=1</span></a></p>\r\n<p><b id="docs-internal-guid-1ab8e12f-7fff-7bba-9933-827ebc9e8cad" style="font-weight: normal;">&nbsp;</b></p>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Attendees</span><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">: Laura Gordon (EST), Chris Keen (Europe Amsterdam UTC +1), Todd Woodward (EST), AK Shehu (Dubai), SD Williams (CST)&nbsp;</span></p>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Apologies</span><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">: all core members present</span></p>\r\n<p><b style="font-weight: normal;">&nbsp;</b></p>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Contact with non-active members:</span></p>\r\n<ul style="margin-top: 0; margin-bottom: 0;">\r\n<li dir="ltr" style="list-style-type: disc; font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre;">\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Todd - reached out directly via email and glip.&nbsp; We have our core team. Former members moved to the Honor Roll and will send to Davide Messia (VET)</span></p>\r\n</li>\r\n</ul>\r\n<p><b style="font-weight: normal;">&nbsp;</b></p>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Joomla Community Magazine</span></p>\r\n<ul style="margin-top: 0; margin-bottom: 0;">\r\n<li dir="ltr" style="list-style-type: disc; font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre;">\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">AK’s article, When the Covid-19 couldn’t stop Students, Joomla triumph in the Arab nation of Oman, submitted for November will be published</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre;">\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">SD’s article, Entice, Engage, and Educate in 2021, will be published for Jan 2021</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre;">\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Laura possibly for February 14th submission</span></p>\r\n</li>\r\n</ul>\r\n<p><b style="font-weight: normal;">&nbsp;</b></p>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">2021 Working Group Goals</span></p>\r\n<ul style="margin-top: 0; margin-bottom: 0;">\r\n<li dir="ltr" style="list-style-type: disc; font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre;">\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Implement a Hub of Shared Training Materials for those who want to teach Joomla&nbsp;</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre;">\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Share both within and outside of our community the existence, purpose, and use of the Hub</span></p>\r\n</li>\r\n</ul>\r\n<p><b style="font-weight: normal;">&nbsp;</b></p>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">January Meeting Thoughts</span></p>\r\n<ul style="margin-top: 0; margin-bottom: 0;">\r\n<li dir="ltr" style="list-style-type: disc; font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre;">\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Structure and organization of the resources and tools to include in the hub</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre;">\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Determination of the types of materials</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre;">\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Alert other ‘joomla members’ that this area exists?</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre;">\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Information Architecture and Content Strategy for teachjoomla.org</span></p>\r\n</li>\r\n<li dir="ltr" style="list-style-type: disc; font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre;">\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Think about having meetings earlier in the day to open up opportunities for folks in Australia to participate.</span></p>\r\n</li>\r\n</ul>\r\n<p><b style="font-weight: normal;">&nbsp;</b></p>\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: bold; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">Next Meeting</span></p>\r\n<p>&nbsp;</p>\r\n<ul style="margin-top: 0; margin-bottom: 0;">\r\n<li dir="ltr" style="list-style-type: disc; font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre;">\r\n<p dir="ltr" style="line-height: 1.38; margin-top: 0pt; margin-bottom: 0pt;" role="presentation"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;">January 15, 2021 at 16:00 UTC / 11 am EST / 10 am CST&nbsp;</span></p>\r\n</li>\r\n</ul>', NULL, 1, 1071, 1, 1715, '2020-12-11 23:28:42', 0, '2020-12-11 23:31:40', 0, '0000-00-00 00:00:00'),
	(1386, 0, 25, 'Joomla Bug Squad Team Meeting - December 11, 2020', 'joomla-bug-squad-team-meeting-december-11-2020', '<p id="docs-internal-guid-e8b1d1c5-7fff-4d3a-cb33-8e3beba58f5d" dir="ltr" style="line-height: 1.9871999999999999; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: underline; -webkit-text-decoration-skip: none; text-decoration-skip-ink: none; vertical-align: baseline; white-space: pre-wrap;">Date:</span><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;"> December 11, 2020</span></p>\r\n<p dir="ltr" style="line-height: 1.9871999999999999; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: underline; -webkit-text-decoration-skip: none; text-decoration-skip-ink: none; vertical-align: baseline; white-space: pre-wrap;">Time:</span><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: none; vertical-align: baseline; white-space: pre-wrap;"> 19:10 CET</span></p>\r\n<p dir="ltr" style="line-height: 1.9871999999999999; margin-top: 0pt; margin-bottom: 0pt;"><span style="font-size: 11pt; font-family: Arial; color: #000000; background-color: transparent; font-weight: 400; font-style: normal; font-variant: normal; text-decoration: underline; -webkit-text-decoration-skip: none; text-decoration-skip-ink: none; vertical-align: baseline; white-space: pre-wrap;">Total time of meeting:</span><span style="font-size: 11pt; font-family: Arial; color: #000000; b